import React, { useState } from 'react';
import { HelpCircle, Info } from 'lucide-react';

interface VCTooltipProps {
  title: string;
  content: string;
  type?: 'info' | 'help';
  position?: 'top' | 'bottom' | 'left' | 'right';
}

const VCTooltip: React.FC<VCTooltipProps> = ({ 
  title, 
  content, 
  type = 'info',
  position = 'top' 
}) => {
  const [isVisible, setIsVisible] = useState(false);

  const positionClasses = {
    top: 'bottom-full left-1/2 transform -translate-x-1/2 mb-2',
    bottom: 'top-full left-1/2 transform -translate-x-1/2 mt-2',
    left: 'right-full top-1/2 transform -translate-y-1/2 mr-2',
    right: 'left-full top-1/2 transform -translate-y-1/2 ml-2'
  };

  const Icon = type === 'help' ? HelpCircle : Info;

  return (
    <div className="relative inline-block">
      <button
        onMouseEnter={() => setIsVisible(true)}
        onMouseLeave={() => setIsVisible(false)}
        onClick={() => setIsVisible(!isVisible)}
        className="p-1 rounded-full hover:bg-white/10 transition-colors duration-200"
      >
        <Icon className="w-4 h-4 text-blue-400 hover:text-blue-300" />
      </button>

      {isVisible && (
        <div className={`absolute z-50 w-64 ${positionClasses[position]}`}>
          <div className="bg-gray-900 text-white p-3 rounded-lg shadow-xl border border-gray-700">
            <div className="font-semibold text-sm mb-1 text-blue-300">{title}</div>
            <div className="text-xs text-gray-300 leading-relaxed">{content}</div>

            {/* Arrow */}
            <div className={`absolute w-2 h-2 bg-gray-900 border-gray-700 transform rotate-45 ${
              position === 'top' ? 'top-full left-1/2 -translate-x-1/2 -mt-1 border-b border-r' :
              position === 'bottom' ? 'bottom-full left-1/2 -translate-x-1/2 -mb-1 border-t border-l' :
              position === 'left' ? 'left-full top-1/2 -translate-y-1/2 -ml-1 border-t border-r' :
              'right-full top-1/2 -translate-y-1/2 -mr-1 border-b border-l'
            }`}></div>
          </div>
        </div>
      )}
    </div>
  );
};

export default VCTooltip;

