import { useState, useEffect } from 'react';
import Navigation from './components/Navigation';
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';
import DROList from './components/DROList';
import RecommendedDROs from './components/RecommendedDROs';
import InvestorDashboard from './components/InvestorDashboard';
import MSMEDashboard from './components/MSMEDashboard';
import InvestmentModal from './components/InvestmentModal';
import DRODetailModal from './components/DRODetailModal';
import DemoModeToggle from './components/DemoModeToggle';
import { getCurrentUser } from './services/api';
import { DemoService, isDemoMode } from './services/demoService';
import type { DRO } from './services/api';

function App() {
  const [currentView, setCurrentView] = useState('home');
  const [user, setUser] = useState(getCurrentUser());
  const [selectedDRO, setSelectedDRO] = useState<DRO | null>(null);
  const [showInvestmentModal, setShowInvestmentModal] = useState(false);
  const [showDetailModal, setShowDetailModal] = useState(false);

  useEffect(() => {
    let user = getCurrentUser();

    // If in demo mode, use demo user
    if (isDemoMode() && !user) {
      user = DemoService.getCurrentUser();
    }

    setUser(user);

    // Redirect to appropriate view if user is logged in and on auth pages
    if (user && (currentView === 'login' || currentView === 'register')) {
      setCurrentView('home');
    }
  }, [currentView]);

  const handleAuthSuccess = () => {
    const user = getCurrentUser();
    setUser(user);
    setCurrentView('home');
  };

  const handleNavigate = (view: string) => {
    setCurrentView(view);
  };

  const handleInvest = (dro: DRO) => {
    setSelectedDRO(dro);
    setShowInvestmentModal(true);
  };

  const handleViewDetails = (dro: DRO) => {
    setSelectedDRO(dro);
    setShowDetailModal(true);
  };

  const handleInvestmentSuccess = () => {
    setShowInvestmentModal(false);
    setSelectedDRO(null);
  };

  const renderCurrentView = () => {
    switch (currentView) {
      case 'login':
        return (
          <LoginForm
            onSuccess={handleAuthSuccess}
            onSwitchToRegister={() => setCurrentView('register')}
          />
        );

      case 'register':
        return (
          <RegisterForm
            onSuccess={handleAuthSuccess}
            onSwitchToLogin={() => setCurrentView('login')}
          />
        );

      case 'dashboard':
        if (!user) {
          setCurrentView('home');
          return null;
        }
        return user.user_type === 'investor' ?
          <InvestorDashboard /> :
          <MSMEDashboard />;

      case 'recommended':
        return (
          <RecommendedDROs 
            onInvest={handleInvest}
            onViewDetails={handleViewDetails}
            onNavigateToBrowse={() => setCurrentView('home')}
          />
        );

      case 'opportunities':
      case 'home':
      default:
        return <DROList onNavigateToRecommended={() => setCurrentView('recommended')} />;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-dark relative overflow-hidden">
      {/* Background effects */}
      <div className="absolute inset-0 bg-gradient-mesh opacity-10"></div>
      <div className="absolute top-0 left-1/4 w-96 h-96 bg-primary-500/10 rounded-full blur-3xl animate-float"></div>
      <div className="absolute bottom-0 right-1/4 w-80 h-80 bg-secondary-500/10 rounded-full blur-3xl animate-float" style={{animationDelay: '2s'}}></div>

      <div className="relative z-10">
        <Navigation 
          onNavigate={handleNavigate}
          currentView={currentView}
        />

        <div className="transition-all duration-500 ease-in-out">
          {renderCurrentView()}
        </div>
      </div>

      {/* Modals */}
      {showInvestmentModal && selectedDRO && (
        <InvestmentModal
          dro={selectedDRO}
          onClose={() => {
            setShowInvestmentModal(false);
            setSelectedDRO(null);
          }}
          onSuccess={handleInvestmentSuccess}
        />
      )}

      {showDetailModal && selectedDRO && (
        <DRODetailModal
          dro={selectedDRO}
          onClose={() => {
            setShowDetailModal(false);
            setSelectedDRO(null);
          }}
          onInvest={handleInvest}
        />
      )}

      {/* Demo Mode Toggle */}
      <DemoModeToggle />
    </div>
  );
}

export default App

