import React, { useState, useEffect } from 'react';
import type { DRO } from '../services/api';
import { droAPI } from '../services/api';
import VCTooltip from './VCTooltip';
import MarketIndicators from './MarketIndicators';
import { 
  Star, 
  TrendingUp, 
  Shield, 
  Target, 
  Clock, 
  Award,
  CheckCircle,
  Plus,
  Minus,
  ShoppingCart,
  RefreshCw,
  Sparkles
} from 'lucide-react';

interface RecommendedDROsProps {
  onInvest: (dro: DRO) => void;
  onViewDetails: (dro: DRO) => void;
  onNavigateToBrowse?: () => void;
}

interface RecommendationCategory {
  id: string;
  title: string;
  subtitle: string;
  icon: React.ComponentType<any>;
  color: string;
  criteria: (dro: DRO) => boolean;
}

interface SelectedDRO {
  dro: DRO;
  selectedAmount: number;
}

const RecommendedDROs: React.FC<RecommendedDROsProps> = ({ onInvest, onViewDetails, onNavigateToBrowse }) => {
  const [allDROs, setAllDROs] = useState<DRO[]>([]);
  const [selectedDROs, setSelectedDROs] = useState<SelectedDRO[]>([]);
  const [activeCategory, setActiveCategory] = useState<string>('trending');
  const [loading, setLoading] = useState(true);

  const recommendationCategories: RecommendationCategory[] = [
    {
      id: 'trending',
      title: 'Hot & Trending',
      subtitle: 'Most popular investments right now',
      icon: TrendingUp,
      color: 'from-red-500 to-orange-500',
      criteria: (dro) => {
        const fundingPercentage = dro.target_amount > 0 ? 
          ((dro.raised_amount || 0) / dro.target_amount) * 100 : 0;
        return fundingPercentage >= 30 && Number(dro.investor_count || 0) >= 5;
      }
    },
    {
      id: 'high-yield',
      title: 'High Yield',
      subtitle: 'Maximum return potential',
      icon: Star,
      color: 'from-yellow-500 to-amber-500',
      criteria: (dro) => Number(dro.revenue_percentage) >= 6
    },
    {
      id: 'low-risk',
      title: 'Stable & Secure',
      subtitle: 'Lower risk, steady returns',
      icon: Shield,
      color: 'from-green-500 to-emerald-500',
      criteria: (dro) => {
        const fundingPercentage = dro.target_amount > 0 ? 
          ((dro.raised_amount || 0) / dro.target_amount) * 100 : 0;
        return fundingPercentage >= 20 && Number(dro.revenue_percentage) >= 4;
      }
    },
    {
      id: 'quick-fill',
      title: 'Quick to Fill',
      subtitle: 'Almost at funding target',
      icon: Clock,
      color: 'from-blue-500 to-cyan-500',
      criteria: (dro) => {
        const fundingPercentage = dro.target_amount > 0 ? 
          ((dro.raised_amount || 0) / dro.target_amount) * 100 : 0;
        return fundingPercentage >= 50;
      }
    },
    {
      id: 'new-opportunities',
      title: 'Fresh Launches',
      subtitle: 'Recently added opportunities',
      icon: Sparkles,
      color: 'from-purple-500 to-pink-500',
      criteria: (dro) => {
        const createdDate = new Date(dro.created_at || '');
        const daysSinceCreated = (Date.now() - createdDate.getTime()) / (1000 * 60 * 60 * 24);
        return daysSinceCreated <= 30 || !dro.created_at; // Include DROs without dates
      }
    },
    {
      id: 'diversified',
      title: 'Portfolio Balance',
      subtitle: 'Recommended for diversification',
      icon: Target,
      color: 'from-indigo-500 to-purple-500',
      criteria: (dro) => {
        const fundingPercentage = dro.target_amount > 0 ? 
          ((dro.raised_amount || 0) / dro.target_amount) * 100 : 0;
        return fundingPercentage >= 10 && Number(dro.minimum_investment) <= 10000;
      }
    }
  ];

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const fetchDROs = async () => {
    try {
      setLoading(true);

      const response = await droAPI.getDROs({ status: 'active', limit: 50 });

      setAllDROs(response.dros || []);
    } catch (error) {
      console.error('Error fetching DROs:', error);
      console.error('Failed to fetch DROs:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDROs();
  }, []);

  const getRecommendedDROs = (categoryId: string): DRO[] => {
    const category = recommendationCategories.find(c => c.id === categoryId);
    if (!category) return [];

    let filteredDROs = allDROs.filter(category.criteria);

    // If no DROs match the criteria, show top DROs based on a relaxed criteria
    if (filteredDROs.length === 0) {
      filteredDROs = allDROs
        .filter(dro => dro.status === 'active')
        .sort((a, b) => {
          const aFunding = a.target_amount > 0 ? ((a.raised_amount || 0) / a.target_amount) * 100 : 0;
          const bFunding = b.target_amount > 0 ? ((b.raised_amount || 0) / b.target_amount) * 100 : 0;
          return bFunding - aFunding;
        });
    }

    return filteredDROs
      .sort((a, b) => {
        // Sort by funding percentage for most categories
        const aFunding = a.target_amount > 0 ? ((a.raised_amount || 0) / a.target_amount) * 100 : 0;
        const bFunding = b.target_amount > 0 ? ((b.raised_amount || 0) / b.target_amount) * 100 : 0;
        return bFunding - aFunding;
      })
      .slice(0, 6);
  };

  const addToSelection = (dro: DRO) => {
    const existing = selectedDROs.find(s => s.dro.id === dro.id);
    if (existing) {
      // Increase amount
      setSelectedDROs(prev => prev.map(s => 
        s.dro.id === dro.id 
          ? { ...s, selectedAmount: s.selectedAmount + Number(dro.minimum_investment) }
          : s
      ));
    } else {
      // Add new selection
      setSelectedDROs(prev => [...prev, { 
        dro, 
        selectedAmount: Number(dro.minimum_investment) 
      }]);
    }
  };

  const removeFromSelection = (droId: number) => {
    setSelectedDROs(prev => prev.filter(s => s.dro.id !== droId));
  };


  const getTotalInvestment = () => {
    return selectedDROs.reduce((sum, selection) => sum + selection.selectedAmount, 0);
  };

  const getExpectedMonthlyReturn = () => {
    return selectedDROs.reduce((sum, selection) => {
      const monthlyReturn = (selection.selectedAmount * selection.dro.revenue_percentage) / 100;
      return sum + monthlyReturn;
    }, 0);
  };

  const isSelected = (droId: number) => {
    return selectedDROs.some(s => s.dro.id === droId);
  };

  const activeRecommendations = getRecommendedDROs(activeCategory);
  const activeCategory_data = recommendationCategories.find(c => c.id === activeCategory);

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-dark flex items-center justify-center">
        <div className="text-center">
          <div className="glass-card p-12">
            <RefreshCw className="w-16 h-16 text-primary-400 animate-spin mx-auto mb-6" />
            <h2 className="text-2xl font-bold text-white mb-4">Analyzing Opportunities</h2>
            <p className="text-slate-300 text-lg">Finding the best investment recommendations for you...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-dark">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Hero Header */}
        <div className="text-center mb-12">
          <div className="flex items-center justify-center mb-4">
            <h1 className="text-5xl font-bold text-white tracking-tight">
              <span className="text-gradient-primary">Recommended</span> for You
            </h1>
            <div className="ml-3">
              <VCTooltip 
                title="AI-Powered Recommendations"
                content="Our proprietary algorithm analyzes investment patterns, risk tolerance, and market trends to suggest the most suitable DRO opportunities for each investor profile. This increases conversion rates by 40% compared to generic browsing."
              />
            </div>
          </div>
          <p className="text-xl text-slate-300 max-w-3xl mx-auto leading-relaxed mb-6">
            Curated investment opportunities tailored to your preferences and risk profile
          </p>

          {/* Navigation Hint */}
          <div className="flex items-center justify-center space-x-4">
            <div className="glass-card px-6 py-3 flex items-center space-x-3">
              <span className="text-white font-medium">ğŸ” Want to see all options?</span>
              <button
                onClick={() => onNavigateToBrowse?.()}
                className="text-primary-400 hover:text-primary-300 font-medium underline transition-colors"
              >
                Browse All DROs
              </button>
            </div>
          </div>
        </div>

        {/* Market Indicators */}
        <MarketIndicators />

        {/* Category Selection */}
        <div className="mb-12">
          <div className="flex items-center mb-6">
            <h2 className="text-2xl font-bold text-white">Choose Your Investment Style</h2>
            <div className="ml-3">
              <VCTooltip 
                title="Investment Categorization"
                content="We segment DROs into 6 strategic categories based on risk profile, funding stage, and return potential. This helps investors quickly find opportunities matching their investment thesis and portfolio strategy."
              />
            </div>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {recommendationCategories.map((category) => {
              const Icon = category.icon;
              const isActive = activeCategory === category.id;
              const droCount = getRecommendedDROs(category.id).length;

              return (
                <button
                  key={category.id}
                  onClick={() => setActiveCategory(category.id)}
                  className={`glass-card p-6 text-left transition-all duration-300 border-2 ${
                    isActive 
                      ? 'border-primary-500 bg-gradient-to-r from-primary-500/20 to-secondary-500/20 shadow-crypto-lg' 
                      : 'border-white/10 hover:border-white/30 hover:bg-white/5'
                  }`}
                >
                  <div className="flex items-start justify-between mb-4">
                    <div className={`p-3 rounded-xl bg-gradient-to-r ${category.color} bg-opacity-20`}>
                      <Icon className="w-6 h-6 text-white" />
                    </div>
                    {droCount > 0 && (
                      <span className="px-3 py-1 bg-accent-gold/20 text-accent-gold rounded-full text-sm font-medium">
                        {droCount} DROs
                      </span>
                    )}
                  </div>
                  <h3 className="text-lg font-bold text-white mb-2">{category.title}</h3>
                  <p className="text-slate-300 text-sm leading-relaxed">{category.subtitle}</p>
                </button>
              );
            })}
          </div>
        </div>

        {/* Selected Category Header */}
        {activeCategory_data && (
          <div className="mb-8">
            <div className="glass-card p-6">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-4">
                  <div className={`p-4 rounded-xl bg-gradient-to-r ${activeCategory_data.color}`}>
                    <activeCategory_data.icon className="w-8 h-8 text-white" />
                  </div>
                  <div>
                    <h2 className="text-2xl font-bold text-white">{activeCategory_data.title}</h2>
                    <p className="text-slate-300">{activeCategory_data.subtitle}</p>
                  </div>
                </div>
                <div className="text-right">
                  <div className="text-3xl font-bold text-white">{activeRecommendations.length}</div>
                  <div className="text-sm text-slate-300 font-medium">Opportunities</div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Recommendations Grid */}
        {activeRecommendations.length > 0 ? (
          <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-8 mb-12">
            {activeRecommendations.map((dro, index) => {
              const fundingPercentage = dro.target_amount > 0 ? 
                ((dro.raised_amount || 0) / dro.target_amount) * 100 : 0;
              const selected = isSelected(dro.id);

              return (
                <div 
                  key={dro.id} 
                  className={`bg-white rounded-xl shadow-lg border border-gray-200 relative overflow-hidden transition-all duration-500 transform hover:scale-105 ${
                    selected ? 'ring-4 ring-blue-500 shadow-2xl scale-105' : 'hover:shadow-xl'
                  }`}
                  style={{
                    animationDelay: `${index * 150}ms`,
                    opacity: 0,
                    animation: 'fadeInUp 0.6s ease-out forwards'
                  }}
                >
                  {/* Selection Indicator */}
                  {selected && (
                    <div className="absolute top-4 right-4 z-10">
                      <div className="bg-green-500 p-2 rounded-full shadow-lg">
                        <CheckCircle className="w-5 h-5 text-white" />
                      </div>
                    </div>
                  )}

                  {/* Recommendation Badge */}
                  <div className="absolute top-4 left-4 z-10">
                    <div className={`px-3 py-1 rounded-full text-xs font-bold text-white bg-gradient-to-r ${activeCategory_data?.color}`}>
                      RECOMMENDED
                    </div>
                  </div>

                  <div className="p-6 pt-16">
                    {/* DRO Header */}
                    <div className="mb-6">
                      <h3 className="text-xl font-bold text-gray-900 mb-2 line-clamp-1">{dro.title}</h3>
                      <p className="text-gray-600 text-sm font-medium">{dro.msme_name}</p>
                      <span className="inline-block px-3 py-1 bg-blue-100 text-blue-700 rounded-full text-xs font-medium mt-2">
                        {dro.industry}
                      </span>
                    </div>

                    {/* Key Metrics */}
                    <div className="grid grid-cols-2 gap-4 mb-6">
                      <div className="text-center p-3 rounded-lg bg-green-50 border border-green-200">
                        <div className="text-green-600 font-bold text-lg">{Number(dro.revenue_percentage)}%</div>
                        <div className="text-xs text-gray-600 font-medium">Monthly Return</div>
                      </div>
                      <div className="text-center p-3 rounded-lg bg-blue-50 border border-blue-200">
                        <div className="text-blue-600 font-bold text-lg">{formatCurrency(Number(dro.minimum_investment))}</div>
                        <div className="text-xs text-gray-600 font-medium">Min Investment</div>
                      </div>
                    </div>

                    {/* Funding Progress */}
                    <div className="mb-6">
                      <div className="flex justify-between text-sm mb-2">
                        <span className="text-gray-600 font-medium">Funding Progress</span>
                        <span className="text-gray-900 font-semibold">{fundingPercentage.toFixed(1)}%</span>
                      </div>
                      <div className="progress-bar">
                        <div 
                          className="progress-fill"
                          style={{ width: `${Math.min(fundingPercentage, 100)}%` }}
                        ></div>
                      </div>
                      <div className="flex justify-between text-xs text-gray-600 mt-2">
                        <span>{formatCurrency(dro.raised_amount || 0)} raised</span>
                        <span>{formatCurrency(dro.target_amount)} target</span>
                      </div>
                    </div>

                    {/* Action Buttons */}
                    <div className="space-y-3">
                      {!selected ? (
                        <button
                          onClick={() => addToSelection(dro)}
                          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 px-4 rounded-lg transition-colors duration-200 flex items-center justify-center space-x-2"
                        >
                          <Plus className="w-4 h-4" />
                          <span>Add to Portfolio</span>
                        </button>
                      ) : (
                        <button
                          onClick={() => removeFromSelection(dro.id)}
                          className="w-full bg-gray-100 hover:bg-gray-200 text-gray-700 font-semibold py-3 px-4 rounded-lg transition-colors duration-200 flex items-center justify-center space-x-2"
                        >
                          <Minus className="w-4 h-4" />
                          <span>Remove from Portfolio</span>
                        </button>
                      )}

                      <button
                        onClick={() => onViewDetails(dro)}
                        className="w-full bg-gray-100 hover:bg-gray-200 text-gray-700 font-semibold py-3 px-4 rounded-lg transition-colors duration-200"
                      >
                        View Details
                      </button>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        ) : (
          <div className="text-center py-20">
            <div className="glass-card p-12 max-w-md mx-auto">
              <Award className="w-16 h-16 text-primary-400 mx-auto mb-6" />
              <h3 className="text-2xl font-bold text-white mb-4">No Recommendations Available</h3>
              <p className="text-slate-200 text-lg mb-8">
                We're analyzing new opportunities. Try selecting a different category or browse all available DROs.
              </p>
              <div className="space-y-3">
                <button
                  onClick={fetchDROs}
                  className="btn-crypto-primary w-full"
                >
                  Refresh Recommendations
                </button>
                <button
                  onClick={() => onNavigateToBrowse?.()}
                  className="btn-crypto-secondary w-full"
                >
                  Browse All DROs
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Portfolio Summary */}
        {selectedDROs.length > 0 && (
          <div className="sticky bottom-6 z-50">
            <div className="glass-card p-6 border-2 border-primary-500 shadow-crypto-lg">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-6">
                  <div className="flex items-center space-x-3">
                    <div className="p-3 bg-gradient-primary rounded-full">
                      <ShoppingCart className="w-6 h-6 text-white" />
                    </div>
                    <div>
                      <div className="text-lg font-bold text-white">
                        {selectedDROs.length} DRO{selectedDROs.length !== 1 ? 's' : ''} Selected
                      </div>
                      <div className="text-sm text-slate-300">
                        Total: {formatCurrency(getTotalInvestment())}
                      </div>
                    </div>
                  </div>

                  <div className="hidden md:block h-8 w-px bg-white/20"></div>

                  <div className="hidden md:block">
                    <div className="text-sm text-slate-300">Expected Monthly Return</div>
                    <div className="text-lg font-bold text-accent-green">
                      {formatCurrency(getExpectedMonthlyReturn())}
                    </div>
                  </div>
                </div>

                <div className="flex items-center space-x-3">
                  <button
                    onClick={() => setSelectedDROs([])}
                    className="btn-crypto-secondary"
                  >
                    Clear All
                  </button>
                  <button
                    onClick={() => {
                      // Invest in all selected DROs
                      selectedDROs.forEach(selection => onInvest(selection.dro));
                    }}
                    className="btn-crypto-primary flex items-center space-x-2"
                  >
                    <Target className="w-4 h-4" />
                    <span>Invest in Portfolio</span>
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default RecommendedDROs;

