const axios = require('axios');

async function debugDatabase() {
  console.log('ðŸ” Debugging database operations...\n');

  try {
    // Test basic API connectivity
    console.log('1ï¸âƒ£ Testing server connectivity...');
    const response = await axios.get('http://localhost:3001/api/dro/statistics');
    console.log('Server response:', response.status);
  } catch (error) {
    console.log('âŒ Server error details:');
    console.log('Status:', error.response?.status);
    console.log('Error data:', error.response?.data);
    console.log('Error message:', error.message);
  }

  // Test with very detailed registration payload
  console.log('\n2ï¸âƒ£ Testing detailed registration...');
  try {
    const registerData = {
      email: 'test@example.com',
      password: 'password123',
      first_name: 'Test',
      last_name: 'User',
      user_type: 'investor'
    };

    console.log('Sending registration data:', registerData);

    const response = await axios.post('http://localhost:3001/api/auth/register', registerData, {
      headers: {
        'Content-Type': 'application/json'
      }
    });

    console.log('âœ… Registration successful:', response.data);
  } catch (error) {
    console.log('âŒ Registration error details:');
    console.log('Status:', error.response?.status);
    console.log('Headers:', error.response?.headers);
    console.log('Data:', error.response?.data);

    if (error.code) console.log('Error code:', error.code);
    if (error.message) console.log('Error message:', error.message);
  }
}

debugDatabase();

