const fetch = require('node-fetch');

const API_BASE = 'http://localhost:3006/api';
const API_TOKEN = 'sirex-api-token-2025-batch-operations';

// Use existing demo users from the original seed data
const existingUsers = [
  { email: 'msme1@example.com', password: 'password123' },
  { email: 'msme2@example.com', password: 'password123' },
  { email: 'msme3@example.com', password: 'password123' },
  { email: 'msme4@example.com', password: 'password123' },
  { email: 'msme5@example.com', password: 'password123' },
  { email: 'msme6@example.com', password: 'password123' },
  { email: 'msme7@example.com', password: 'password123' },
  { email: 'msme8@example.com', password: 'password123' },
];

function generateDROData(index, baseTitle = '') {
  const industries = ['Technology', 'Food & Beverage', 'Services', 'Healthcare', 'Education', 'Real Estate', 'Entertainment', 'Manufacturing', 'Agriculture', 'Retail', 'Renewable Energy'];
  const riskLevels = ['low', 'medium', 'high'];

  const industry = industries[index % industries.length];
  const riskLevel = riskLevels[index % riskLevels.length];

  const revenuePercentage = 5 + (index % 25); // 5% to 30%
  const minInvestment = 1000 + (index * 500); // Starting from 1k
  const targetAmount = minInvestment * (3 + (index % 10)); // 3x to 13x min investment

  return {
    title: `${industry} Revenue Share ${index + 1}${baseTitle ? ' - ' + baseTitle : ''}`,
    description: `Revenue sharing opportunity in ${industry.toLowerCase()} sector. This established business offers investors a ${revenuePercentage}% share of monthly revenue with strong growth potential and proven market demand.`,
    industry,
    revenue_percentage: revenuePercentage,
    minimum_investment: minInvestment,
    target_amount: targetAmount,
    duration_months: 12 + (index % 36), // 12 to 48 months
    annual_revenue: 400000 + (index * 5000),
    roi_estimate: revenuePercentage * 1.2,
    risk_level: riskLevel,
    collateral_description: `${industry} business assets, equipment, and revenue contracts`,
    use_of_funds: `Business expansion, ${industry.toLowerCase()} equipment, and working capital`,
    repayment_terms: 'Monthly revenue distributions based on actual business performance'
  };
}

async function testConnection() {
  try {
    const response = await fetch(`${API_BASE}/dro`);
    if (response.ok) {
      console.log('âœ… API connection successful');
      return true;
    } else {
      console.log('âŒ API connection failed:', response.status);
      return false;
    }
  } catch (error) {
    console.error('âŒ API connection error:', error.message);
    return false;
  }
}

async function loginUser(email, password) {
  try {
    console.log(`ðŸ”‘ Attempting login for: ${email}`);
    const response = await fetch(`${API_BASE}/auth/login`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email, password }),
    });

    if (response.ok) {
      const result = await response.json();
      console.log(`âœ… Login successful for: ${email}`);
      return result.token;
    } else {
      const errorText = await response.text();
      console.log(`âŒ Login failed for ${email}: ${errorText}`);
      return null;
    }
  } catch (error) {
    console.error(`âŒ Login error for ${email}:`, error.message);
    return null;
  }
}

async function createDRO(droData, token) {
  try {
    const response = await fetch(`${API_BASE}/dro`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
      body: JSON.stringify(droData),
    });

    if (response.ok) {
      const result = await response.json();
      return result;
    } else {
      const error = await response.text();
      console.log(`âŒ Failed to create DRO "${droData.title}": ${error}`);
      return null;
    }
  } catch (error) {
    console.error(`âŒ Error creating DRO "${droData.title}":`, error.message);
    return null;
  }
}

async function add200DROs() {
  try {
    console.log('ðŸ—„ï¸ Starting to add 200 DROs via API...');
    console.log('ðŸ“¡ API Base URL:', API_BASE);

    // Test API connection
    const connected = await testConnection();
    if (!connected) {
      throw new Error('Cannot connect to API. Make sure the backend server is running on localhost:3001');
    }

    // Login existing users
    console.log('ðŸ”‘ Logging in existing MSME users...');
    const userTokens = [];

    for (const userData of existingUsers) {
      const token = await loginUser(userData.email, userData.password);
      if (token) {
        userTokens.push({ email: userData.email, token });
      }
      // Small delay between requests
      await new Promise(resolve => setTimeout(resolve, 200));
    }

    console.log(`âœ… Successfully logged in ${userTokens.length} users`);

    if (userTokens.length === 0) {
      throw new Error('No users available to create DROs. Please check if the demo users exist and have correct passwords.');
    }

    // Create DROs
    console.log('ðŸŽ¯ Creating 200 DROs...');
    let successCount = 0;

    for (let i = 0; i < 200; i++) {
      const userIndex = i % userTokens.length;
      const userToken = userTokens[userIndex];

      const droData = generateDROData(i, `User ${userIndex + 1}`);
      const result = await createDRO(droData, userToken.token);

      if (result) {
        successCount++;
        console.log(`âœ… Created DRO ${successCount}: ${droData.title}`);
      }

      // Progress update every 25 DROs
      if ((i + 1) % 25 === 0) {
        console.log(`ðŸ“Š Progress: ${i + 1}/200 DROs processed (${successCount} successful)`);
      }

      // Small delay to avoid overwhelming the API
      await new Promise(resolve => setTimeout(resolve, 100));
    }

    console.log('ðŸŽ‰ DRO creation process completed!');
    console.log(`ðŸ“ˆ Final result: ${successCount}/200 DROs created successfully`);

    // Verify by checking total count
    console.log('ðŸ” Verifying results...');
    try {
      const response = await fetch(`${API_BASE}/dro`);
      if (response.ok) {
        const result = await response.json();
        const totalDROs = result.pagination?.total || result.length || 'unknown';
        console.log(`ðŸ“Š Total DROs in system: ${totalDROs}`);

        if (result.data && Array.isArray(result.data)) {
          // Show distribution by industry
          const industryCount = {};
          result.data.forEach(dro => {
            industryCount[dro.industry] = (industryCount[dro.industry] || 0) + 1;
          });

          console.log('ðŸ­ DROs by industry:');
          Object.entries(industryCount).forEach(([industry, count]) => {
            console.log(`   ${industry}: ${count}`);
          });
        }
      }
    } catch (error) {
      console.log('âš ï¸  Could not verify results:', error.message);
    }

  } catch (error) {
    console.error('âŒ Process failed:', error.message);
    process.exit(1);
  }
}

// Run the process
console.log('ðŸš€ Starting DRO import process...');
add200DROs()
  .then(() => {
    console.log('âœ… Process completed successfully');
    process.exit(0);
  })
  .catch((error) => {
    console.error('âŒ Process failed:', error);
    process.exit(1);
  });

