import { DROModel, CreateDROData, DROFilters } from '../models/DRO';
import { InvestmentModel, CreateInvestmentData } from '../models/Investment';
import { authenticateToken, requireUserType, requireKycApproval, authenticateApiToken } from '../middleware/auth';

const router = express.Router();

// GET /api/dro - List all DROs with filtering
router.get('/', async (req, res) => {
  try {
    const {
      industry,
      min_investment,
      max_investment,
      min_return,
      max_return,
      status = 'active',
      search,
      page = '1',
      limit = '20'
    } = req.query;

    const filters: DROFilters = {};

    if (industry && typeof industry === 'string') filters.industry = industry;
    if (min_investment) filters.min_investment = parseFloat(min_investment as string);
    if (max_investment) filters.max_investment = parseFloat(max_investment as string);
    if (min_return) filters.min_return = parseFloat(min_return as string);
    if (max_return) filters.max_return = parseFloat(max_return as string);
    if (status && typeof status === 'string') filters.status = status;
    if (search && typeof search === 'string') filters.search = search;

    const pageNum = parseInt(page as string);
    const limitNum = parseInt(limit as string);
    const offset = (pageNum - 1) * limitNum;

    const droModel = new DROModel();
    const dros = await droModel.findAll(filters, limitNum, offset);

    res.json({
      dros,
      pagination: {
        page: pageNum,
        limit: limitNum,
        total: dros.length
      },
      filters
    });

  } catch (error) {
    console.error('Get DROs error:', error);
    res.status(500).json({ 
      error: 'Failed to fetch DROs',
      code: 'FETCH_DROS_ERROR'
    });
  }
});

// GET /api/dro/statistics - Get platform statistics
router.get('/statistics', async (req, res) => {
  try {
    const droModel = new DROModel();
    const stats = await droModel.getStatistics();
    const industryBreakdown = await droModel.getIndustryBreakdown();

    res.json({
      statistics: stats,
      industry_breakdown: industryBreakdown
    });

  } catch (error) {
    console.error('Get statistics error:', error);
    res.status(500).json({ 
      error: 'Failed to fetch statistics',
      code: 'FETCH_STATS_ERROR'
    });
  }
});

// GET /api/dro/my-dros - Get current user's DROs (MSME only)
router.get('/my-dros', authenticateToken, requireUserType('msme'), async (req, res) => {
  try {
    const droModel = new DROModel();
    const dros = await droModel.findByMSME(req.user!.userId);

    res.json({
      dros,
      total: dros.length
    });

  } catch (error) {
    console.error('Get my DROs error:', error);
    res.status(500).json({ 
      error: 'Failed to fetch your DROs',
      code: 'FETCH_MY_DROS_ERROR'
    });
  }
});

// GET /api/dro/my-investments - Get current user's investments (Investor only)
router.get('/my-investments', authenticateToken, requireUserType('investor'), async (req, res) => {
  try {
    const investmentModel = new InvestmentModel();
    const portfolio = await investmentModel.getInvestorPortfolio(req.user!.userId);

    res.json(portfolio);

  } catch (error) {
    console.error('Get my investments error:', error);
    res.status(500).json({ 
      error: 'Failed to fetch your investments',
      code: 'FETCH_MY_INVESTMENTS_ERROR'
    });
  }
});

// GET /api/dro/:id - Get specific DRO with investment details
router.get('/:id', async (req, res) => {
  try {
    const id = parseInt(req.params.id);

    if (isNaN(id)) {
      return res.status(400).json({ 
        error: 'Invalid DRO ID',
        code: 'INVALID_ID'
      });
    }

    const droModel = new DROModel();
    const investmentModel = new InvestmentModel();

    const dro = await droModel.findById(id);

    if (!dro) {
      return res.status(404).json({ 
        error: 'DRO not found',
        code: 'DRO_NOT_FOUND'
      });
    }

    // Get investment summary for this DRO
    const investments = await investmentModel.findByDRO(id);
    const totalInvested = await investmentModel.getTotalInvestedInDRO(id);

    res.json({
      dro: {
        ...dro,
        total_invested: totalInvested,
        funding_percentage: dro.target_amount > 0 ? (totalInvested / dro.target_amount) * 100 : 0,
        investor_count: investments.length
      },
      investments: investments.length > 0 ? investments.slice(0, 5) : [] // Show recent 5 investments
    });

  } catch (error) {
    console.error('Get DRO error:', error);
    res.status(500).json({ 
      error: 'Failed to fetch DRO',
      code: 'FETCH_DRO_ERROR'
    });
  }
});

// POST /api/dro - Create new DRO (MSME only)
router.post('/', authenticateToken, requireUserType('msme'), requireKycApproval, async (req, res) => {
  try {
    const {
      title,
      description,
      industry,
      revenue_percentage,
      minimum_investment,
      target_amount,
      duration_months
    } = req.body;

    // Validation
    if (!title || !industry || !revenue_percentage || !minimum_investment || !target_amount || !duration_months) {
      return res.status(400).json({ 
        error: 'Missing required fields: title, industry, revenue_percentage, minimum_investment, target_amount, duration_months',
        code: 'VALIDATION_ERROR'
      });
    }

    if (revenue_percentage <= 0 || revenue_percentage > 100) {
      return res.status(400).json({ 
        error: 'Revenue percentage must be between 0 and 100',
        code: 'INVALID_REVENUE_PERCENTAGE'
      });
    }

    if (minimum_investment <= 0 || target_amount <= 0) {
      return res.status(400).json({ 
        error: 'Investment amounts must be positive',
        code: 'INVALID_AMOUNTS'
      });
    }

    if (duration_months <= 0 || duration_months > 120) {
      return res.status(400).json({ 
        error: 'Duration must be between 1 and 120 months',
        code: 'INVALID_DURATION'
      });
    }

    const droData: CreateDROData = {
      msme_id: req.user!.userId,
      title,
      description,
      industry,
      revenue_percentage: parseFloat(revenue_percentage),
      minimum_investment: parseFloat(minimum_investment),
      target_amount: parseFloat(target_amount),
      duration_months: parseInt(duration_months)
    };

    const droModel = new DROModel();
    const dro = await droModel.create(droData);

    res.status(201).json({
      message: 'DRO created successfully',
      dro
    });

  } catch (error) {
    console.error('Create DRO error:', error);
    res.status(500).json({ 
      error: 'Failed to create DRO',
      code: 'CREATE_DRO_ERROR'
    });
  }
});


// PUT /api/dro/:id/status - Update DRO status (Admin or MSME owner)
router.put('/:id/status', authenticateToken, async (req, res) => {
  try {
    const id = parseInt(req.params.id);
    const { status } = req.body;

    if (isNaN(id)) {
      return res.status(400).json({ 
        error: 'Invalid DRO ID',
        code: 'INVALID_ID'
      });
    }

    if (!['pending', 'active', 'completed', 'cancelled'].includes(status)) {
      return res.status(400).json({ 
        error: 'Invalid status. Must be one of: pending, active, completed, cancelled',
        code: 'INVALID_STATUS'
      });
    }

    const droModel = new DROModel();
    const dro = await droModel.findById(id);

    if (!dro) {
      return res.status(404).json({ 
        error: 'DRO not found',
        code: 'DRO_NOT_FOUND'
      });
    }

    // Check if user owns this DRO (or is admin - we'll add admin role later)
    if (dro.msme_id !== req.user!.userId) {
      return res.status(403).json({ 
        error: 'You can only update your own DROs',
        code: 'INSUFFICIENT_PERMISSIONS'
      });
    }

    await droModel.updateStatus(id, status);

    res.json({
      message: 'DRO status updated successfully',
      dro_id: id,
      new_status: status
    });

  } catch (error) {
    console.error('Update DRO status error:', error);
    res.status(500).json({ 
      error: 'Failed to update DRO status',
      code: 'UPDATE_STATUS_ERROR'
    });
  }
});

// POST /api/dro/:id/invest - Invest in DRO (Investor only)
router.post('/:id/invest', authenticateToken, requireUserType('investor'), requireKycApproval, async (req, res) => {
  try {
    const droId = parseInt(req.params.id);
    const { amount } = req.body;

    if (isNaN(droId)) {
      return res.status(400).json({ 
        error: 'Invalid DRO ID',
        code: 'INVALID_ID'
      });
    }

    if (!amount || amount <= 0) {
      return res.status(400).json({ 
        error: 'Investment amount must be positive',
        code: 'INVALID_AMOUNT'
      });
    }

    const droModel = new DROModel();
    const investmentModel = new InvestmentModel();

    const dro = await droModel.findById(droId);

    if (!dro) {
      return res.status(404).json({ 
        error: 'DRO not found',
        code: 'DRO_NOT_FOUND'
      });
    }

    if (dro.status !== 'active') {
      return res.status(400).json({ 
        error: 'Can only invest in active DROs',
        code: 'DRO_NOT_ACTIVE'
      });
    }

    if (amount < dro.minimum_investment) {
      return res.status(400).json({ 
        error: `Minimum investment is ${dro.minimum_investment}`,
        code: 'BELOW_MINIMUM_INVESTMENT'
      });
    }

    const totalInvested = await investmentModel.getTotalInvestedInDRO(droId);

    if (totalInvested + amount > dro.target_amount) {
      return res.status(400).json({ 
        error: 'Investment would exceed target amount',
        code: 'EXCEEDS_TARGET'
      });
    }

    // Create investment
    const investmentData: CreateInvestmentData = {
      investor_id: req.user!.userId,
      dro_id: droId,
      amount: parseFloat(amount)
    };

    const investment = await investmentModel.create(investmentData);

    // Update DRO raised amount
    await droModel.incrementRaisedAmount(droId, parseFloat(amount));

    res.status(201).json({
      message: 'Investment created successfully',
      investment
    });

  } catch (error) {
    console.error('Create investment error:', error);
    res.status(500).json({ 
      error: 'Failed to create investment',
      code: 'CREATE_INVESTMENT_ERROR'
    });
  }
});

// DELETE /api/dro/:id - Delete DRO (MSME owner only, only if no investments)
router.delete('/:id', authenticateToken, requireUserType('msme'), async (req, res) => {
  try {
    const id = parseInt(req.params.id);

    if (isNaN(id)) {
      return res.status(400).json({ 
        error: 'Invalid DRO ID',
        code: 'INVALID_ID'
      });
    }

    const droModel = new DROModel();
    const dro = await droModel.findById(id);

    if (!dro) {
      return res.status(404).json({ 
        error: 'DRO not found',
        code: 'DRO_NOT_FOUND'
      });
    }

    // Check if user owns this DRO
    if (dro.msme_id !== req.user!.userId) {
      return res.status(403).json({ 
        error: 'You can only delete your own DROs',
        code: 'INSUFFICIENT_PERMISSIONS'
      });
    }

    await droModel.delete(id);

    res.json({
      message: 'DRO deleted successfully',
      dro_id: id
    });

  } catch (error) {
    if (error instanceof Error && error.message.includes('Cannot delete DRO with existing investments')) {
      return res.status(400).json({ 
        error: 'Cannot delete DRO with existing investments',
        code: 'HAS_INVESTMENTS'
      });
    }

    console.error('Delete DRO error:', error);
    res.status(500).json({ 
      error: 'Failed to delete DRO',
      code: 'DELETE_DRO_ERROR'
    });
  }
});

export default router;

