import bcrypt from 'bcryptjs';
import { randomUUID } from 'crypto';
import { duckdb } from '../config/duckdb-connection';

export interface User {
  id?: string;
  email: string;
  password_hash?: string;
  first_name?: string;
  last_name?: string;
  user_type: 'investor' | 'msme';
  kyc_status?: 'pending' | 'approved' | 'rejected';
  is_verified?: boolean;
  created_at?: string;
  updated_at?: string;
}

export interface CreateUserData {
  email: string;
  password: string;
  first_name?: string;
  last_name?: string;
  user_type: 'investor' | 'msme';
}

export class UserModel {
  // No need for constructor - using global duckdb connection

  async create(userData: CreateUserData): Promise<User> {
    const { email, password, first_name, last_name, user_type } = userData;

    // Generate UUID for user ID
    const id = randomUUID();

    // Hash password
    const password_hash = await bcrypt.hash(password, 12);

    try {
      // Insert user with UUID
      const sql = `
        INSERT INTO users (id, email, password_hash, first_name, last_name, user_type)
        VALUES (?, ?, ?, ?, ?, ?)
        RETURNING *
      `;

      const result = await duckdb.query(sql, [id, email, password_hash, first_name, last_name, user_type]);

      if (result.rows && result.rows.length > 0) {
        // Remove password hash from response
        const { password_hash, ...userWithoutPassword } = result.rows[0] as User;
        return userWithoutPassword as User;
      } else {
        throw new Error('User not found after creation');
      }
    } catch (error) {
      throw error;
    }
  }

  async findByEmail(email: string): Promise<User | null> {
    try {
      const sql = 'SELECT * FROM users WHERE email = ?';
      const result = await duckdb.query(sql, [email]);
      return result.rows && result.rows.length > 0 ? result.rows[0] as User : null;
    } catch (error) {
      throw error;
    }
  }

  async findById(id: string): Promise<User | null> {
    try {
      const sql = 'SELECT * FROM users WHERE id = ?';
      const result = await duckdb.query(sql, [id]);
      if (result.rows && result.rows.length > 0) {
        // Remove password hash from response
        const { password_hash, ...userWithoutPassword } = result.rows[0] as User;
        return userWithoutPassword as User;
      } else {
        return null;
      }
    } catch (error) {
      throw error;
    }
  }

  async findByIdWithPassword(id: string): Promise<User | null> {
    try {
      const sql = 'SELECT * FROM users WHERE id = ?';
      const result = await duckdb.query(sql, [id]);
      return result.rows && result.rows.length > 0 ? result.rows[0] as User : null;
    } catch (error) {
      throw error;
    }
  }

  async verifyPassword(plainPassword: string, hashedPassword: string): Promise<boolean> {
    return bcrypt.compare(plainPassword, hashedPassword);
  }

  async updateKycStatus(userId: string, status: 'pending' | 'approved' | 'rejected'): Promise<void> {
    try {
      const sql = 'UPDATE users SET kyc_status = ?, updated_at = CURRENT_TIMESTAMP WHERE id = ?';
      await duckdb.query(sql, [status, userId]);
    } catch (error) {
      throw error;
    }
  }

  async verifyUser(userId: string): Promise<void> {
    try {
      const sql = 'UPDATE users SET is_verified = true, updated_at = CURRENT_TIMESTAMP WHERE id = ?';
      await duckdb.query(sql, [userId]);
    } catch (error) {
      throw error;
    }
  }

  async getAllUsers(limit: number = 50, offset: number = 0): Promise<User[]> {
    try {
      const sql = `
        SELECT id, email, first_name, last_name, user_type, kyc_status, is_verified, created_at, updated_at
        FROM users
        ORDER BY created_at DESC
        LIMIT ? OFFSET ?
      `;
      const result = await duckdb.query(sql, [limit, offset]);
      return result.rows as User[];
    } catch (error) {
      throw error;
    }
  }

  async getUserCounts(): Promise<{total_users: number, investor_users: number, msme_users: number}> {
    try {
      const sql = `
        SELECT 
          COUNT(*) as total_users,
          SUM(CASE WHEN user_type = 'investor' THEN 1 ELSE 0 END) as investor_users,
          SUM(CASE WHEN user_type = 'msme' THEN 1 ELSE 0 END) as msme_users
        FROM users
      `;

      const result = await duckdb.query(sql);
      if (result.rows && result.rows.length > 0) {
        const row = result.rows[0];
        return {
          total_users: Number(row.total_users),
          investor_users: Number(row.investor_users),
          msme_users: Number(row.msme_users)
        };
      } else {
        return {total_users: 0, investor_users: 0, msme_users: 0};
      }
    } catch (error) {
      console.error('Get user counts error:', error);
      throw error;
    }
  }
}

