      end_date as string
    );

    if (format === 'csv') {
      const csv = reportingService.exportToCSV(report, 'investor');
      res.setHeader('Content-Type', 'text/csv');
      res.setHeader('Content-Disposition', `attachment; filename=investor-report-${req.user!.userId}-${start_date}-${end_date}.csv`);
      res.send(csv);
    } else {
