      end_date as string
    );

    if (format === 'csv') {
      const csv = reportingService.exportToCSV(report, 'msme');
      res.setHeader('Content-Type', 'text/csv');
      res.setHeader('Content-Disposition', `attachment; filename=msme-report-${req.user!.userId}-${start_date}-${end_date}.csv`);
      res.send(csv);
    } else {
      res.json({
        report
      });
    }

  } catch (error) {
    console.error('Generate MSME report error:', error);
    res.status(500).json({ 
      error: 'Failed to generate MSME report',
      code: 'GENERATE_MSME_REPORT_ERROR'
    });
  }
});

export default router;

