import { RevenueDistributionModel, InvestorPayoutModel } from '../models/RevenueDistribution';
import { DROModel } from '../models/DRO';
import { InvestmentModel } from '../models/Investment';
import { UserModel } from '../models/User';

export interface PlatformRevenueReport {
  period: {
    start_date: string;
    end_date: string;
  };
  summary: {
    total_revenue_distributed: number;
    total_payouts_processed: number;
    active_dros: number;
    active_investors: number;
    platform_performance: number; // Success rate %
  };
  dro_performance: Array<{
    dro_id: number;
    dro_title: string;
    industry: string;
    total_revenue: number;
    total_distributed: number;
    investor_count: number;
    distribution_count: number;
  }>;
  investor_performance: Array<{
    investor_id: number;
    investor_name: string;
    total_invested: number;
    total_returns: number;
    roi_percentage: number;
    active_investments: number;
  }>;
}

export interface InvestorReport {
  investor_id: number;
  report_period: {
    start_date: string;
    end_date: string;
  };
  portfolio_summary: {
    total_invested: number;
    total_returns: number;
    net_profit: number;
    roi_percentage: number;
    active_investments: number;
  };
  investment_breakdown: Array<{
    dro_id: number;
    dro_title: string;
    industry: string;
    investment_amount: number;
    total_returns: number;
    roi_percentage: number;
    status: string;
  }>;
  monthly_performance: Array<{
    month: string;
    payouts_received: number;
    payout_amount: number;
  }>;
}

export interface MSMEReport {
  msme_id: number;
  report_period: {
    start_date: string;
    end_date: string;
  };
  business_summary: {
    total_revenue_reported: number;
    total_amount_distributed: number;
    distribution_percentage: number;
    total_funding_raised: number;
    investor_count: number;
  };
  dro_performance: Array<{
    dro_id: number;
    title: string;
    funding_status: string;
    total_raised: number;
    target_amount: number;
    funding_percentage: number;
    total_revenue_shared: number;
    distribution_count: number;
  }>;
  monthly_distributions: Array<{
    month: string;
    total_revenue: number;
    distribution_amount: number;
    investor_payouts: number;
  }>;
}

export class RevenueReportingService {
  private revenueDistributionModel: RevenueDistributionModel;
  private investorPayoutModel: InvestorPayoutModel;
  private droModel: DROModel;
  private investmentModel: InvestmentModel;
  private userModel: UserModel;

  constructor() {
    this.revenueDistributionModel = new RevenueDistributionModel();
    this.investorPayoutModel = new InvestorPayoutModel();
    this.droModel = new DROModel();
    this.investmentModel = new InvestmentModel();
    this.userModel = new UserModel();
  }

  /**
   * Generate comprehensive platform revenue report
   */
  async generatePlatformReport(startDate: string, endDate: string): Promise<PlatformRevenueReport> {
    try {
      console.log(`ðŸ“Š Generating platform report for ${startDate} to ${endDate}`);

      // Get revenue distributions in date range
      const distributions = await this.revenueDistributionModel.findByDateRange(startDate, endDate);

      // Calculate summary metrics
      const summary = {
        total_revenue_distributed: distributions.reduce((sum, d) => sum + d.distribution_amount, 0),
        total_payouts_processed: 0, // Will be calculated below
        active_dros: new Set(distributions.map(d => d.dro_id)).size,
        active_investors: 0, // Will be calculated below
        platform_performance: 0 // Will be calculated below
      };

      // Get DRO performance data
      const droPerformance = await this.getDROPerformanceData(distributions);

      // Get investor performance data
      const investorPerformance = await this.getInvestorPerformanceData(startDate, endDate);

      // Update summary with calculated values
      summary.active_investors = investorPerformance.length;
      summary.total_payouts_processed = investorPerformance.reduce((sum, inv) => sum + inv.total_returns, 0);
      summary.platform_performance = this.calculatePlatformPerformance(distributions);

      return {
        period: {
          start_date: startDate,
          end_date: endDate
        },
        summary,
        dro_performance: droPerformance,
        investor_performance: investorPerformance
      };

    } catch (error) {
      console.error('Error generating platform report:', error);
      throw error;
    }
  }

  /**
   * Generate investor-specific report
   */
  async generateInvestorReport(investorId: number, startDate: string, endDate: string): Promise<InvestorReport> {
    try {
      console.log(`ðŸ“Š Generating investor report for user ${investorId}`);

      // Get investor portfolio
      const portfolio = await this.investmentModel.getInvestorPortfolio(investorId);

      // Get payouts in date range
      const payouts = await this.investorPayoutModel.findByInvestor(investorId);
      const periodPayouts = payouts.filter(p => 
        p.payout_date && p.payout_date >= startDate && p.payout_date <= endDate
      );

      // Calculate portfolio summary
      const portfolioSummary = {
        total_invested: portfolio.total_invested,
        total_returns: portfolio.total_return_earned,
        net_profit: portfolio.total_return_earned - portfolio.total_invested,
        roi_percentage: portfolio.total_invested > 0 ? 
          ((portfolio.total_return_earned - portfolio.total_invested) / portfolio.total_invested) * 100 : 0,
        active_investments: portfolio.active_investments
      };

      // Get investment breakdown
      const investmentBreakdown = await this.getInvestmentBreakdown(investorId);

      // Get monthly performance
      const monthlyPerformance = await this.getMonthlyPerformance(investorId, startDate, endDate);

      return {
        investor_id: investorId,
        report_period: {
          start_date: startDate,
          end_date: endDate
        },
        portfolio_summary: portfolioSummary,
        investment_breakdown: investmentBreakdown,
        monthly_performance: monthlyPerformance
      };

    } catch (error) {
      console.error('Error generating investor report:', error);
      throw error;
    }
  }

  /**
   * Generate MSME-specific report
   */
  async generateMSMEReport(msmeId: number, startDate: string, endDate: string): Promise<MSMEReport> {
    try {
      console.log(`ðŸ“Š Generating MSME report for user ${msmeId}`);

      // Get MSME's DROs
      const dros = await this.droModel.findByMSME(msmeId);

      // Get revenue distributions for MSME's DROs in date range
      let totalRevenueReported = 0;
      let totalAmountDistributed = 0;
      let totalFundingRaised = 0;
      let uniqueInvestors = new Set<number>();

      const droPerformance = [];
      const monthlyDistributions = [];

      for (const dro of dros) {
        const distributions = await this.revenueDistributionModel.findByDRO(dro.id!);
        const periodDistributions = distributions.filter(d => 
          d.distribution_date >= startDate && d.distribution_date <= endDate
        );

        const droRevenue = periodDistributions.reduce((sum, d) => sum + d.total_revenue, 0);
        const droDistribution = periodDistributions.reduce((sum, d) => sum + d.distribution_amount, 0);

        totalRevenueReported += droRevenue;
        totalAmountDistributed += droDistribution;
        totalFundingRaised += dro.raised_amount || 0;

        // Get investors for this DRO
        const investments = await this.investmentModel.findByDRO(dro.id!);
        investments.forEach(inv => uniqueInvestors.add(inv.investor_id));

        droPerformance.push({
          dro_id: dro.id!,
          title: dro.title,
          funding_status: dro.status || 'unknown',
          total_raised: dro.raised_amount || 0,
          target_amount: dro.target_amount,
          funding_percentage: dro.target_amount > 0 ? ((dro.raised_amount || 0) / dro.target_amount) * 100 : 0,
          total_revenue_shared: droRevenue,
          distribution_count: periodDistributions.length
        });
      }

      // Calculate monthly distributions
      const monthlyData = await this.getMSMEMonthlyDistributions(msmeId, startDate, endDate);

      const businessSummary = {
        total_revenue_reported: totalRevenueReported,
        total_amount_distributed: totalAmountDistributed,
        distribution_percentage: totalRevenueReported > 0 ? (totalAmountDistributed / totalRevenueReported) * 100 : 0,
        total_funding_raised: totalFundingRaised,
        investor_count: uniqueInvestors.size
      };

      return {
        msme_id: msmeId,
        report_period: {
          start_date: startDate,
          end_date: endDate
        },
        business_summary: businessSummary,
        dro_performance: droPerformance,
        monthly_distributions: monthlyData
      };

    } catch (error) {
      console.error('Error generating MSME report:', error);
      throw error;
    }
  }

  /**
   * Export report to CSV format
   */
  exportToCSV(report: PlatformRevenueReport | InvestorReport | MSMEReport, reportType: string): string {
    try {
      let csv = '';

      if (reportType === 'platform') {
        const platformReport = report as PlatformRevenueReport;
        csv = this.generatePlatformCSV(platformReport);
      } else if (reportType === 'investor') {
        const investorReport = report as InvestorReport;
        csv = this.generateInvestorCSV(investorReport);
      } else if (reportType === 'msme') {
        const msmeReport = report as MSMEReport;
        csv = this.generateMSMECSV(msmeReport);
      }

      return csv;

    } catch (error) {
      console.error('Error exporting to CSV:', error);
      throw error;
    }
  }

  /**
   * Private helper methods
   */
  private async getDROPerformanceData(distributions: any[]): Promise<any[]> {
    const droGroups = new Map<number, any[]>();
    distributions.forEach(d => {
      if (!droGroups.has(d.dro_id)) {
        droGroups.set(d.dro_id, []);
      }
      droGroups.get(d.dro_id)!.push(d);
    });

    const performance = [];
    for (const [droId, droDistributions] of droGroups) {
      const dro = await this.droModel.findById(droId);
      if (dro) {
        const investments = await this.investmentModel.findByDRO(droId);

        performance.push({
          dro_id: droId,
          dro_title: dro.title,
          industry: dro.industry,
          total_revenue: droDistributions.reduce((sum, d) => sum + d.total_revenue, 0),
          total_distributed: droDistributions.reduce((sum, d) => sum + d.distribution_amount, 0),
          investor_count: investments.length,
          distribution_count: droDistributions.length
        });
      }
    }

    return performance;
  }

  private async getInvestorPerformanceData(startDate: string, endDate: string): Promise<any[]> {
    // This is a simplified implementation
    // In production, you'd want to optimize this query
    const allUsers = await this.userModel.getAllUsers(1000, 0);
    const investors = allUsers.filter(u => u.user_type === 'investor');

    const performance = [];
    for (const investor of investors) {
      const portfolio = await this.investmentModel.getInvestorPortfolio(investor.id!);
      if (portfolio.total_invested > 0) {
        const netProfit = portfolio.total_return_earned - portfolio.total_invested;
        const roiPercentage = (netProfit / portfolio.total_invested) * 100;

        performance.push({
          investor_id: investor.id!,
          investor_name: `${investor.first_name || ''} ${investor.last_name || ''}`.trim() || 'Unknown',
          total_invested: portfolio.total_invested,
          total_returns: portfolio.total_return_earned,
          roi_percentage: roiPercentage,
          active_investments: portfolio.active_investments
        });
      }
    }

    return performance;
  }

  private async getInvestmentBreakdown(investorId: number): Promise<any[]> {
    const investments = await this.investmentModel.findByInvestor(investorId);

    return investments.map(inv => ({
      dro_id: inv.dro_id,
      dro_title: inv.dro_title || 'Unknown',
      industry: inv.dro_industry || 'Unknown',
      investment_amount: inv.amount,
      total_returns: 0, // Would be calculated from payouts
      roi_percentage: 0, // Would be calculated
      status: inv.status || 'unknown'
    }));
  }

  private async getMonthlyPerformance(investorId: number, startDate: string, endDate: string): Promise<any[]> {
    const payouts = await this.investorPayoutModel.findByInvestor(investorId);
    const periodPayouts = payouts.filter(p => 
      p.payout_date && p.payout_date >= startDate && p.payout_date <= endDate
    );

    // Group by month
    const monthlyData = new Map<string, any>();
    periodPayouts.forEach(payout => {
      if (payout.payout_date) {
        const month = payout.payout_date.substring(0, 7); // YYYY-MM
        if (!monthlyData.has(month)) {
          monthlyData.set(month, { payouts_received: 0, payout_amount: 0 });
        }
        const data = monthlyData.get(month)!;
        data.payouts_received++;
        data.payout_amount += payout.payout_amount;
      }
    });

    return Array.from(monthlyData.entries()).map(([month, data]) => ({
      month,
      payouts_received: data.payouts_received,
      payout_amount: data.payout_amount
    }));
  }

  private async getMSMEMonthlyDistributions(msmeId: number, startDate: string, endDate: string): Promise<any[]> {
    // Simplified implementation
    return [];
  }

  private calculatePlatformPerformance(distributions: any[]): number {
    // Calculate platform success rate based on distribution frequency and amount
    // This is a simplified metric
    return distributions.length > 0 ? 95.5 : 0; // Placeholder
  }

  private generatePlatformCSV(report: PlatformRevenueReport): string {
    let csv = 'Platform Revenue Report\n\n';
    csv += 'Period,' + report.period.start_date + ' to ' + report.period.end_date + '\n\n';
    csv += 'Summary\n';
    csv += 'Total Revenue Distributed,' + report.summary.total_revenue_distributed + '\n';
    csv += 'Total Payouts Processed,' + report.summary.total_payouts_processed + '\n';
    csv += 'Active DROs,' + report.summary.active_dros + '\n';
    csv += 'Active Investors,' + report.summary.active_investors + '\n';
    csv += 'Platform Performance,' + report.summary.platform_performance + '%\n\n';

    csv += 'DRO Performance\n';
    csv += 'DRO ID,Title,Industry,Total Revenue,Total Distributed,Investor Count,Distribution Count\n';
    report.dro_performance.forEach(dro => {
      csv += `${dro.dro_id},${dro.dro_title},${dro.industry},${dro.total_revenue},${dro.total_distributed},${dro.investor_count},${dro.distribution_count}\n`;
    });

    return csv;
  }

  private generateInvestorCSV(report: InvestorReport): string {
    let csv = 'Investor Report\n\n';
    csv += 'Investor ID,' + report.investor_id + '\n';
    csv += 'Period,' + report.report_period.start_date + ' to ' + report.report_period.end_date + '\n\n';

    csv += 'Portfolio Summary\n';
    csv += 'Total Invested,' + report.portfolio_summary.total_invested + '\n';
    csv += 'Total Returns,' + report.portfolio_summary.total_returns + '\n';
    csv += 'Net Profit,' + report.portfolio_summary.net_profit + '\n';
    csv += 'ROI Percentage,' + report.portfolio_summary.roi_percentage + '%\n';
    csv += 'Active Investments,' + report.portfolio_summary.active_investments + '\n\n';

    csv += 'Investment Breakdown\n';
    csv += 'DRO ID,Title,Industry,Investment Amount,Total Returns,ROI %,Status\n';
    report.investment_breakdown.forEach(inv => {
      csv += `${inv.dro_id},${inv.dro_title},${inv.industry},${inv.investment_amount},${inv.total_returns},${inv.roi_percentage},${inv.status}\n`;
    });

    return csv;
  }

  private generateMSMECSV(report: MSMEReport): string {
    let csv = 'MSME Report\n\n';
    csv += 'MSME ID,' + report.msme_id + '\n';
    csv += 'Period,' + report.report_period.start_date + ' to ' + report.report_period.end_date + '\n\n';

    csv += 'Business Summary\n';
    csv += 'Total Revenue Reported,' + report.business_summary.total_revenue_reported + '\n';
    csv += 'Total Amount Distributed,' + report.business_summary.total_amount_distributed + '\n';
    csv += 'Distribution Percentage,' + report.business_summary.distribution_percentage + '%\n';
    csv += 'Total Funding Raised,' + report.business_summary.total_funding_raised + '\n';
    csv += 'Investor Count,' + report.business_summary.investor_count + '\n\n';

    csv += 'DRO Performance\n';
    csv += 'DRO ID,Title,Status,Total Raised,Target Amount,Funding %,Revenue Shared,Distributions\n';
    report.dro_performance.forEach(dro => {
      csv += `${dro.dro_id},${dro.title},${dro.funding_status},${dro.total_raised},${dro.target_amount},${dro.funding_percentage},${dro.total_revenue_shared},${dro.distribution_count}\n`;
    });

    return csv;
  }
