import { dbManager } from '../config/database';
import { UserModel } from '../models/User';
import { DROModel } from '../models/DRO';
import { InvestmentModel } from '../models/Investment';
import { RevenueDistributionModel } from '../models/RevenueDistribution';

// Demo users data
const demoUsers = [
  {
    email: 'investor@demo.com',
    password: 'demo123456',
    name: 'Demo Investor',
    first_name: 'Demo',
    last_name: 'Investor',
    user_type: 'investor' as const,
  },
  {
    email: 'msme@demo.com',
    password: 'demo123456',
    name: 'Demo MSME',
    first_name: 'Demo',
    last_name: 'MSME',
    user_type: 'msme' as const,
  },
  {
    email: 'john.investor@example.com',
    password: 'password123',
    name: 'John Smith',
    first_name: 'John',
    last_name: 'Smith',
    user_type: 'investor' as const,
  },
  {
    email: 'sarah.tech@example.com',
    password: 'password123',
    name: 'Sarah Chen',
    first_name: 'Sarah',
    last_name: 'Chen',
    user_type: 'msme' as const,
  },
  {
    email: 'mike.investor@example.com',
    password: 'password123',
    name: 'Mike Johnson',
    first_name: 'Mike',
    last_name: 'Johnson',
    user_type: 'investor' as const,
  },
  {
    email: 'lisa.restaurant@example.com',
    password: 'password123',
    name: 'Lisa Wong',
    first_name: 'Lisa',
    last_name: 'Wong',
    user_type: 'msme' as const,
  },
];

// Demo DROs data
const demoDROs = [
  {
    title: 'TechStart SaaS Growth Funding',
    description: 'Growing SaaS platform seeking funding for international expansion. We provide project management software for small teams with 500+ active customers.',
    industry: 'Technology',
    revenue_percentage: 5.5,
    minimum_investment: 1000,
    target_amount: 50000,
    duration_months: 18,
    msme_email: 'sarah.tech@example.com',
  },
  {
    title: 'Green CafÃ© Revenue Sharing',
    description: 'Established eco-friendly cafÃ© chain with 3 locations. Seeking investment for 4th location and sustainable packaging initiative.',
    industry: 'Food & Beverage',
    revenue_percentage: 7.2,
    minimum_investment: 500,
    target_amount: 30000,
    duration_months: 24,
    msme_email: 'lisa.restaurant@example.com',
  },
  {
    title: 'E-Commerce Platform Expansion',
    description: 'Online marketplace connecting local artisans with global customers. 200+ active sellers, seeking funding for mobile app development.',
    industry: 'E-Commerce',
    revenue_percentage: 4.8,
    minimum_investment: 2000,
    target_amount: 75000,
    duration_months: 12,
    msme_email: 'sarah.tech@example.com',
  },
  {
    title: 'Fitness Studio Equipment Upgrade',
    description: 'Popular fitness studio with 300+ members seeking equipment upgrades and class expansion. Strong community following.',
    industry: 'Health & Fitness',
    revenue_percentage: 6.0,
    minimum_investment: 250,
    target_amount: 20000,
    duration_months: 15,
    msme_email: 'lisa.restaurant@example.com',
  },
  {
    title: 'Educational Platform Scaling',
    description: 'Online learning platform for professional development. 1000+ enrolled students, seeking funding for content expansion.',
    industry: 'Education',
    revenue_percentage: 5.2,
    minimum_investment: 1500,
    target_amount: 60000,
    duration_months: 20,
    msme_email: 'sarah.tech@example.com',
  },
];

// Demo investments data (will be created after DROs)
const demoInvestments = [
  {
    investor_email: 'investor@demo.com',
    dro_title: 'TechStart SaaS Growth Funding',
    amount: 5000,
  },
  {
    investor_email: 'john.investor@example.com',
    dro_title: 'TechStart SaaS Growth Funding',
    amount: 10000,
  },
  {
    investor_email: 'mike.investor@example.com',
    dro_title: 'Green CafÃ© Revenue Sharing',
    amount: 3000,
  },
  {
    investor_email: 'investor@demo.com',
    dro_title: 'E-Commerce Platform Expansion',
    amount: 8000,
  },
  {
    investor_email: 'john.investor@example.com',
    dro_title: 'Fitness Studio Equipment Upgrade',
    amount: 1500,
  },
  {
    investor_email: 'mike.investor@example.com',
    dro_title: 'Educational Platform Scaling',
    amount: 6000,
  },
];

async function seedDatabase() {
  try {
    console.log('ðŸŒ± Starting database seeding...');

    // Initialize database connection
    await dbManager.connect();

    // Initialize models
    const userModel = new UserModel();
    const droModel = new DROModel();
    const investmentModel = new InvestmentModel();
    const revenueModel = new RevenueDistributionModel();

    // Create demo users
    console.log('ðŸ‘¥ Creating demo users...');
    const createdUsers: any[] = [];

    for (const userData of demoUsers) {
      try {
        const user = await userModel.create(userData);
        createdUsers.push(user);
        console.log(`âœ… Created user: ${userData.email}`);
      } catch (error: any) {
        if (error.message?.includes('UNIQUE constraint failed')) {
          console.log(`âš ï¸  User already exists: ${userData.email}`);
          // Get existing user
          const existingUser = await userModel.findByEmail(userData.email);
          if (existingUser) {
            createdUsers.push(existingUser);
          }
        } else {
          console.error(`âŒ Error creating user ${userData.email}:`, error.message);
        }
      }
    }

    // Create demo DROs
    console.log('ðŸŽ¯ Creating demo DROs...');
    const createdDROs: any[] = [];

    for (const droData of demoDROs) {
      try {
        // Find MSME user
        const msmeUser = createdUsers.find(user => user.email === droData.msme_email);
        if (!msmeUser) {
          console.error(`âŒ MSME user not found: ${droData.msme_email}`);
          continue;
        }

        const dro = await droModel.create({
          ...droData,
          msme_id: msmeUser.id,
        });

        createdDROs.push(dro);
        console.log(`âœ… Created DRO: ${droData.title}`);
      } catch (error: any) {
        console.error(`âŒ Error creating DRO ${droData.title}:`, error.message);
      }
    }

    // Create demo investments
    console.log('ðŸ’° Creating demo investments...');

    for (const investmentData of demoInvestments) {
      try {
        // Find investor user
        const investor = createdUsers.find(user => user.email === investmentData.investor_email);
        if (!investor) {
          console.error(`âŒ Investor not found: ${investmentData.investor_email}`);
          continue;
        }

        // Find DRO
        const dro = createdDROs.find(d => d.title === investmentData.dro_title);
        if (!dro) {
          console.error(`âŒ DRO not found: ${investmentData.dro_title}`);
          continue;
        }

        const investment = await investmentModel.create({
          investor_id: investor.id,
          dro_id: dro.id,
          amount: investmentData.amount,
        });

        console.log(`âœ… Created investment: ${investor.email} -> ${dro.title} ($${investmentData.amount})`);
      } catch (error: any) {
        console.error(`âŒ Error creating investment:`, error.message);
      }
    }

    // Create some demo revenue distributions
    console.log('ðŸ“Š Creating demo revenue distributions...');

    for (const dro of createdDROs.slice(0, 3)) { // Only for first 3 DROs
      try {
        const totalRevenue = Math.floor(Math.random() * 5000) + 1000; // Random revenue between $1000-$6000
        const revenue = await revenueModel.create({
          dro_id: dro.id,
          distribution_date: new Date().toISOString().split('T')[0],
          total_revenue: totalRevenue,
        });

        console.log(`âœ… Created revenue distribution for: ${dro.title} ($${totalRevenue})`);
      } catch (error: any) {
        console.error(`âŒ Error creating revenue distribution:`, error.message);
      }
    }

    console.log('ðŸŽ‰ Database seeding completed successfully!');
    console.log(`ðŸ“Š Summary:
    - Users: ${createdUsers.length}
    - DROs: ${createdDROs.length}
    - Investments: ${demoInvestments.length}
    - Revenue distributions: 3`);

  } catch (error) {
    console.error('âŒ Database seeding failed:', error);
    throw error;
  }
}

// Run seeding if this file is executed directly
if (require.main === module) {
  seedDatabase()
    .then(() => {
      console.log('âœ… Seeding process completed');
      process.exit(0);
    })
    .catch((error) => {
      console.error('âŒ Seeding process failed:', error);
      process.exit(1);
    });
}

export { seedDatabase };

