    user_type: 'msme'
  },
  {
    email: 'mike.investor@example.com',
    password: 'password123',
    first_name: 'Mike',
    last_name: 'Johnson',
    user_type: 'investor'
  },
  {
    email: 'lisa.restaurant@example.com',
    password: 'password123',
    first_name: 'Lisa',
    last_name: 'Wong',
    user_type: 'msme'
  }
];

async function createUser(userData) {
  try {
    const response = await fetch(`${API_BASE}/auth/register`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(userData)
    });

    if (response.ok) {
      const result = await response.json();
      console.log(`âœ… Created user: ${userData.email} (${userData.user_type})`);
      return result;
    } else {
      const error = await response.text();
      console.log(`âŒ Failed to create ${userData.email}: ${error}`);
      return null;
    }
  } catch (error) {
    console.error(`âŒ Error creating ${userData.email}:`, error.message);
    return null;
  }
}

async function createAllUsers() {
  console.log('ðŸ‘¥ Creating demo users via API...');

  let successCount = 0;

  for (const userData of demoUsers) {
    const result = await createUser(userData);
    if (result) {
      successCount++;
    }
    // Small delay between requests
    await new Promise(resolve => setTimeout(resolve, 200));
  }

  console.log(`\nðŸŽ‰ User creation completed!`);
  console.log(`ðŸ“ˆ Successfully created: ${successCount}/${demoUsers.length} users`);

  // Verify results
  console.log('\nðŸ” Verifying results...');
  try {
    const response = await fetch(`${API_BASE}/dro/statistics`);
    if (response.ok) {
      const stats = await response.json();
      console.log(`ðŸ“Š Platform now has:`);
      console.log(`   - Total users: ${stats.statistics?.total_users || 'Unknown'}`);
      console.log(`   - Total DROs: ${stats.statistics?.total_dros || 0}`);
    }
  } catch (error) {
    console.log('âš ï¸  Could not verify results');
  }
}

console.log('ðŸš€ Starting Demo User Creation...');
createAllUsers()
  .then(() => {
    console.log('âœ… Process completed successfully');
    process.exit(0);
  })
  .catch((error) => {
    console.error('âŒ Process failed:', error);
    process.exit(1);
  });

