      const response = await fetch(`${API_BASE}/dro`);
      if (response.ok) {
        const result = await response.json();
        const totalDROs = result.pagination?.total || result.length || 'unknown';
        console.log(`ðŸ“Š Total DROs in system: ${totalDROs}`);

        if (result.data && Array.isArray(result.data)) {
          // Show distribution by industry
          const industryCount = {};
