const fetch = require('node-fetch');

const API_BASE = 'http://localhost:3001/api';
const API_TOKEN = 'sirex-api-token-2025-batch-operations';

// Sample MSME users for DRO creation
const sampleMSMEUsers = [
  { id: 1, name: 'Tech Startup Co' },
  { id: 2, name: 'Green Energy Solutions' },
  { id: 3, name: 'Local Food Market' },
  { id: 4, name: 'Healthcare Services' },
  { id: 5, name: 'Education Platform' },
  { id: 6, name: 'Real Estate Group' },
  { id: 7, name: 'Manufacturing Ltd' },
  { id: 8, name: 'Retail Chain' }
];

function generateDROData(index) {
  const industries = [
    'Technology', 'Food & Beverage', 'Services', 'Healthcare', 'Education', 
    'Real Estate', 'Entertainment', 'Manufacturing', 'Agriculture', 'Retail', 
    'Renewable Energy', 'Logistics', 'Finance', 'Tourism', 'Construction'
  ];

  const riskLevels = ['low', 'medium', 'high'];

  const industry = industries[index % industries.length];
  const riskLevel = riskLevels[index % riskLevels.length];
  const msmeUser = sampleMSMEUsers[index % sampleMSMEUsers.length];

  const revenuePercentage = 5 + (index % 25); // 5% to 30%
  const minInvestment = 1000 + (index * 100); // Starting from 1k
  const targetAmount = minInvestment * (2 + (index % 8)); // 2x to 10x min investment
  const annualRevenue = 200000 + (index * 3000); // 200k to 800k+

  return {
    msme_id: msmeUser.id,
    title: `${industry} Revenue Share Opportunity ${index + 1}`,
    description: `Established ${industry.toLowerCase()} business seeking growth capital. This opportunity offers investors a ${revenuePercentage}% share of monthly revenue with proven market demand and strong growth potential. The business has demonstrated consistent revenue generation and is looking to expand operations.`,
    industry,
    revenue_percentage: revenuePercentage,
    minimum_investment: minInvestment,
    target_amount: targetAmount,
    duration_months: 12 + (index % 36), // 12 to 48 months
    annual_revenue: annualRevenue,
    roi_estimate: revenuePercentage * 1.5, // Estimated ROI
    risk_level: riskLevel,
    collateral_description: `${industry} business assets including equipment, inventory, and revenue contracts. Additional security provided through business insurance and personal guarantees.`,
    use_of_funds: `Business expansion including ${industry.toLowerCase()} equipment upgrades, working capital for operations, marketing initiatives, and strategic growth investments.`,
    repayment_terms: `Monthly revenue distributions based on actual business performance. Payments are calculated as ${revenuePercentage}% of verified monthly revenue and distributed within 5 business days of month-end.`
  };
}

async function testConnection() {
  try {
    console.log('ðŸ” Testing API connection...');
    const response = await fetch(`http://localhost:3001/health`);
    if (response.ok) {
      console.log('âœ… API connection successful');
      return true;
    } else {
      console.log('âŒ API connection failed:', response.status);
      return false;
    }
  } catch (error) {
    console.error('âŒ API connection error:', error.message);
    return false;
  }
}

async function createDROsBatch(dros) {
  try {
    console.log(`ðŸš€ Creating batch of ${dros.length} DROs...`);

    const response = await fetch(`${API_BASE}/batch/dros`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'X-API-Key': API_TOKEN
      },
      body: JSON.stringify({ dros })
    });

    if (response.ok) {
      const result = await response.json();
      console.log(`âœ… Batch created: ${result.summary.successful}/${result.summary.total} DROs`);

      if (result.errors && result.errors.length > 0) {
        console.log('âš ï¸  Errors encountered:');
        result.errors.slice(0, 5).forEach(error => {
          console.log(`   - Index ${error.index}: ${error.error}`);
        });
        if (result.errors.length > 5) {
          console.log(`   ... and ${result.errors.length - 5} more errors`);
        }
      }

      return result.summary.successful;
    } else {
      const error = await response.text();
      console.log(`âŒ Batch creation failed: ${response.status} - ${error}`);
      return 0;
    }
  } catch (error) {
    console.error(`âŒ Batch creation error:`, error.message);
    return 0;
  }
}

async function import200DROs() {
  try {
    console.log('ðŸŽ¯ Starting 200 DRO import process...');
    console.log('ðŸ“¡ API Base URL:', API_BASE);

    // Test connection
    const connected = await testConnection();
    if (!connected) {
      throw new Error('Cannot connect to API. Make sure the server is running.');
    }

    // Check current DRO count
    console.log('ðŸ“Š Checking current DRO count...');
    const statsResponse = await fetch(`${API_BASE}/dro/statistics`);
    if (statsResponse.ok) {
      const stats = await statsResponse.json();
      console.log(`Current DRO count: ${stats.statistics.total_dros}`);
    }

    console.log('ðŸ­ Generating 200 DRO records...');

    // Generate all 200 DROs
    const allDROs = [];
    for (let i = 0; i < 200; i++) {
      allDROs.push(generateDROData(i));
    }

    console.log('ðŸ“¦ Splitting into batches of 100...');

    // Split into batches of 100 (API limit)
    const batch1 = allDROs.slice(0, 100);
    const batch2 = allDROs.slice(100, 200);

    let totalCreated = 0;

    // Create batch 1
    console.log('\nðŸ”„ Processing Batch 1 (DROs 1-100)...');
    const batch1Success = await createDROsBatch(batch1);
    totalCreated += batch1Success;

    // Small delay between batches
    await new Promise(resolve => setTimeout(resolve, 1000));

    // Create batch 2
    console.log('\nðŸ”„ Processing Batch 2 (DROs 101-200)...');
    const batch2Success = await createDROsBatch(batch2);
    totalCreated += batch2Success;

    console.log('\nðŸŽ‰ Import process completed!');
    console.log(`ðŸ“ˆ Final result: ${totalCreated}/200 DROs created successfully`);

    // Verify final count
    console.log('\nðŸ” Verifying final results...');
    const finalStatsResponse = await fetch(`${API_BASE}/dro/statistics`);
    if (finalStatsResponse.ok) {
      const finalStats = await finalStatsResponse.json();
      console.log(`ðŸ“Š Total DROs in system: ${finalStats.statistics.total_dros}`);

      if (finalStats.industry_breakdown && finalStats.industry_breakdown.length > 0) {
        console.log('\nðŸ­ Industry breakdown:');
        finalStats.industry_breakdown.forEach(item => {
          console.log(`   ${item.industry}: ${item.count} DROs`);
        });
      }
    }

    console.log('\nâœ… 200 DRO import completed successfully!');

  } catch (error) {
    console.error('âŒ Import process failed:', error.message);
    process.exit(1);
  }
}

// Run the import
console.log('ðŸš€ Starting SIREX DRO Batch Import...');
import200DROs()
  .then(() => {
    console.log('âœ… Process completed successfully');
    process.exit(0);
  })
  .catch((error) => {
    console.error('âŒ Process failed:', error);
    process.exit(1);
  });

