import { randomUUID } from 'crypto';
import { duckdb } from '../config/duckdb-connection';

export interface DRO {
  id?: string;
  msme_id: string;
  title: string;
  description?: string;
  industry: string;
  revenue_percentage: number;
  minimum_investment: number;
  target_amount: number;
  raised_amount?: number;
  duration_months: number;
  status?: 'pending' | 'active' | 'completed' | 'cancelled';
  created_at?: string;
  updated_at?: string;
  // Joined fields
  msme_name?: string;
  msme_email?: string;
}

export interface CreateDROData {
  msme_id: string;
  title: string;
  description?: string;
  industry: string;
  revenue_percentage: number;
  minimum_investment: number;
  target_amount: number;
  duration_months: number;
  annual_revenue?: number;
  roi_estimate?: number;
  risk_level?: 'low' | 'medium' | 'high';
  collateral_description?: string;
  use_of_funds?: string;
  repayment_terms?: string;
}

export interface DROFilters {
  industry?: string;
  min_investment?: number;
  max_investment?: number;
  min_return?: number;
  max_return?: number;
  status?: string;
  search?: string;
}

export class DROModel {
  // No need for constructor - using global duckdb connection

  async create(droData: CreateDROData): Promise<DRO> {
    try {
      const {
        msme_id,
        title,
        description,
        industry,
        revenue_percentage,
        minimum_investment,
        target_amount,
        duration_months,
        annual_revenue = target_amount * 2, // Default if not provided
        roi_estimate,
        risk_level,
        collateral_description,
        use_of_funds,
        repayment_terms
      } = droData;

      // Generate UUID for DRO
      const droId = randomUUID();

      const sql = `
        INSERT INTO dros (
          id, msme_id, title, description, industry, revenue_percentage,
          minimum_investment, target_amount, duration_months, annual_revenue,
          roi_estimate, risk_level, collateral_description, use_of_funds,
          repayment_terms, status
        ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, 'pending')
      `;

      await duckdb.query(sql, [
        droId, msme_id, title, description, industry, revenue_percentage,
        minimum_investment, target_amount, duration_months, annual_revenue,
        roi_estimate, risk_level, collateral_description, use_of_funds,
        repayment_terms
      ]);

      // Get the created DRO with MSME details (DuckDB doesn't return lastID, so we find by MSME and timestamp)
      const getDROSql = `
        SELECT d.*, u.first_name || ' ' || u.last_name as msme_name, u.email as msme_email
        FROM dros d
        JOIN users u ON d.msme_id = u.id
        WHERE d.msme_id = ? AND d.title = ?
        ORDER BY d.created_at DESC
        LIMIT 1
      `;

      const result = await duckdb.query(getDROSql, [msme_id, title]);
      if (result.rows && result.rows.length > 0) {
        return result.rows[0] as DRO;
      } else {
        throw new Error('DRO not found after creation');
      }
    } catch (error) {
      console.error('DRO create error:', error);
      throw error;
    }
  }

  async findById(id: number): Promise<DRO | null> {
    try {
      const sql = `
        SELECT d.*, u.first_name || ' ' || u.last_name as msme_name, u.email as msme_email
        FROM dros d
        JOIN users u ON d.msme_id = u.id
        WHERE d.id = ?
      `;

      const result = await duckdb.query(sql, [id]);
      return result.rows && result.rows.length > 0 ? result.rows[0] as DRO : null;
    } catch (error) {
      console.error('DRO findById error:', error);
      throw error;
    }
  }

