        WHERE d.id = ?
      `;

      const result = await duckdb.query(getDROSql, [droId]);
      if (result.rows && result.rows.length > 0) {
        return result.rows[0] as DRO;
      } else {
        throw new Error('DRO not found after creation');
      }
    } catch (error) {
      console.error('DRO create error:', error);
      throw error;
    }
  }

  async findById(id: number): Promise<DRO | null> {
    try {
      const sql = `
        SELECT d.*, u.first_name || ' ' || u.last_name as msme_name, u.email as msme_email
        FROM dros d
        JOIN users u ON d.msme_id = u.id
        WHERE d.id = ?
      `;

      const result = await duckdb.query(sql, [id]);
      return result.rows && result.rows.length > 0 ? result.rows[0] as DRO : null;
    } catch (error) {
      console.error('DRO findById error:', error);
      throw error;
    }
  }

  async findAll(filters: DROFilters = {}, limit: number = 50, offset: number = 0): Promise<DRO[]> {
    try {
      let sql = `
        SELECT d.*, u.first_name || ' ' || u.last_name as msme_name, u.email as msme_email,
               ROUND((d.raised_amount / d.target_amount) * 100, 2) as funding_percentage
        FROM dros d
        JOIN users u ON d.msme_id = u.id
        WHERE 1=1
      `;

      const params: any[] = [];

      // Apply filters
      if (filters.industry) {
        sql += ' AND LOWER(d.industry) LIKE LOWER(?)';
        params.push(`%${filters.industry}%`);
      }

      if (filters.min_investment) {
        sql += ' AND d.minimum_investment >= ?';
        params.push(filters.min_investment);
      }

      if (filters.max_investment) {
        sql += ' AND d.minimum_investment <= ?';
        params.push(filters.max_investment);
      }

      if (filters.min_return) {
        sql += ' AND d.revenue_percentage >= ?';
        params.push(filters.min_return);
      }

      if (filters.max_return) {
        sql += ' AND d.revenue_percentage <= ?';
        params.push(filters.max_return);
      }

      if (filters.status) {
        sql += ' AND d.status = ?';
        params.push(filters.status);
      }

      if (filters.search) {
        sql += ' AND (LOWER(d.title) LIKE LOWER(?) OR LOWER(d.description) LIKE LOWER(?))';
        params.push(`%${filters.search}%`, `%${filters.search}%`);
      }

      sql += ' ORDER BY d.created_at DESC LIMIT ? OFFSET ?';
      params.push(limit, offset);

      const result = await duckdb.query(sql, params);
      return result.rows as DRO[];
    } catch (error) {
      console.error('DRO findAll error:', error);
      throw error;
    }
  }

  async findByMSME(msmeId: string): Promise<DRO[]> {
    try {
      const sql = `
        SELECT d.*, u.first_name || ' ' || u.last_name as msme_name, u.email as msme_email,
               ROUND((d.raised_amount / d.target_amount) * 100, 2) as funding_percentage
        FROM dros d
        JOIN users u ON d.msme_id = u.id
        WHERE d.msme_id = ?
        ORDER BY d.created_at DESC
      `;

      const result = await duckdb.query(sql, [msmeId]);
      return result.rows as DRO[];
    } catch (error) {
      console.error('DRO findByMSME error:', error);
      throw error;
    }
  }

  async updateStatus(id: number, status: 'pending' | 'active' | 'completed' | 'cancelled'): Promise<void> {
    try {
      const sql = 'UPDATE dros SET status = ?, updated_at = CURRENT_TIMESTAMP WHERE id = ?';
      await duckdb.query(sql, [status, id]);
    } catch (error) {
      console.error('DRO updateStatus error:', error);
      throw error;
    }
  }

  async updateRaisedAmount(id: number, amount: number): Promise<void> {
    try {
      const sql = 'UPDATE dros SET raised_amount = ?, updated_at = CURRENT_TIMESTAMP WHERE id = ?';
      await duckdb.query(sql, [amount, id]);
    } catch (error) {
      console.error('DRO updateRaisedAmount error:', error);
      throw error;
    }
  }

  async incrementRaisedAmount(id: number, additionalAmount: number): Promise<DRO | null> {
    try {
      // First, get current raised amount
      const dro = await this.findById(id);
      if (!dro) {
        throw new Error('DRO not found');
      }

      const newRaisedAmount = (dro.raised_amount || 0) + additionalAmount;

      const sql = 'UPDATE dros SET raised_amount = ?, updated_at = CURRENT_TIMESTAMP WHERE id = ?';
      await duckdb.query(sql, [newRaisedAmount, id]);

      // Return updated DRO
      return await this.findById(id);
    } catch (error) {
      console.error('DRO incrementRaisedAmount error:', error);
      throw error;
    }
  }

  async getStatistics(): Promise<{
    total_dros: number;
    active_dros: number;
    total_funding: number;
    completed_dros: number;
  }> {
    try {
      const sql = `
        SELECT 
          COUNT(*) as total_dros,
          COUNT(CASE WHEN status = 'active' THEN 1 END) as active_dros,
          COUNT(CASE WHEN status = 'completed' THEN 1 END) as completed_dros,
          COALESCE(SUM(raised_amount), 0) as total_funding
        FROM dros
      `;

      const result = await duckdb.query(sql, []);
      if (result.rows && result.rows.length > 0) {
        const row = result.rows[0];
        return {
          total_dros: Number(row.total_dros),
          active_dros: Number(row.active_dros),
          total_funding: Number(row.total_funding),
          completed_dros: Number(row.completed_dros)
        };
      } else {
        return {
          total_dros: 0,
          active_dros: 0,
          total_funding: 0,
          completed_dros: 0
        };
      }
    } catch (error) {
      console.error('DRO getStatistics error:', error);
      throw error;
    }
  }

  async getIndustryBreakdown(): Promise<Array<{industry: string, count: number, total_funding: number}>> {
    try {
      const sql = `
        SELECT 
          industry,
          COUNT(*) as count,
          COALESCE(SUM(raised_amount), 0) as total_funding
        FROM dros
        WHERE status IN ('active', 'completed')
        GROUP BY industry
        ORDER BY total_funding DESC
      `;

      const result = await duckdb.query(sql, []);
      return result.rows ? result.rows.map(row => ({
        industry: row.industry,
        count: Number(row.count),
        total_funding: Number(row.total_funding)
      })) : [];
    } catch (error) {
      console.error('DRO getIndustryBreakdown error:', error);
      throw error;
    }
  }

  async delete(id: number): Promise<void> {
    try {
      // Check if DRO has any investments first
      const checkInvestmentsSql = 'SELECT COUNT(*) as count FROM investments WHERE dro_id = ?';

      const result = await duckdb.query(checkInvestmentsSql, [id]);
      const count = result.rows && result.rows.length > 0 ? result.rows[0].count : 0;

      if (count > 0) {
        throw new Error('Cannot delete DRO with existing investments');
      }

      // Safe to delete
      const deleteSql = 'DELETE FROM dros WHERE id = ?';
      await duckdb.query(deleteSql, [id]);
    } catch (error) {
      console.error('DRO delete error:', error);
      throw error;
    }
  }
}

