import { duckdb } from '../config/duckdb-connection';

export interface Investment {
  id?: number;
  investor_id: string;
  dro_id: number;
  amount: number;
  investment_date?: string;
  status?: 'active' | 'completed' | 'cancelled';
  // Joined fields
  investor_name?: string;
  investor_email?: string;
  dro_title?: string;
  dro_industry?: string;
  dro_revenue_percentage?: number;
}

export interface CreateInvestmentData {
  investor_id: string;
  dro_id: number;
  amount: number;
}

export class InvestmentModel {
  // No need for constructor - using global duckdb connection

  async create(investmentData: CreateInvestmentData): Promise<Investment> {
    const { investor_id, dro_id, amount } = investmentData;

    try {
      // Insert investment and get the created investment with details
      const sql = `
        INSERT INTO investments (investor_id, dro_id, amount, status)
        VALUES (?, ?, ?, 'active')
        RETURNING *
      `;

      const result = await duckdb.query(sql, [investor_id, dro_id, amount]);

      if (result.rows && result.rows.length > 0) {
        const investmentId = result.rows[0].id;

        // Get the created investment with details
        const getInvestmentSql = `
          SELECT
            i.*,
            u.first_name || ' ' || u.last_name as investor_name,
            u.email as investor_email,
            d.title as dro_title,
            d.industry as dro_industry,
            d.revenue_percentage as dro_revenue_percentage
          FROM investments i
          JOIN users u ON i.investor_id = u.id
          JOIN dros d ON i.dro_id = d.id
          WHERE i.id = ?
        `;

        const detailResult = await duckdb.query(getInvestmentSql, [investmentId]);

        if (detailResult.rows && detailResult.rows.length > 0) {
          return detailResult.rows[0] as Investment;
        } else {
          throw new Error('Investment not found after creation');
        }
      } else {
        throw new Error('Investment not found after creation');
      }
    } catch (error) {
      throw error;
    }
  }

  async findById(id: number): Promise<Investment | null> {
    try {
      const sql = `
        SELECT
          i.*,
          u.first_name || ' ' || u.last_name as investor_name,
          u.email as investor_email,
          d.title as dro_title,
          d.industry as dro_industry,
          d.revenue_percentage as dro_revenue_percentage
        FROM investments i
        JOIN users u ON i.investor_id = u.id
        JOIN dros d ON i.dro_id = d.id
        WHERE i.id = ?
      `;

      const result = await duckdb.query(sql, [id]);
      return result.rows && result.rows.length > 0 ? result.rows[0] as Investment : null;
    } catch (error) {
      throw error;
    }
  }

  async findByInvestor(investorId: number): Promise<Investment[]> {
    try {
      const sql = `
        SELECT
          i.*,
          u.first_name || ' ' || u.last_name as investor_name,
          u.email as investor_email,
          d.title as dro_title,
          d.industry as dro_industry,
          d.revenue_percentage as dro_revenue_percentage,
          d.status as dro_status
        FROM investments i
        JOIN users u ON i.investor_id = u.id
        JOIN dros d ON i.dro_id = d.id
        WHERE i.investor_id = ?
        ORDER BY i.investment_date DESC
      `;

      const result = await duckdb.query(sql, [investorId]);
      return result.rows as Investment[];
    } catch (error) {
      throw error;
    }
  }

  async findByDRO(droId: number): Promise<Investment[]> {
    try {
      const sql = `
        SELECT
          i.*,
          u.first_name || ' ' || u.last_name as investor_name,
          u.email as investor_email,
          d.title as dro_title,
          d.industry as dro_industry,
          d.revenue_percentage as dro_revenue_percentage
        FROM investments i
        JOIN users u ON i.investor_id = u.id
        JOIN dros d ON i.dro_id = d.id
        WHERE i.dro_id = ?
        ORDER BY i.investment_date DESC
      `;

      const result = await duckdb.query(sql, [droId]);
      return result.rows as Investment[];
    } catch (error) {
      throw error;
    }
  }

  async getInvestorPortfolio(investorId: number): Promise<{
    total_invested: number;
    active_investments: number;
    total_return_earned: number;
    investments: Investment[];
  }> {
    try {
      // Get portfolio summary
      const summarySql = `
        SELECT
          COUNT(*) as active_investments,
          COALESCE(SUM(amount), 0) as total_invested
        FROM investments
        WHERE investor_id = ? AND status = 'active'
      `;

      const summaryResult = await duckdb.query(summarySql, [investorId]);
      const summary = summaryResult.rows && summaryResult.rows.length > 0 ? summaryResult.rows[0] : {
        active_investments: 0,
        total_invested: 0
      };

      // Get total returns (this would be calculated from revenue_distributions)
      const returnsSql = `
        SELECT COALESCE(SUM(payout_amount), 0) as total_return_earned
        FROM investor_payouts ip
        JOIN investments i ON ip.investment_id = i.id
        WHERE i.investor_id = ? AND ip.status = 'completed'
      `;

      const returnsResult = await duckdb.query(returnsSql, [investorId]);
      const returns = returnsResult.rows && returnsResult.rows.length > 0 ? returnsResult.rows[0] : {
        total_return_earned: 0
      };

      // Get detailed investments
      const investments = await this.findByInvestor(investorId);

      return {
        total_invested: summary.total_invested,
        active_investments: summary.active_investments,
        total_return_earned: returns.total_return_earned || 0,
        investments
      };
    } catch (error) {
      throw error;
    }
  }

  async updateStatus(id: number, status: 'active' | 'completed' | 'cancelled'): Promise<void> {
    try {
      const sql = 'UPDATE investments SET status = ? WHERE id = ?';
      await duckdb.query(sql, [status, id]);
    } catch (error) {
      throw error;
    }
  }

  async getTotalInvestedInDRO(droId: number): Promise<number> {
    try {
      const sql = `
        SELECT COALESCE(SUM(amount), 0) as total_invested
        FROM investments
        WHERE dro_id = ? AND status = 'active'
      `;

      const result = await duckdb.query(sql, [droId]);
      const data = result.rows && result.rows.length > 0 ? result.rows[0] : { total_invested: 0 };
      return data.total_invested;
    } catch (error) {
      throw error;
    }
  }

  async getInvestmentStatistics(): Promise<{
    total_investments: number;
    total_amount_invested: number;
    active_investments: number;
    unique_investors: number;
  }> {
    try {
      const sql = `
        SELECT
          COUNT(*) as total_investments,
          COALESCE(SUM(amount), 0) as total_amount_invested,
          COUNT(CASE WHEN status = 'active' THEN 1 END) as active_investments,
          COUNT(DISTINCT investor_id) as unique_investors
        FROM investments
      `;

      const result = await duckdb.query(sql, []);
      const data = result.rows && result.rows.length > 0 ? result.rows[0] : {
        total_investments: 0,
        total_amount_invested: 0,
        active_investments: 0,
        unique_investors: 0
      };
      return data;
    } catch (error) {
      throw error;
    }
  }
}

