import express from 'express';
import { 
  RevenueDistributionModel, 
  InvestorPayoutModel, 
  CreateRevenueDistributionData 
} from '../models/RevenueDistribution';
import { DROModel } from '../models/DRO';
import { PayoutCalculationService } from '../services/PayoutCalculationService';
import { AutomatedPayoutService } from '../services/AutomatedPayoutService';
import { RevenueReportingService } from '../services/RevenueReportingService';
import { authenticateToken, requireUserType, requireKycApproval } from '../middleware/auth';

const router = express.Router();

// POST /api/revenue/distributions - Create revenue distribution (MSME only)
router.post('/distributions', authenticateToken, requireUserType('msme'), requireKycApproval, async (req, res) => {
  try {
    const { dro_id, distribution_date, total_revenue } = req.body;

    // Validation
    if (!dro_id || !distribution_date || !total_revenue) {
      return res.status(400).json({ 
        error: 'Missing required fields: dro_id, distribution_date, total_revenue',
        code: 'VALIDATION_ERROR'
      });
    }

    if (total_revenue <= 0) {
      return res.status(400).json({ 
        error: 'Total revenue must be positive',
        code: 'INVALID_REVENUE'
      });
    }

    // Verify DRO exists and user owns it
    const droModel = new DROModel();
    const dro = await droModel.findById(dro_id);

    if (!dro) {
      return res.status(404).json({ 
        error: 'DRO not found',
        code: 'DRO_NOT_FOUND'
      });
    }

    if (dro.msme_id !== req.user!.userId) {
      return res.status(403).json({ 
        error: 'You can only create revenue distributions for your own DROs',
        code: 'INSUFFICIENT_PERMISSIONS'
      });
    }

    if (dro.status !== 'active') {
      return res.status(400).json({ 
        error: 'Can only create revenue distributions for active DROs',
        code: 'DRO_NOT_ACTIVE'
      });
    }

    // Create revenue distribution
    const distributionData: CreateRevenueDistributionData = {
      dro_id: parseInt(dro_id),
      distribution_date,
      total_revenue: parseFloat(total_revenue)
    };

    const revenueDistributionModel = new RevenueDistributionModel();
    const distribution = await revenueDistributionModel.create(distributionData);

    // Process investor payouts
    const payoutService = new PayoutCalculationService();
    const payoutResult = await payoutService.processRevenueDistribution(dro_id, distribution.id!);

    res.status(201).json({
      message: 'Revenue distribution created successfully',
      distribution,
      payout_summary: payoutResult
    });

  } catch (error) {
    console.error('Create revenue distribution error:', error);
    res.status(500).json({ 
      error: 'Failed to create revenue distribution',
      code: 'CREATE_DISTRIBUTION_ERROR'
    });
  }
});

// GET /api/revenue/distributions/:id - Get revenue distribution details
router.get('/distributions/:id', authenticateToken, async (req, res) => {
  try {
    const id = req.params.id;

    if (!id || typeof id !== 'string') {
      return res.status(400).json({ 
        error: 'Invalid distribution ID',
        code: 'INVALID_ID'
      });
    }

    const revenueDistributionModel = new RevenueDistributionModel();
    const investorPayoutModel = new InvestorPayoutModel();

    const distribution = await revenueDistributionModel.findById(id);

    if (!distribution) {
      return res.status(404).json({ 
        error: 'Revenue distribution not found',
        code: 'DISTRIBUTION_NOT_FOUND'
      });
    }

    // Get investor payouts for this distribution
    const payouts = await investorPayoutModel.findByRevenueDistribution(id);

    res.json({
      distribution,
      payouts,
      summary: {
        total_payouts: payouts.length,
        total_amount: payouts.reduce((sum, payout) => sum + payout.payout_amount, 0),
        pending_payouts: payouts.filter(p => p.status === 'pending').length,
        completed_payouts: payouts.filter(p => p.status === 'completed').length
      }
    });

  } catch (error) {
    console.error('Get revenue distribution error:', error);
    res.status(500).json({ 
      error: 'Failed to fetch revenue distribution',
      code: 'FETCH_DISTRIBUTION_ERROR'
    });
  }
});

// GET /api/revenue/dro/:droId/distributions - Get all distributions for a DRO
router.get('/dro/:droId/distributions', authenticateToken, async (req, res) => {
  try {
    const droId = req.params.droId;
    const { page = '1', limit = '20' } = req.query;

    if (!droId || typeof droId !== 'string') {
      return res.status(400).json({ 
        error: 'Invalid DRO ID',
        code: 'INVALID_ID'
      });
    }

    const pageNum = parseInt(page as string);
    const limitNum = parseInt(limit as string);
    const offset = (pageNum - 1) * limitNum;

    const revenueDistributionModel = new RevenueDistributionModel();
    const distributions = await revenueDistributionModel.findByDRO(droId, limitNum, offset);
    const stats = await revenueDistributionModel.getTotalDistributionByDRO(droId);

    res.json({
      distributions,
      pagination: {
        page: pageNum,
        limit: limitNum,
        total: distributions.length
      },
      statistics: stats
    });

  } catch (error) {
    console.error('Get DRO distributions error:', error);
    res.status(500).json({ 
      error: 'Failed to fetch DRO distributions',
      code: 'FETCH_DRO_DISTRIBUTIONS_ERROR'
    });
  }
});

// GET /api/revenue/my-payouts - Get current user's payouts (Investor only)
router.get('/my-payouts', authenticateToken, requireUserType('investor'), async (req, res) => {
  try {
    const { page = '1', limit = '20' } = req.query;

    const pageNum = parseInt(page as string);
    const limitNum = parseInt(limit as string);
    const offset = (pageNum - 1) * limitNum;

    const investorPayoutModel = new InvestorPayoutModel();
    const payouts = await investorPayoutModel.findByInvestor(parseInt(req.user!.userId), limitNum, offset);
    const summary = await investorPayoutModel.getInvestorPayoutSummary(parseInt(req.user!.userId));

    res.json({
      payouts,
      pagination: {
        page: pageNum,
        limit: limitNum,
        total: payouts.length
      },
      summary
    });

  } catch (error) {
    console.error('Get my payouts error:', error);
    res.status(500).json({ 
      error: 'Failed to fetch your payouts',
      code: 'FETCH_MY_PAYOUTS_ERROR'
    });
  }
});

// GET /api/revenue/performance - Get investor performance analytics (Investor only)
router.get('/performance', authenticateToken, requireUserType('investor'), async (req, res) => {
  try {
    const payoutService = new PayoutCalculationService();
    const performance = await payoutService.getInvestorPerformance(parseInt(req.user!.userId));

    res.json({
      performance
    });

  } catch (error) {
    console.error('Get performance error:', error);
    res.status(500).json({ 
      error: 'Failed to fetch performance data',
      code: 'FETCH_PERFORMANCE_ERROR'
    });
  }
});

// POST /api/revenue/calculate-returns - Calculate potential returns for investment
router.post('/calculate-returns', authenticateToken, requireUserType('investor'), async (req, res) => {
  try {
    const { dro_id, investment_amount, projected_monthly_revenue, duration_months } = req.body;

    if (!dro_id || !investment_amount || !projected_monthly_revenue || !duration_months) {
      return res.status(400).json({ 
        error: 'Missing required fields: dro_id, investment_amount, projected_monthly_revenue, duration_months',
        code: 'VALIDATION_ERROR'
      });
    }

    if (investment_amount <= 0 || projected_monthly_revenue <= 0 || duration_months <= 0) {
      return res.status(400).json({ 
        error: 'All amounts and duration must be positive',
        code: 'INVALID_VALUES'
      });
    }

    // Verify DRO exists
    const droModel = new DROModel();
    const dro = await droModel.findById(dro_id);

    if (!dro) {
      return res.status(404).json({ 
        error: 'DRO not found',
        code: 'DRO_NOT_FOUND'
      });
    }

    const payoutService = new PayoutCalculationService();
    const returns = await payoutService.calculatePotentialReturns(
      dro_id, 
      parseFloat(investment_amount), 
      parseFloat(projected_monthly_revenue), 
      parseInt(duration_months)
    );

    res.json({
      dro: {
        id: dro.id,
        title: dro.title,
        industry: dro.industry
      },
      investment_amount: parseFloat(investment_amount),
      projected_returns: returns
    });

  } catch (error) {
    console.error('Calculate returns error:', error);
    res.status(500).json({ 
      error: 'Failed to calculate returns',
      code: 'CALCULATE_RETURNS_ERROR'
    });
  }
});

// GET /api/revenue/dro/:droId/payout-schedule - Simulate payout schedule
router.get('/dro/:droId/payout-schedule', authenticateToken, async (req, res) => {
  try {
    const droId = req.params.droId;
    const { monthly_revenue, months = '12' } = req.query;

    if (!droId || typeof droId !== 'string') {
      return res.status(400).json({ 
        error: 'Invalid DRO ID',
        code: 'INVALID_ID'
      });
    }

    if (!monthly_revenue || parseFloat(monthly_revenue as string) <= 0) {
      return res.status(400).json({ 
        error: 'Valid monthly_revenue is required',
        code: 'INVALID_MONTHLY_REVENUE'
      });
    }

    const monthsNum = parseInt(months as string);
    if (monthsNum <= 0 || monthsNum > 60) {
      return res.status(400).json({ 
        error: 'Months must be between 1 and 60',
        code: 'INVALID_MONTHS'
      });
    }

    // Verify DRO exists
    const droModel = new DROModel();
    const dro = await droModel.findById(droId);

    if (!dro) {
      return res.status(404).json({ 
        error: 'DRO not found',
        code: 'DRO_NOT_FOUND'
      });
    }

    const payoutService = new PayoutCalculationService();
    const schedule = await payoutService.simulatePayoutSchedule(
      droId, 
      parseFloat(monthly_revenue as string), 
      monthsNum
    );

    res.json({
      dro: {
        id: dro.id,
        title: dro.title,
        revenue_percentage: dro.revenue_percentage
      },
      simulation_parameters: {
        monthly_revenue: parseFloat(monthly_revenue as string),
        months: monthsNum
      },
      payout_schedule: schedule
    });

  } catch (error) {
    console.error('Get payout schedule error:', error);
    res.status(500).json({ 
      error: 'Failed to generate payout schedule',
      code: 'PAYOUT_SCHEDULE_ERROR'
    });
  }
});

// PUT /api/revenue/payouts/:id/status - Update payout status (Admin or automated system)
router.put('/payouts/:id/status', authenticateToken, async (req, res) => {
  try {
    const id = req.params.id;
    const { status } = req.body;

    if (!id || typeof id !== 'string') {
      return res.status(400).json({ 
        error: 'Invalid payout ID',
        code: 'INVALID_ID'
      });
    }

    if (!['pending', 'completed', 'failed'].includes(status)) {
      return res.status(400).json({ 
        error: 'Invalid status. Must be one of: pending, completed, failed',
        code: 'INVALID_STATUS'
      });
    }

    // For now, only allow MSME users to update their own payouts
    // In production, this would be restricted to admin users or payment processing system

    const investorPayoutModel = new InvestorPayoutModel();
    await investorPayoutModel.updateStatus(id, status);

    res.json({
      message: 'Payout status updated successfully',
      payout_id: id,
      new_status: status
    });

  } catch (error) {
    console.error('Update payout status error:', error);
    res.status(500).json({ 
      error: 'Failed to update payout status',
      code: 'UPDATE_PAYOUT_STATUS_ERROR'
    });
  }
});

// GET /api/revenue/statistics - Get platform revenue statistics
router.get('/statistics', async (req, res) => {
  try {
    const { year, month } = req.query;

    const revenueDistributionModel = new RevenueDistributionModel();

    if (year && month) {
      const stats = await revenueDistributionModel.getMonthlyDistributionStats(
        parseInt(year as string), 
        parseInt(month as string)
      );

      res.json({
        period: { year: parseInt(year as string), month: parseInt(month as string) },
        statistics: stats
      });
    } else {
      // Return current month stats by default
      const now = new Date();
      const stats = await revenueDistributionModel.getMonthlyDistributionStats(
        now.getFullYear(), 
        now.getMonth() + 1
      );

      res.json({
        period: { year: now.getFullYear(), month: now.getMonth() + 1 },
        statistics: stats
      });
    }

  } catch (error) {
    console.error('Get revenue statistics error:', error);
    res.status(500).json({ 
      error: 'Failed to fetch revenue statistics',
      code: 'FETCH_REVENUE_STATS_ERROR'
    });
  }
});

// POST /api/revenue/process-payouts - Manually trigger payout processing (Admin only)
router.post('/process-payouts', authenticateToken, async (req, res) => {
  try {
    // In production, this would be restricted to admin users only
    // For now, we'll allow any authenticated user for testing

    const { dro_id } = req.body;

    const payoutService = new AutomatedPayoutService();

    let result;
    if (dro_id) {
      // Process payouts for specific DRO
      result = await payoutService.processPayoutsForDRO(parseInt(dro_id));
    } else {
      // Process all pending payouts
      result = await payoutService.processPendingPayouts();
    }

    res.json({
      message: 'Payout processing completed',
      result
    });

  } catch (error) {
    console.error('Manual payout processing error:', error);
    res.status(500).json({ 
      error: 'Failed to process payouts',
      code: 'PROCESS_PAYOUTS_ERROR'
    });
  }
});

// GET /api/revenue/payout-status - Get payout processing status
router.get('/payout-status', authenticateToken, async (req, res) => {
  try {
    const payoutService = new AutomatedPayoutService();
    const status = await payoutService.getProcessingStatus();

    res.json({
      status
    });

  } catch (error) {
    console.error('Get payout status error:', error);
    res.status(500).json({ 
      error: 'Failed to fetch payout status',
      code: 'FETCH_PAYOUT_STATUS_ERROR'
    });
  }
});

// GET /api/revenue/reports/platform - Generate platform revenue report
router.get('/reports/platform', authenticateToken, async (req, res) => {
  try {
    const { start_date, end_date, format = 'json' } = req.query;

    if (!start_date || !end_date) {
      return res.status(400).json({ 
        error: 'start_date and end_date are required',
        code: 'VALIDATION_ERROR'
      });
    }

    // In production, this would be restricted to admin users
    const reportingService = new RevenueReportingService();
    const report = await reportingService.generatePlatformReport(
      start_date as string, 
      end_date as string
    );

    if (format === 'csv') {
      const csv = reportingService.exportToCSV(report, 'platform');
      res.setHeader('Content-Type', 'text/csv');
      res.setHeader('Content-Disposition', `attachment; filename=platform-report-${start_date}-${end_date}.csv`);
      res.send(csv);
    } else {
      res.json({
        report
      });
    }

  } catch (error) {
    console.error('Generate platform report error:', error);
    res.status(500).json({ 
      error: 'Failed to generate platform report',
      code: 'GENERATE_PLATFORM_REPORT_ERROR'
    });
  }
});

// GET /api/revenue/reports/investor - Generate investor report (Investor only)
router.get('/reports/investor', authenticateToken, requireUserType('investor'), async (req, res) => {
  try {
    const { start_date, end_date, format = 'json' } = req.query;

    if (!start_date || !end_date) {
      return res.status(400).json({ 
        error: 'start_date and end_date are required',
