      res.setHeader('Content-Type', 'text/csv');
      res.setHeader('Content-Disposition', `attachment; filename=investor-report-${req.user!.userId}-${start_date}-${end_date}.csv`);
      res.send(csv);
    } else {
      res.json({
        report
      });
    }

  } catch (error) {
    console.error('Generate investor report error:', error);
    res.status(500).json({ 
      error: 'Failed to generate investor report',
      code: 'GENERATE_INVESTOR_REPORT_ERROR'
    });
  }
});

// GET /api/revenue/reports/msme - Generate MSME report (MSME only)
router.get('/reports/msme', authenticateToken, requireUserType('msme'), async (req, res) => {
  try {
    const { start_date, end_date, format = 'json' } = req.query;

    if (!start_date || !end_date) {
      return res.status(400).json({ 
        error: 'start_date and end_date are required',
        code: 'VALIDATION_ERROR'
      });
    }

    const reportingService = new RevenueReportingService();
    const report = await reportingService.generateMSMEReport(
      parseInt(req.user!.userId),
      start_date as string, 
      end_date as string
    );

    if (format === 'csv') {
      const csv = reportingService.exportToCSV(report, 'msme');
      res.setHeader('Content-Type', 'text/csv');
      res.setHeader('Content-Disposition', `attachment; filename=msme-report-${req.user!.userId}-${start_date}-${end_date}.csv`);
      res.send(csv);
    } else {
