    if (!dro) {
      return res.status(404).json({ 
        error: 'DRO not found',
        code: 'DRO_NOT_FOUND'
      });
    }

    const payoutService = new PayoutCalculationService();
    const returns = await payoutService.calculatePotentialReturns(
      dro_id, 
      parseFloat(investment_amount), 
      parseFloat(projected_monthly_revenue), 
      parseInt(duration_months)
    );

    res.json({
      dro: {
        id: dro.id,
        title: dro.title,
        industry: dro.industry
      },
      investment_amount: parseFloat(investment_amount),
      projected_returns: returns
    });

