import { randomUUID } from 'crypto';
import { duckdb } from '../config/duckdb-connection';

export interface RevenueDistribution {
  id?: string;
  dro_id: string;
  distribution_date: string;
  total_revenue: number;
  distribution_amount: number;
  created_at?: string;
  // Joined fields
  dro_title?: string;
  dro_revenue_percentage?: number;
  msme_name?: string;
}

export interface CreateRevenueDistributionData {
  dro_id: string;
  distribution_date: string;
  total_revenue: number;
}

export interface InvestorPayout {
  id?: string;
  investment_id: string;
  revenue_distribution_id: string;
  payout_amount: number;
  payout_date?: string;
  status?: 'pending' | 'completed' | 'failed';
  // Joined fields
  investor_name?: string;
  investor_email?: string;
  investment_amount?: number;
  dro_title?: string;
}

export interface CreateInvestorPayoutData {
  investment_id: number;
  revenue_distribution_id: number;
  payout_amount: number;
}

export class RevenueDistributionModel {
  // No need for constructor - using global duckdb connection

  async create(distributionData: CreateRevenueDistributionData): Promise<RevenueDistribution> {
    const { dro_id, distribution_date, total_revenue } = distributionData;

    try {
      // First, get the DRO to calculate distribution amount
      const getDROSql = 'SELECT revenue_percentage FROM dros WHERE id = ?';
      const droResult = await duckdb.query(getDROSql, [dro_id]);

      if (!droResult.rows || droResult.rows.length === 0) {
        throw new Error('DRO not found');
      }

      const dro = droResult.rows[0];
      const distribution_amount = total_revenue * (dro.revenue_percentage / 100);

      const sql = `
        INSERT INTO revenue_distributions (dro_id, distribution_date, total_revenue, distribution_amount)
        VALUES (?, ?, ?, ?)
        RETURNING *
      `;

      const result = await duckdb.query(sql, [dro_id, distribution_date, total_revenue, distribution_amount]);

      if (result.rows && result.rows.length > 0) {
        const distributionId = result.rows[0].id;

        // Get the created distribution with DRO details
        const getDistributionSql = `
          SELECT
            rd.*,
            d.title as dro_title,
            d.revenue_percentage as dro_revenue_percentage,
            u.first_name || ' ' || u.last_name as msme_name
          FROM revenue_distributions rd
          JOIN dros d ON rd.dro_id = d.id
          JOIN users u ON d.msme_id = u.id
          WHERE rd.id = ?
        `;

        const detailResult = await duckdb.query(getDistributionSql, [distributionId]);

        if (detailResult.rows && detailResult.rows.length > 0) {
          return detailResult.rows[0] as RevenueDistribution;
        } else {
          throw new Error('Revenue distribution not found after creation');
        }
      } else {
        throw new Error('Revenue distribution not found after creation');
      }
    } catch (error) {
      throw error;
    }
  }

  async findById(id: number): Promise<RevenueDistribution | null> {
    try {
      const sql = `
        SELECT
          rd.*,
          d.title as dro_title,
          d.revenue_percentage as dro_revenue_percentage,
          u.first_name || ' ' || u.last_name as msme_name
        FROM revenue_distributions rd
        JOIN dros d ON rd.dro_id = d.id
        JOIN users u ON d.msme_id = u.id
        WHERE rd.id = ?
      `;

      const result = await duckdb.query(sql, [id]);
      return result.rows && result.rows.length > 0 ? result.rows[0] as RevenueDistribution : null;
    } catch (error) {
      throw error;
    }
  }

  async findByDRO(droId: string, limit: number = 50, offset: number = 0): Promise<RevenueDistribution[]> {
    try {
      const sql = `
        SELECT
          rd.*,
          d.title as dro_title,
          d.revenue_percentage as dro_revenue_percentage,
          u.first_name || ' ' || u.last_name as msme_name
        FROM revenue_distributions rd
        JOIN dros d ON rd.dro_id = d.id
        JOIN users u ON d.msme_id = u.id
        WHERE rd.dro_id = ?
        ORDER BY rd.distribution_date DESC
        LIMIT ? OFFSET ?
      `;

      const result = await duckdb.query(sql, [droId, limit, offset]);
      return result.rows as RevenueDistribution[];
    } catch (error) {
      throw error;
    }
  }

  async findByDateRange(startDate: string, endDate: string): Promise<RevenueDistribution[]> {
    try {
      const sql = `
        SELECT
          rd.*,
          d.title as dro_title,
          d.revenue_percentage as dro_revenue_percentage,
          u.first_name || ' ' || u.last_name as msme_name
        FROM revenue_distributions rd
        JOIN dros d ON rd.dro_id = d.id
        JOIN users u ON d.msme_id = u.id
        WHERE rd.distribution_date BETWEEN ? AND ?
        ORDER BY rd.distribution_date DESC
      `;

      const result = await duckdb.query(sql, [startDate, endDate]);
      return result.rows as RevenueDistribution[];
    } catch (error) {
      throw error;
    }
  }

  async getTotalDistributionByDRO(droId: string): Promise<{
    total_revenue: number;
    total_distribution: number;
    distribution_count: number;
  }> {
    try {
      const sql = `
        SELECT
          COALESCE(SUM(total_revenue), 0) as total_revenue,
          COALESCE(SUM(distribution_amount), 0) as total_distribution,
          COUNT(*) as distribution_count
        FROM revenue_distributions
        WHERE dro_id = ?
      `;

      const result = await duckdb.query(sql, [droId]);
      const data = result.rows && result.rows.length > 0 ? result.rows[0] : {
        total_revenue: 0,
        total_distribution: 0,
        distribution_count: 0
      };
      return data;
    } catch (error) {
      throw error;
    }
  }

  async getMonthlyDistributionStats(year: number, month: number): Promise<{
    total_distributions: number;
    total_amount: number;
    unique_dros: number;
  }> {
    try {
      const sql = `
        SELECT
          COUNT(*) as total_distributions,
          COALESCE(SUM(distribution_amount), 0) as total_amount,
          COUNT(DISTINCT dro_id) as unique_dros
        FROM revenue_distributions
        WHERE strftime('%Y', distribution_date) = ?
        AND strftime('%m', distribution_date) = ?
      `;

      const result = await duckdb.query(sql, [year.toString(), month.toString().padStart(2, '0')]);
      const data = result.rows && result.rows.length > 0 ? result.rows[0] : {
        total_distributions: 0,
        total_amount: 0,
        unique_dros: 0
      };
      return data;
    } catch (error) {
      throw error;
    }
  }
}

export class InvestorPayoutModel {
  // No need for constructor - using global duckdb connection

  async create(payoutData: CreateInvestorPayoutData): Promise<InvestorPayout> {
    const { investment_id, revenue_distribution_id, payout_amount } = payoutData;

    try {
      const sql = `
        INSERT INTO investor_payouts (investment_id, revenue_distribution_id, payout_amount, status)
        VALUES (?, ?, ?, 'pending')
        RETURNING *
      `;

      const result = await duckdb.query(sql, [investment_id, revenue_distribution_id, payout_amount]);

      if (result.rows && result.rows.length > 0) {
        const payoutId = result.rows[0].id;

        // Get the created payout with details
        const getPayoutSql = `
          SELECT
            ip.*,
            u.first_name || ' ' || u.last_name as investor_name,
            u.email as investor_email,
            i.amount as investment_amount,
            d.title as dro_title
          FROM investor_payouts ip
          JOIN investments i ON ip.investment_id = i.id
          JOIN users u ON i.investor_id = u.id
          JOIN dros d ON i.dro_id = d.id
          WHERE ip.id = ?
        `;

        const detailResult = await duckdb.query(getPayoutSql, [payoutId]);

        if (detailResult.rows && detailResult.rows.length > 0) {
          return detailResult.rows[0] as InvestorPayout;
        } else {
          throw new Error('Investor payout not found after creation');
        }
      } else {
        throw new Error('Investor payout not found after creation');
      }
    } catch (error) {
      throw error;
    }
  }

  async findByInvestor(investorId: string, limit: number = 50, offset: number = 0): Promise<InvestorPayout[]> {
    try {
      const sql = `
        SELECT
          ip.*,
          u.first_name || ' ' || u.last_name as investor_name,
          u.email as investor_email,
          i.amount as investment_amount,
          d.title as dro_title,
          rd.distribution_date
        FROM investor_payouts ip
        JOIN investments i ON ip.investment_id = i.id
        JOIN users u ON i.investor_id = u.id
        JOIN dros d ON i.dro_id = d.id
        JOIN revenue_distributions rd ON ip.revenue_distribution_id = rd.id
        WHERE i.investor_id = ?
        ORDER BY ip.payout_date DESC
        LIMIT ? OFFSET ?
      `;

      const result = await duckdb.query(sql, [investorId, limit, offset]);
      return result.rows as InvestorPayout[];
    } catch (error) {
      throw error;
    }
  }

  async findByRevenueDistribution(revenueDistributionId: number): Promise<InvestorPayout[]> {
    try {
      const sql = `
        SELECT
          ip.*,
          u.first_name || ' ' || u.last_name as investor_name,
          u.email as investor_email,
          i.amount as investment_amount,
          d.title as dro_title
        FROM investor_payouts ip
        JOIN investments i ON ip.investment_id = i.id
        JOIN users u ON i.investor_id = u.id
        JOIN dros d ON i.dro_id = d.id
        WHERE ip.revenue_distribution_id = ?
        ORDER BY ip.payout_amount DESC
      `;

      const result = await duckdb.query(sql, [revenueDistributionId]);
      return result.rows as InvestorPayout[];
    } catch (error) {
      throw error;
    }
  }

  async updateStatus(id: number, status: 'pending' | 'completed' | 'failed'): Promise<void> {
    try {
      const sql = 'UPDATE investor_payouts SET status = ?, payout_date = CURRENT_TIMESTAMP WHERE id = ?';
      await duckdb.query(sql, [status, id]);
    } catch (error) {
      throw error;
    }
  }

  async batchUpdateStatus(ids: number[], status: 'pending' | 'completed' | 'failed'): Promise<void> {
    try {
      const placeholders = ids.map(() => '?').join(',');
      const sql = `UPDATE investor_payouts SET status = ?, payout_date = CURRENT_TIMESTAMP WHERE id IN (${placeholders})`;
      await duckdb.query(sql, [status, ...ids]);
    } catch (error) {
      throw error;
    }
  }

  async getInvestorPayoutSummary(investorId: string): Promise<{
    total_payouts: number;
    total_amount: number;
    pending_amount: number;
    completed_amount: number;
  }> {
    try {
      const sql = `
        SELECT
          COUNT(*) as total_payouts,
          COALESCE(SUM(payout_amount), 0) as total_amount,
          COALESCE(SUM(CASE WHEN status = 'pending' THEN payout_amount ELSE 0 END), 0) as pending_amount,
          COALESCE(SUM(CASE WHEN status = 'completed' THEN payout_amount ELSE 0 END), 0) as completed_amount
        FROM investor_payouts ip
        JOIN investments i ON ip.investment_id = i.id
        WHERE i.investor_id = ?
      `;

      const result = await duckdb.query(sql, [investorId]);
      const data = result.rows && result.rows.length > 0 ? result.rows[0] : {
        total_payouts: 0,
        total_amount: 0,
        pending_amount: 0,
        completed_amount: 0
      };
      return data;
    } catch (error) {
      throw error;
    }
  }
}

