import { InvestmentModel } from '../models/Investment';
import { RevenueDistributionModel, InvestorPayoutModel, CreateInvestorPayoutData } from '../models/RevenueDistribution';

export interface PayoutCalculation {
  investment_id: string;
  investor_id: number;
  investor_name: string;
  investment_amount: number;
  ownership_percentage: number;
  payout_amount: number;
}

export interface RevenueDistributionResult {
  revenue_distribution_id: string;
  total_distribution_amount: number;
  total_investment_amount: number;
  investor_count: number;
  payouts: PayoutCalculation[];
}

export class PayoutCalculationService {
  private investmentModel: InvestmentModel;
  private revenueDistributionModel: RevenueDistributionModel;
  private investorPayoutModel: InvestorPayoutModel;

  constructor() {
    this.investmentModel = new InvestmentModel();
    this.revenueDistributionModel = new RevenueDistributionModel();
    this.investorPayoutModel = new InvestorPayoutModel();
  }

  /**
   * Calculate payouts for all investors in a DRO based on revenue distribution
   */
  async calculatePayouts(droId: string, revenueDistributionId: string): Promise<PayoutCalculation[]> {
    try {
      // Get all active investments for this DRO
      const investments = await this.investmentModel.findByDRO(droId);
      const activeInvestments = investments.filter(inv => inv.status === 'active');

      if (activeInvestments.length === 0) {
        return [];
      }

      // Get the revenue distribution details
      const revenueDistribution = await this.revenueDistributionModel.findById(revenueDistributionId);
      if (!revenueDistribution) {
        throw new Error('Revenue distribution not found');
      }

      // Calculate total investment amount for this DRO
      const totalInvestmentAmount = activeInvestments.reduce((sum, inv) => sum + inv.amount, 0);

      if (totalInvestmentAmount === 0) {
        return [];
      }

      // Calculate each investor's payout based on their ownership percentage
      const payouts: PayoutCalculation[] = activeInvestments.map(investment => {
        const ownershipPercentage = (investment.amount / totalInvestmentAmount) * 100;
        const payoutAmount = (investment.amount / totalInvestmentAmount) * revenueDistribution.distribution_amount;

        return {
          investment_id: investment.id!,
          investor_id: parseInt(investment.investor_id),
          investor_name: investment.investor_name || 'Unknown',
          investment_amount: investment.amount,
          ownership_percentage: ownershipPercentage,
          payout_amount: Math.round(payoutAmount * 100) / 100 // Round to 2 decimal places
        };
      });

      return payouts;

    } catch (error) {
      console.error('Error calculating payouts:', error);
      throw error;
    }
  }

  /**
   * Process revenue distribution and create investor payouts
   */
  async processRevenueDistribution(droId: string, revenueDistributionId: string): Promise<RevenueDistributionResult> {
    try {
      // Calculate payouts
      const payouts = await this.calculatePayouts(droId, revenueDistributionId);

      if (payouts.length === 0) {
        throw new Error('No active investments found for this DRO');
      }

      // Create investor payout records
      const createdPayouts: PayoutCalculation[] = [];

      for (const payout of payouts) {
        const payoutData: CreateInvestorPayoutData = {
          investment_id: payout.investment_id,
          revenue_distribution_id: revenueDistributionId,
          payout_amount: payout.payout_amount
        };

        await this.investorPayoutModel.create(payoutData);
        createdPayouts.push(payout);
      }

      // Calculate summary
      const totalDistributionAmount = payouts.reduce((sum, payout) => sum + payout.payout_amount, 0);
      const totalInvestmentAmount = payouts.reduce((sum, payout) => sum + payout.investment_amount, 0);

      return {
        revenue_distribution_id: revenueDistributionId,
        total_distribution_amount: totalDistributionAmount,
        total_investment_amount: totalInvestmentAmount,
        investor_count: payouts.length,
        payouts: createdPayouts
      };

    } catch (error) {
      console.error('Error processing revenue distribution:', error);
      throw error;
    }
  }

  /**
   * Calculate potential returns for an investment amount
   */
  async calculatePotentialReturns(droId: number, investmentAmount: number, projectedMonthlyRevenue: number, durationMonths: number): Promise<{
    total_potential_return: number;
    monthly_average_return: number;
    annual_return_rate: number;
    ownership_percentage: number;
  }> {
    try {
      // Get current total investment in the DRO
      const currentTotalInvested = await this.investmentModel.getTotalInvestedInDRO(droId);
      const projectedTotalInvestment = currentTotalInvested + investmentAmount;

      // Calculate ownership percentage
      const ownershipPercentage = (investmentAmount / projectedTotalInvestment) * 100;

      // Get DRO details for revenue percentage
      const revenueDistribution = await this.revenueDistributionModel.findByDRO(droId, 1);
      if (revenueDistribution.length === 0) {
        throw new Error('No revenue distribution data found for this DRO');
      }

      // Use revenue percentage from DRO (this should come from DRO model, but we'll estimate)
      const revenuePercentage = 10; // Default 10% - this should come from DRO data

      // Calculate projected returns
      const monthlyRevenueShare = projectedMonthlyRevenue * (revenuePercentage / 100);
      const monthlyInvestorShare = monthlyRevenueShare * (ownershipPercentage / 100);
      const totalPotentialReturn = monthlyInvestorShare * durationMonths;
      const annualReturnRate = ((totalPotentialReturn / investmentAmount) / (durationMonths / 12)) * 100;

      return {
        total_potential_return: Math.round(totalPotentialReturn * 100) / 100,
        monthly_average_return: Math.round(monthlyInvestorShare * 100) / 100,
        annual_return_rate: Math.round(annualReturnRate * 100) / 100,
        ownership_percentage: Math.round(ownershipPercentage * 100) / 100
      };

    } catch (error) {
      console.error('Error calculating potential returns:', error);
      throw error;
    }
  }

  /**
   * Get investor performance analytics
   */
  async getInvestorPerformance(investorId: string): Promise<{
    total_invested: number;
    total_returns: number;
    net_profit: number;
    roi_percentage: number;
    active_investments: number;
    completed_payouts: number;
    pending_payouts: number;
    average_monthly_return: number;
  }> {
    try {
      // Get investor portfolio
      const portfolio = await this.investmentModel.getInvestorPortfolio(investorId);

      // Get payout summary
      const payoutSummary = await this.investorPayoutModel.getInvestorPayoutSummary(investorId);

      // Calculate performance metrics
      const netProfit = payoutSummary.completed_amount - portfolio.total_invested;
      const roiPercentage = portfolio.total_invested > 0 ? (netProfit / portfolio.total_invested) * 100 : 0;

      // Estimate average monthly return (this is simplified)
      const averageMonthlyReturn = payoutSummary.total_payouts > 0 ? 
        payoutSummary.completed_amount / Math.max(payoutSummary.total_payouts, 1) : 0;

      return {
        total_invested: portfolio.total_invested,
        total_returns: payoutSummary.completed_amount,
        net_profit: Math.round(netProfit * 100) / 100,
        roi_percentage: Math.round(roiPercentage * 100) / 100,
        active_investments: portfolio.active_investments,
        completed_payouts: payoutSummary.total_payouts,
        pending_payouts: Math.round(payoutSummary.pending_amount * 100) / 100,
        average_monthly_return: Math.round(averageMonthlyReturn * 100) / 100
      };

    } catch (error) {
      console.error('Error getting investor performance:', error);
      throw error;
    }
  }

  /**
   * Validate revenue distribution data
   */
  validateRevenueDistribution(totalRevenue: number, revenuePercentage: number): {
    isValid: boolean;
    errors: string[];
    distributionAmount: number;
  } {
    const errors: string[] = [];

    if (totalRevenue <= 0) {
      errors.push('Total revenue must be positive');
    }

    if (revenuePercentage <= 0 || revenuePercentage > 100) {
      errors.push('Revenue percentage must be between 0 and 100');
    }

    const distributionAmount = totalRevenue * (revenuePercentage / 100);

    return {
      isValid: errors.length === 0,
      errors,
      distributionAmount: Math.round(distributionAmount * 100) / 100
    };
  }

  /**
   * Get payout schedule simulation
   */
  async simulatePayoutSchedule(droId: string, monthlyRevenue: number, months: number): Promise<Array<{
    month: number;
    projected_revenue: number;
    distribution_amount: number;
    investor_payouts: { investor_id: number; payout_amount: number; }[];
  }>> {
    try {
      const investments = await this.investmentModel.findByDRO(droId);
      const activeInvestments = investments.filter(inv => inv.status === 'active');

      if (activeInvestments.length === 0) {
        return [];
      }

      const totalInvestment = activeInvestments.reduce((sum, inv) => sum + inv.amount, 0);
      const revenuePercentage = 10; // This should come from DRO data

      const schedule = [];

      for (let month = 1; month <= months; month++) {
        const distributionAmount = monthlyRevenue * (revenuePercentage / 100);
        const investorPayouts = activeInvestments.map(investment => ({
          investor_id: investment.investor_id,
          payout_amount: Math.round(((investment.amount / totalInvestment) * distributionAmount) * 100) / 100
        }));

        schedule.push({
          month,
          projected_revenue: monthlyRevenue,
          distribution_amount: Math.round(distributionAmount * 100) / 100,
          investor_payouts: investorPayouts
        });
      }

      return schedule;

    } catch (error) {
      console.error('Error simulating payout schedule:', error);
      throw error;
    }
  }
}

