      const payouts = await this.investorPayoutModel.findByInvestor(investorId);
      const periodPayouts = payouts.filter(p => 
        p.payout_date && p.payout_date >= startDate && p.payout_date <= endDate
      );

      // Calculate portfolio summary
      const portfolioSummary = {
        total_invested: portfolio.total_invested,
        total_returns: portfolio.total_return_earned,
        net_profit: portfolio.total_return_earned - portfolio.total_invested,
        roi_percentage: portfolio.total_invested > 0 ? 
          ((portfolio.total_return_earned - portfolio.total_invested) / portfolio.total_invested) * 100 : 0,
        active_investments: portfolio.active_investments
      };

      // Get investment breakdown
      const investmentBreakdown = await this.getInvestmentBreakdown(investorId);

      // Get monthly performance
      const monthlyPerformance = await this.getMonthlyPerformance(investorId, startDate, endDate);

      return {
        investor_id: investorId,
        report_period: {
          start_date: startDate,
          end_date: endDate
        },
        portfolio_summary: portfolioSummary,
        investment_breakdown: investmentBreakdown,
        monthly_performance: monthlyPerformance
      };

