import { InvestorPayoutModel, RevenueDistributionModel } from '../models/RevenueDistribution';
import { UserModel } from '../models/User';

export interface PayoutProcessingResult {
  total_processed: number;
  successful_payouts: number;
  failed_payouts: number;
  total_amount: number;
  processing_errors: Array<{
    payout_id: number;
    investor_email: string;
    error: string;
  }>;
}

export interface PaymentGatewayResponse {
  success: boolean;
  transaction_id?: string;
  error_message?: string;
}

export class AutomatedPayoutService {
  private investorPayoutModel: InvestorPayoutModel;
  private userModel: UserModel;

  constructor() {
    this.investorPayoutModel = new InvestorPayoutModel();
    this.userModel = new UserModel();
  }

  /**
   * Process all pending payouts
   */
  async processPendingPayouts(): Promise<PayoutProcessingResult> {
    console.log('ðŸ”„ Starting automated payout processing...');

    const result: PayoutProcessingResult = {
      total_processed: 0,
      successful_payouts: 0,
      failed_payouts: 0,
      total_amount: 0,
      processing_errors: []
    };

    try {
      // Get all pending payouts (simplified query - in production you'd want pagination)
      const pendingPayouts = await this.getPendingPayouts();

      if (pendingPayouts.length === 0) {
        console.log('âœ… No pending payouts to process');
        return result;
      }

      console.log(`ðŸ“‹ Found ${pendingPayouts.length} pending payouts to process`);
      result.total_processed = pendingPayouts.length;

      // Process each payout
      for (const payout of pendingPayouts) {
        try {
          const processResult = await this.processSinglePayout(payout);

          if (processResult.success) {
            result.successful_payouts++;
            result.total_amount += payout.payout_amount;

            // Update payout status to completed
            await this.investorPayoutModel.updateStatus(payout.id!, 'completed');

            console.log(`âœ… Payout ${payout.id} processed successfully: $${payout.payout_amount} to ${payout.investor_email}`);
          } else {
            result.failed_payouts++;

            // Update payout status to failed
            await this.investorPayoutModel.updateStatus(payout.id!, 'failed');

            result.processing_errors.push({
              payout_id: payout.id!,
              investor_email: payout.investor_email || 'Unknown',
              error: processResult.error_message || 'Unknown error'
            });

            console.error(`âŒ Payout ${payout.id} failed: ${processResult.error_message}`);
          }

          // Add delay between payments to avoid rate limiting
          await this.delay(1000);

        } catch (error) {
          result.failed_payouts++;
          const errorMessage = error instanceof Error ? error.message : 'Unknown error';

          result.processing_errors.push({
            payout_id: payout.id!,
            investor_email: payout.investor_email || 'Unknown',
            error: errorMessage
          });

          console.error(`âŒ Error processing payout ${payout.id}:`, error);
        }
      }

      console.log(`âœ… Payout processing complete: ${result.successful_payouts} successful, ${result.failed_payouts} failed`);

      // Send summary notification (in production, this would send emails/SMS)
      await this.sendProcessingSummary(result);

      return result;

    } catch (error) {
      console.error('âŒ Fatal error in payout processing:', error);
      throw error;
    }
  }

  /**
   * Process a single payout
   */
  private async processSinglePayout(payout: any): Promise<PaymentGatewayResponse> {
    try {
      // Validate payout data
      if (!payout.investor_email || !payout.payout_amount || payout.payout_amount <= 0) {
        return {
          success: false,
          error_message: 'Invalid payout data'
        };
      }

      // Get investor details
      const investor = await this.userModel.findByEmail(payout.investor_email);
      if (!investor) {
        return {
          success: false,
          error_message: 'Investor not found'
        };
      }

      // Verify investor KYC status
      if (investor.kyc_status !== 'approved') {
        return {
          success: false,
          error_message: 'Investor KYC not approved'
        };
      }

      // Simulate payment gateway processing
      const paymentResult = await this.processPaymentGateway(payout);

      return paymentResult;

    } catch (error) {
      console.error('Error processing single payout:', error);
      return {
        success: false,
        error_message: error instanceof Error ? error.message : 'Processing error'
      };
    }
  }

  /**
   * Simulate payment gateway processing
   * In production, this would integrate with Stripe, PayPal, etc.
   */
  private async processPaymentGateway(payout: any): Promise<PaymentGatewayResponse> {
    try {
      // Simulate payment processing delay
      await this.delay(500);

      // Simulate 95% success rate
      const isSuccess = Math.random() > 0.05;

      if (isSuccess) {
        return {
          success: true,
          transaction_id: `tx_${Date.now()}_${payout.id}`
        };
      } else {
        // Simulate various failure reasons
        const errors = [
          'Insufficient funds in platform account',
          'Invalid bank account details',
          'Payment gateway timeout',
          'Currency conversion error',
          'Regulatory compliance check failed'
        ];

        return {
          success: false,
          error_message: errors[Math.floor(Math.random() * errors.length)]
        };
      }

    } catch (error) {
      return {
        success: false,
        error_message: 'Payment gateway error'
      };
    }
  }

  /**
   * Get pending payouts with investor details
   */
  private async getPendingPayouts(): Promise<any[]> {
    try {
      // This is a simplified implementation
      // In production, you'd want to batch process and handle large volumes
      const sql = `
        SELECT 
          ip.*,
          u.email as investor_email,
          u.first_name || ' ' || u.last_name as investor_name,
          d.title as dro_title
        FROM investor_payouts ip
        JOIN investments inv ON ip.investment_id = inv.id
        JOIN users u ON inv.investor_id = u.id
        JOIN dros d ON inv.dro_id = d.id
        WHERE ip.status = 'pending'
        ORDER BY ip.payout_date ASC
        LIMIT 100
      `;

      return new Promise((resolve, reject) => {
        const db = this.investorPayoutModel['db']; // Access private db property
        db.all(sql, [], (err: any, rows: any[]) => {
          if (err) {
            reject(err);
          } else {
            resolve(rows || []);
          }
        });
      });

    } catch (error) {
      console.error('Error fetching pending payouts:', error);
      throw error;
    }
  }

  /**
   * Send processing summary notification
   */
  private async sendProcessingSummary(result: PayoutProcessingResult): Promise<void> {
    try {
      // In production, this would send notifications via email, SMS, or push notifications
      const summary = {
        timestamp: new Date().toISOString(),
        total_processed: result.total_processed,
        successful_payouts: result.successful_payouts,
        failed_payouts: result.failed_payouts,
        total_amount: result.total_amount,
        success_rate: result.total_processed > 0 ? 
          ((result.successful_payouts / result.total_processed) * 100).toFixed(2) : '0',
        errors: result.processing_errors
      };

      console.log('ðŸ“Š Payout Processing Summary:', JSON.stringify(summary, null, 2));

      // Here you would integrate with notification services:
      // - Send email to admin team
      // - Post to Slack/Discord
      // - Update monitoring dashboard
      // - Log to external monitoring service

    } catch (error) {
      console.error('Error sending processing summary:', error);
    }
  }

  /**
   * Process payouts for a specific DRO
   */
  async processPayoutsForDRO(droId: number): Promise<PayoutProcessingResult> {
    console.log(`ðŸ”„ Processing payouts for DRO ${droId}...`);

    const result: PayoutProcessingResult = {
      total_processed: 0,
      successful_payouts: 0,
      failed_payouts: 0,
      total_amount: 0,
      processing_errors: []
    };

    try {
      // Get pending payouts for specific DRO
      const sql = `
        SELECT 
          ip.*,
          u.email as investor_email,
          u.first_name || ' ' || u.last_name as investor_name,
          d.title as dro_title
        FROM investor_payouts ip
        JOIN investments inv ON ip.investment_id = inv.id
        JOIN users u ON inv.investor_id = u.id
        JOIN dros d ON inv.dro_id = d.id
        WHERE ip.status = 'pending' AND d.id = ?
        ORDER BY ip.payout_date ASC
      `;

      const pendingPayouts: any[] = await new Promise((resolve, reject) => {
        const db = this.investorPayoutModel['db'];
        db.all(sql, [droId], (err: any, rows: any[]) => {
          if (err) reject(err);
          else resolve(rows || []);
        });
      });

      if (pendingPayouts.length === 0) {
        console.log(`âœ… No pending payouts for DRO ${droId}`);
        return result;
      }

      console.log(`ðŸ“‹ Found ${pendingPayouts.length} pending payouts for DRO ${droId}`);
      result.total_processed = pendingPayouts.length;

      // Process payouts
      for (const payout of pendingPayouts) {
        try {
          const processResult = await this.processSinglePayout(payout);

          if (processResult.success) {
            result.successful_payouts++;
            result.total_amount += payout.payout_amount;
            await this.investorPayoutModel.updateStatus(payout.id!, 'completed');
          } else {
            result.failed_payouts++;
            await this.investorPayoutModel.updateStatus(payout.id!, 'failed');
            result.processing_errors.push({
              payout_id: payout.id!,
              investor_email: payout.investor_email || 'Unknown',
              error: processResult.error_message || 'Unknown error'
            });
          }

          await this.delay(1000);

        } catch (error) {
          result.failed_payouts++;
          const errorMessage = error instanceof Error ? error.message : 'Unknown error';
          result.processing_errors.push({
            payout_id: payout.id!,
            investor_email: payout.investor_email || 'Unknown',
            error: errorMessage
          });
        }
      }

      console.log(`âœ… DRO ${droId} payout processing complete: ${result.successful_payouts} successful, ${result.failed_payouts} failed`);
      return result;

    } catch (error) {
      console.error(`âŒ Error processing payouts for DRO ${droId}:`, error);
      throw error;
    }
  }

  /**
   * Get payout processing status
   */
  async getProcessingStatus(): Promise<{
    pending_payouts: number;
    processing_queue_length: number;
    last_processing_time?: string;
    next_scheduled_processing?: string;
  }> {
    try {
      const sql = `
        SELECT 
          COUNT(*) as pending_payouts,
          MIN(payout_date) as oldest_pending
        FROM investor_payouts
        WHERE status = 'pending'
      `;

      const status: any = await new Promise((resolve, reject) => {
        const db = this.investorPayoutModel['db'];
        db.all(sql, [], (err: any, rows: any[]) => {
          if (err) reject(err);
          else resolve(rows && rows.length > 0 ? rows[0] : { pending_payouts: 0 });
        });
      });

      return {
        pending_payouts: status.pending_payouts || 0,
        processing_queue_length: status.pending_payouts || 0,
        last_processing_time: new Date().toISOString(),
        next_scheduled_processing: new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString() // Next day
      };

    } catch (error) {
      console.error('Error getting processing status:', error);
      throw error;
    }
  }

  /**
   * Utility function to add delay
   */
  private delay(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
   * Schedule automatic payout processing
   * In production, this would use a proper job scheduler like Bull, Agenda, or cron
   */
  scheduleAutomaticProcessing(): void {
    console.log('â° Scheduling automatic payout processing every 24 hours...');

    // Process payouts daily at 2 AM
    setInterval(async () => {
      try {
        console.log('ðŸ”„ Running scheduled payout processing...');
        await this.processPendingPayouts();
      } catch (error) {
        console.error('âŒ Scheduled payout processing failed:', error);
      }
    }, 24 * 60 * 60 * 1000); // 24 hours

    console.log('âœ… Automatic payout processing scheduled');
  }
}

