      console.log('ðŸ“Š Payout Processing Summary:', JSON.stringify(summary, null, 2));

      // Here you would integrate with notification services:
      // - Send email to admin team
      // - Post to Slack/Discord
      // - Update monitoring dashboard
      // - Log to external monitoring service

    } catch (error) {
      console.error('Error sending processing summary:', error);
    }
  }

  /**
   * Process payouts for a specific DRO
   */
  async processPayoutsForDRO(droId: string): Promise<PayoutProcessingResult> {
    console.log(`ðŸ”„ Processing payouts for DRO ${droId}...`);

    const result: PayoutProcessingResult = {
      total_processed: 0,
      successful_payouts: 0,
      failed_payouts: 0,
      total_amount: 0,
      processing_errors: []
    };

    try {
      // Get pending payouts for specific DRO
      const sql = `
        SELECT 
          ip.*,
          u.email as investor_email,
          u.first_name || ' ' || u.last_name as investor_name,
          d.title as dro_title
        FROM investor_payouts ip
        JOIN investments inv ON ip.investment_id = inv.id
        JOIN users u ON inv.investor_id = u.id
        JOIN dros d ON inv.dro_id = d.id
        WHERE ip.status = 'pending' AND d.id = ?
        ORDER BY ip.payout_date ASC
      `;

      const pendingPayouts: any[] = await new Promise((resolve, reject) => {
        const db = this.investorPayoutModel['db'];
        db.all(sql, [droId], (err: any, rows: any[]) => {
          if (err) reject(err);
          else resolve(rows || []);
        });
      });

      if (pendingPayouts.length === 0) {
        console.log(`âœ… No pending payouts for DRO ${droId}`);
        return result;
      }

      console.log(`ðŸ“‹ Found ${pendingPayouts.length} pending payouts for DRO ${droId}`);
      result.total_processed = pendingPayouts.length;

      // Process payouts
      for (const payout of pendingPayouts) {
        try {
          const processResult = await this.processSinglePayout(payout);

          if (processResult.success) {
            result.successful_payouts++;
            result.total_amount += payout.payout_amount;
            await this.investorPayoutModel.updateStatus(payout.id!, 'completed');
          } else {
            result.failed_payouts++;
            await this.investorPayoutModel.updateStatus(payout.id!, 'failed');
            result.processing_errors.push({
              payout_id: payout.id!,
              investor_email: payout.investor_email || 'Unknown',
              error: processResult.error_message || 'Unknown error'
            });
          }

          await this.delay(1000);

        } catch (error) {
          result.failed_payouts++;
          const errorMessage = error instanceof Error ? error.message : 'Unknown error';
          result.processing_errors.push({
            payout_id: payout.id!,
            investor_email: payout.investor_email || 'Unknown',
            error: errorMessage
          });
        }
      }

      console.log(`âœ… DRO ${droId} payout processing complete: ${result.successful_payouts} successful, ${result.failed_payouts} failed`);
      return result;

    } catch (error) {
      console.error(`âŒ Error processing payouts for DRO ${droId}:`, error);
      throw error;
    }
  }

  /**
   * Get payout processing status
   */
  async getProcessingStatus(): Promise<{
    pending_payouts: number;
    processing_queue_length: number;
    last_processing_time?: string;
    next_scheduled_processing?: string;
  }> {
    try {
      const sql = `
        SELECT 
          COUNT(*) as pending_payouts,
          MIN(payout_date) as oldest_pending
        FROM investor_payouts
        WHERE status = 'pending'
      `;

      const status: any = await new Promise((resolve, reject) => {
        const db = this.investorPayoutModel['db'];
        db.all(sql, [], (err: any, rows: any[]) => {
          if (err) reject(err);
          else resolve(rows && rows.length > 0 ? rows[0] : { pending_payouts: 0 });
        });
      });

      return {
        pending_payouts: status.pending_payouts || 0,
        processing_queue_length: status.pending_payouts || 0,
        last_processing_time: new Date().toISOString(),
        next_scheduled_processing: new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString() // Next day
      };

    } catch (error) {
      console.error('Error getting processing status:', error);
      throw error;
    }
  }

  /**
   * Utility function to add delay
   */
  private delay(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
   * Schedule automatic payout processing
   * In production, this would use a proper job scheduler like Bull, Agenda, or cron
   */
  scheduleAutomaticProcessing(): void {
    console.log('â° Scheduling automatic payout processing every 24 hours...');

    // Process payouts daily at 2 AM
    setInterval(async () => {
      try {
        console.log('ðŸ”„ Running scheduled payout processing...');
        await this.processPendingPayouts();
      } catch (error) {
        console.error('âŒ Scheduled payout processing failed:', error);
      }
    }, 24 * 60 * 60 * 1000); // 24 hours

    console.log('âœ… Automatic payout processing scheduled');
  }
}

