import React from 'react';
import type { DRO } from '../services/api';
import { Building2, TrendingUp, Users, Calendar, DollarSign, Target, Zap, Star, Shield, Award, Clock, Percent } from 'lucide-react';

interface DROCardProps {
  dro: DRO;
  onInvest: (dro: DRO) => void;
  onViewDetails: (dro: DRO) => void;
}

const DROCard: React.FC<DROCardProps> = ({ dro, onInvest, onViewDetails }) => {
  const fundingPercentage = dro.target_amount > 0 ? 
    ((dro.raised_amount || 0) / dro.target_amount) * 100 : 0;

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const getStatusBadgeClass = (status: string) => {
    switch (status) {
      case 'active': return 'badge-active';
      case 'pending': return 'badge-pending';
      case 'completed': return 'badge-completed';
      case 'cancelled': return 'bg-gradient-to-r from-red-500 to-red-600 text-white px-3 py-1 rounded-full text-xs font-semibold uppercase tracking-wide';
      default: return 'bg-gradient-to-r from-gray-500 to-gray-600 text-white px-3 py-1 rounded-full text-xs font-semibold uppercase tracking-wide';
    }
  };

  const getIndustryIcon = (industry: string) => {
    const industryLower = industry.toLowerCase();
    if (industryLower.includes('tech') || industryLower.includes('software')) {
      return <Zap className="w-6 h-6 text-primary-400" />;
    } else if (industryLower.includes('food') || industryLower.includes('restaurant')) {
      return <div className="w-6 h-6 text-accent-gold text-xl">ðŸ½ï¸</div>;
    } else if (industryLower.includes('retail') || industryLower.includes('shop')) {
      return <div className="w-6 h-6 text-secondary-400 text-xl">ðŸ›ï¸</div>;
    } else if (industryLower.includes('health') || industryLower.includes('medical')) {
      return <Shield className="w-6 h-6 text-accent-green" />;
    } else if (industryLower.includes('education')) {
      return <div className="w-6 h-6 text-primary-400 text-xl">ðŸ“š</div>;
    } else {
      return <Building2 className="w-6 h-6 text-dark-500" />;
    }
  };

