    csv += 'Total Amount Distributed,' + report.business_summary.total_amount_distributed + '\n';
    csv += 'Distribution Percentage,' + report.business_summary.distribution_percentage + '%\n';
    csv += 'Total Funding Raised,' + report.business_summary.total_funding_raised + '\n';
    csv += 'Investor Count,' + report.business_summary.investor_count + '\n\n';

    csv += 'DRO Performance\n';
    csv += 'DRO ID,Title,Status,Total Raised,Target Amount,Funding %,Revenue Shared,Distributions\n';
    report.dro_performance.forEach(dro => {
      csv += `${dro.dro_id},${dro.title},${dro.funding_status},${dro.total_raised},${dro.target_amount},${dro.funding_percentage},${dro.total_revenue_shared},${dro.distribution_count}\n`;
    });

    return csv;
  }
