const fetch = require('node-fetch');

const API_BASE = 'http://localhost:3001/api';
const API_TOKEN = 'sirex-api-token-2025-batch-operations';

// Use the actual demo MSME user created earlier
const DEMO_MSME_ID = '324edd87-7831-4c5d-a4dc-ad668bcf0160';

function generateDROData(index) {
  const industries = [
    'Technology', 'Food & Beverage', 'Services', 'Healthcare', 'Education', 
    'Real Estate', 'Entertainment', 'Manufacturing', 'Agriculture', 'Retail', 
    'Renewable Energy', 'Logistics', 'Finance', 'Tourism', 'Construction'
  ];

  const industry = industries[index % industries.length];
  const revenuePercentage = 5 + (index % 25); // 5% to 30%
  const minInvestment = 1000 + (index * 100); // Starting from 1k
  const targetAmount = minInvestment * (2 + (index % 8)); // 2x to 10x min investment

  return {
    msme_id: DEMO_MSME_ID,
    title: `${industry} DRO #${index + 1}`,
    description: `Revenue-based financing opportunity for a growing ${industry.toLowerCase()} business. This DRO offers investors a ${revenuePercentage}% share of daily revenues.`,
    industry: industry,
    revenue_percentage: revenuePercentage,
    minimum_investment: minInvestment,
    target_amount: targetAmount,
    duration_months: 12 + (index % 48), // 12 to 60 months
    status: 'active' // Make them active so they appear in listings
  };
}

async function createDROsBatch(dros) {
  try {
    const response = await fetch(`${API_BASE}/dro/batch`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'X-API-Key': API_TOKEN
      },
      body: JSON.stringify({ dros })
    });

    if (!response.ok) {
      const errorText = await response.text();
      throw new Error(`HTTP ${response.status}: ${errorText}`);
    }

    const result = await response.json();
    return result;
  } catch (error) {
    console.error('Error creating DROs batch:', error);
    throw error;
  }
}

async function main() {
  console.log(' Starting SIREX DRO Import with correct demo user...');

  try {
    // Generate 50 DROs for testing
    console.log(' Generating 50 DRO records...');
    const dros = [];
    for (let i = 0; i < 50; i++) {
      dros.push(generateDROData(i));
    }

    console.log(' Creating batch of 50 DROs...');
    const result = await createDROsBatch(dros);

    console.log(' Batch creation result:', result);
    console.log(` Import completed successfully!`);

  } catch (error) {
    console.error(' Error during import:', error);
    process.exit(1);
  }
}

main();

