import React, { useState } from 'react';
import { getCurrentUser, clearAuth } from '../services/api';
import { 
  Menu, 
  X, 
  Home, 
  Target, 
  BarChart3, 
  User, 
  LogOut, 
  Bell,
  Search,
  ChevronDown
} from 'lucide-react';

interface NavigationProps {
  onNavigate: (view: string) => void;
  currentView: string;
}

const Navigation: React.FC<NavigationProps> = ({ onNavigate, currentView }) => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isProfileDropdownOpen, setIsProfileDropdownOpen] = useState(false);
  const [isNotificationsOpen, setIsNotificationsOpen] = useState(false);

  const currentUser = getCurrentUser();
  const isInvestor = currentUser?.user_type === 'investor';

  const handleLogout = () => {
    clearAuth();
    onNavigate('login');
  };

  const navItems = [
    { id: 'recommended', label: 'Recommended', icon: Target, show: true },
    { id: 'home', label: 'Browse All', icon: Home, show: true },
    { id: 'dashboard', label: isInvestor ? 'My Portfolio' : 'My DROs', icon: BarChart3, show: !!currentUser },
    { id: 'opportunities', label: 'Opportunities', icon: Target, show: isInvestor },
  ];

  const getInitials = (name: string) => {
    return name?.split(' ')
      .map(word => word[0])
      .join('')
      .toUpperCase()
      .slice(0, 2) || 'U';
  };

  const getViewTitle = () => {
    switch (currentView) {
      case 'recommended': return 'Recommended for You';
      case 'home': return 'Browse All DROs';
      case 'dashboard': return isInvestor ? 'My Portfolio' : 'My DROs';
      case 'opportunities': return 'Investment Opportunities';
      default: return 'SIREX';
    }
  };

  return (
    <nav className="glass-dark shadow-crypto border-b border-white/10 sticky top-0 z-40 backdrop-blur-xl">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo and Brand */}
          <div className="flex items-center space-x-6">
            <button
              onClick={() => onNavigate('home')}
              className="flex items-center space-x-3"
            >
              <div className="text-2xl font-bold text-gradient-primary">SIREX</div>
              <div className="hidden md:block text-sm text-slate-300">Sand in River EX</div>
            </button>

            {/* Prominent Recommended Badge */}
            <button
              onClick={() => onNavigate('recommended')}
              className={`hidden lg:flex items-center space-x-3 px-6 py-3 rounded-xl transition-all duration-200 border-2 ${
                currentView === 'recommended'
                  ? 'bg-gradient-primary text-white shadow-crypto-lg border-primary-500 glow-primary'
                  : 'bg-white/10 text-slate-300 hover:bg-white/20 hover:text-white border-white/20 hover:border-primary-500/50'
              }`}
            >
              <Target className="w-5 h-5" />
              <span className="font-bold">ðŸŽ¯ Recommended</span>
              <div className="w-3 h-3 bg-accent-gold rounded-full animate-pulse shadow-lg"></div>
            </button>
          </div>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-8">
            {navItems.map((item) => {
              if (!item.show) return null;
              const Icon = item.icon;
              return (
                <button
                  key={item.id}
                  onClick={() => onNavigate(item.id)}
                  className={`flex items-center space-x-2 px-4 py-2.5 rounded-xl text-sm font-medium transition-all duration-300 ${
                    currentView === item.id
                      ? 'text-white bg-gradient-primary shadow-crypto glow-primary'
                      : 'text-slate-300 hover:text-white hover:bg-gradient-to-r hover:from-primary-500/20 hover:to-secondary-500/20 hover:backdrop-blur-sm'
                  }`}
                >
                  <Icon className="w-4 h-4" />
                  <span>{item.label}</span>
                </button>
              );
            })}
          </div>

          {/* Right Side - User Actions */}
          <div className="flex items-center space-x-4">
            {currentUser ? (
              <>
                {/* Search (Desktop) */}
                <div className="hidden lg:block">
                  <div className="relative">
                    <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                    <input
                      type="text"
                      placeholder="Search DROs..."
                      className="pl-10 pr-4 py-2.5 glass border border-white/20 rounded-xl text-sm text-slate-200 placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 w-64 backdrop-blur-sm"
                    />
                  </div>
                </div>

                {/* Notifications */}
                <div className="relative">
                  <button
                    onClick={() => setIsNotificationsOpen(!isNotificationsOpen)}
                    className="p-2.5 text-slate-400 hover:text-white hover:bg-white/10 rounded-xl transition-all duration-200 relative backdrop-blur-sm"
                  >
                    <Bell className="w-5 h-5" />
                    <span className="absolute top-1 right-1 w-2 h-2 bg-red-500 rounded-full"></span>
                  </button>

                  {/* Notifications Dropdown */}
                  {isNotificationsOpen && (
                    <>
                      <div 
                        className="fixed inset-0 z-10" 
                        onClick={() => setIsNotificationsOpen(false)}
                      ></div>
                      <div className="absolute right-0 mt-2 w-80 glass-card shadow-crypto-lg border border-white/10 z-20">
                        <div className="p-4 border-b border-white/10">
                          <h3 className="text-sm font-medium text-white">Notifications</h3>
                        </div>
                        <div className="max-h-64 overflow-y-auto">
                          <div className="p-4 text-center text-slate-400">
                            <Bell className="w-8 h-8 mx-auto mb-2 text-slate-500" />
                            <p className="text-sm">No new notifications</p>
                          </div>
                        </div>
                      </div>
                    </>
                  )}
                </div>

                {/* Profile Dropdown */}
                <div className="relative">
                  <button
                    onClick={() => setIsProfileDropdownOpen(!isProfileDropdownOpen)}
                    className="flex items-center space-x-2 p-2.5 rounded-xl hover:bg-white/10 transition-all duration-200 backdrop-blur-sm"
                  >
                    <div className="w-8 h-8 bg-gradient-primary text-white rounded-full flex items-center justify-center text-sm font-medium shadow-crypto">
                      {getInitials(currentUser.name)}
                    </div>
                    <div className="hidden md:block text-left">
                      <div className="text-sm font-medium text-white">{currentUser.name}</div>
                      <div className="text-xs text-slate-300 capitalize">{currentUser.user_type}</div>
                    </div>
                    <ChevronDown className="w-4 h-4 text-slate-400" />
                  </button>

                  {/* Profile Dropdown Menu */}
                  {isProfileDropdownOpen && (
                    <>
                      <div 
                        className="fixed inset-0 z-10" 
                        onClick={() => setIsProfileDropdownOpen(false)}
                      ></div>
                      <div className="absolute right-0 mt-2 w-56 glass-card shadow-crypto-lg border border-white/10 z-20">
                        <div className="p-4 border-b border-white/10">
                          <div className="flex items-center space-x-3">
                            <div className="w-10 h-10 bg-gradient-primary text-white rounded-full flex items-center justify-center text-sm font-medium shadow-crypto">
                              {getInitials(currentUser.name)}
                            </div>
                            <div>
                              <div className="text-sm font-medium text-white">{currentUser.name}</div>
                              <div className="text-xs text-slate-300">{currentUser.email}</div>
                              <div className="text-xs text-primary-400 capitalize">{currentUser.user_type} Account</div>
                            </div>
                          </div>
                        </div>
                        <div className="py-1">
                          <button
                            onClick={() => {
                              onNavigate('profile');
                              setIsProfileDropdownOpen(false);
                            }}
                            className="flex items-center px-4 py-2 text-sm text-slate-300 hover:text-white hover:bg-white/10 w-full transition-all duration-200"
                          >
                            <User className="w-4 h-4 mr-3" />
                            Profile Settings
                          </button>
                          <button
                            onClick={handleLogout}
                            className="flex items-center px-4 py-2 text-sm text-slate-300 hover:text-white hover:bg-white/10 w-full transition-all duration-200"
                          >
                            <LogOut className="w-4 h-4 mr-3" />
                            Sign Out
                          </button>
                        </div>
                      </div>
                    </>
                  )}
                </div>
              </>
            ) : (
              <div className="flex items-center space-x-4">
                <button
                  onClick={() => onNavigate('login')}
                  className="text-slate-300 hover:text-white font-medium transition-colors duration-200"
                >
                  Sign In
                </button>
                <button
                  onClick={() => onNavigate('register')}
                  className="btn-crypto-primary text-sm"
                >
                  Get Started
                </button>
              </div>
            )}

            {/* Mobile menu button */}
            <div className="md:hidden">
              <button
                onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                className="p-2 text-slate-400 hover:text-white transition-colors duration-200"
              >
                {isMobileMenuOpen ? (
                  <X className="w-6 h-6" />
                ) : (
                  <Menu className="w-6 h-6" />
                )}
              </button>
            </div>
          </div>
        </div>

        {/* Mobile Search Bar */}
        {currentUser && (
          <div className="lg:hidden pb-3">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <input
                type="text"
                placeholder="Search DROs..."
                className="w-full pl-10 pr-4 py-2.5 glass border border-white/20 rounded-xl text-sm text-slate-200 placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 backdrop-blur-sm"
              />
            </div>
          </div>
        )}
      </div>

      {/* Mobile Navigation Menu */}
      {isMobileMenuOpen && (
        <div className="md:hidden border-t border-white/10 glass-dark backdrop-blur-xl">
          <div className="px-2 pt-2 pb-3 space-y-1">
            {navItems.map((item) => {
              if (!item.show) return null;
              const Icon = item.icon;
              return (
                <button
                  key={item.id}
                  onClick={() => {
                    onNavigate(item.id);
                    setIsMobileMenuOpen(false);
                  }}
                  className={`flex items-center space-x-3 px-4 py-3 rounded-xl text-base font-medium w-full transition-all duration-300 ${
                    currentView === item.id
                      ? 'text-white bg-gradient-primary shadow-crypto'
                      : 'text-slate-300 hover:text-white hover:bg-white/10'
                  }`}
                >
                  <Icon className="w-5 h-5" />
                  <span>{item.label}</span>
                </button>
              );
            })}

            {/* Mobile Auth Buttons */}
            {!currentUser && (
              <div className="border-t border-white/10 pt-4 mt-4 space-y-1">
                <button
                  onClick={() => {
                    onNavigate('login');
                    setIsMobileMenuOpen(false);
                  }}
                  className="flex items-center space-x-3 px-4 py-3 rounded-xl text-base font-medium w-full text-slate-300 hover:text-white hover:bg-white/10 transition-all duration-200"
                >
                  <User className="w-5 h-5" />
                  <span>Sign In</span>
                </button>
                <button
                  onClick={() => {
                    onNavigate('register');
                    setIsMobileMenuOpen(false);
                  }}
                  className="flex items-center space-x-3 px-4 py-3 rounded-xl text-base font-medium w-full text-white bg-gradient-primary hover:shadow-crypto transition-all duration-200"
                >
                  <Target className="w-5 h-5" />
                  <span>Get Started</span>
                </button>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Page Title Bar (Mobile) */}
      <div className="md:hidden glass border-t border-white/10 px-4 py-3 backdrop-blur-xl">
        <h1 className="text-lg font-semibold text-white">{getViewTitle()}</h1>
        {currentUser && (
          <p className="text-sm text-slate-300">
            Welcome back, {currentUser.name.split(' ')[0]}
          </p>
        )}
      </div>
    </nav>
  );
};

export default Navigation;

