   271â†’              <div className="text-sm">
   272â†’                <div className="font-medium text-accent-gold mb-1">Investment Risk Disclosure</div>
   273â†’                <ul className="text-slate-300 space-y-1">
   274â†’                  <li>â€¢ Revenue-based investments carry risk and returns are not guaranteed</li>
   275â†’                  <li>â€¢ Actual returns depend on the business's daily revenue performance</li>
   276â†’                  <li>â€¢ You may lose some or all of your investment</li>
   277â†’                  <li>â€¢ Please consider your risk tolerance before investing</li>
   278â†’                </ul>
   279â†’              </div>
   280â†’            </div>
   281â†’          </div>
   282â†’
   283â†’          {/* Error Message */}
   284â†’          {error && (
   285â†’            <div className="mb-4 glass bg-red-500/10 border border-red-400/30 rounded-xl p-4 backdrop-blur-sm">
   286â†’              <div className="flex items-start">
   287â†’                <AlertTriangle className="w-5 h-5 text-red-300 mr-2 mt-0.5 flex-shrink-0" />
   288â†’                <div className="text-sm text-red-300">{error}</div>
   289â†’              </div>
   290â†’            </div>
   291â†’          )}
   292â†’
   293â†’          {/* Action Buttons */}
   294â†’          <div className="flex space-x-4">
   295â†’            <button
   296â†’              onClick={onClose}
   297â†’              className="flex-1 btn-crypto-secondary"
   298â†’              disabled={loading}
   299â†’            >
   300â†’              Cancel
   301â†’            </button>
   302â†’            <button
   303â†’              onClick={handleInvest}
   304â†’              disabled={
   305â†’                loading || 
   306â†’                !investmentAmount || 
   307â†’                parseFloat(investmentAmount) <= 0 || 
   308â†’                validationErrors.length > 0
   309â†’              }
   310â†’              className="flex-1 btn-crypto-primary flex items-center justify-center"
   311â†’            >
   312â†’              {loading ? (
   313â†’                <>
   314â†’                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
   315â†’                  Processing...
   316â†’                </>
   317â†’              ) : (
   318â†’                <>
   319â†’                  <TrendingUp className="w-4 h-4 mr-2" />
   320â†’                  Invest {investmentAmount ? formatCurrency(parseFloat(investmentAmount)) : ''}
   321â†’                </>
   322â†’              )}
   323â†’            </button>
   324â†’          </div>
   325â†’        </div>
   326â†’      </div>
   327â†’    </div>
   328â†’  );
   329â†’};
   330â†’
   331â†’export default InvestmentModal;

