const fs = require('fs');
const path = require('path');
const Database = require('duckdb').Database;

async function importDROs() {
  try {
    console.log('ðŸ—„ï¸ Starting DRO import process...');

    // Connect to DuckDB
    const dbPath = path.join(__dirname, '..', 'sirex.duckdb');
    const db = new Database(dbPath);

    console.log(`ðŸ“‚ Connected to database: ${dbPath}`);

    // Read and execute Part 1 SQL file
    console.log('ðŸ“„ Reading Part 1 SQL file...');
    const part1Path = path.join(__dirname, 'sirex-100-dros-part1.sql');
    const part1SQL = fs.readFileSync(part1Path, 'utf8');

    console.log('ðŸ”§ Executing Part 1 (100 DROs)...');
    await new Promise((resolve, reject) => {
      db.exec(part1SQL, (err) => {
        if (err) {
          console.error('âŒ Error executing Part 1:', err);
          reject(err);
        } else {
          console.log('âœ… Part 1 executed successfully');
          resolve();
        }
      });
    });

    // Read and execute Part 2 SQL file
    console.log('ðŸ“„ Reading Part 2 SQL file...');
    const part2Path = path.join(__dirname, 'sirex-100-dros-part2.sql');
    const part2SQL = fs.readFileSync(part2Path, 'utf8');

    console.log('ðŸ”§ Executing Part 2 (100 DROs)...');
    await new Promise((resolve, reject) => {
      db.exec(part2SQL, (err) => {
        if (err) {
          console.error('âŒ Error executing Part 2:', err);
          reject(err);
        } else {
          console.log('âœ… Part 2 executed successfully');
          resolve();
        }
      });
    });

    // Verify the import
    console.log('ðŸ” Verifying import...');
    await new Promise((resolve, reject) => {
      db.all('SELECT COUNT(*) as count FROM users WHERE id >= 7', (err, result) => {
        if (err) {
          console.error('âŒ Error verifying users:', err);
          reject(err);
        } else {
          console.log(`âœ… New users added: ${result[0].count}`);
          resolve();
        }
      });
    });

    await new Promise((resolve, reject) => {
      db.all('SELECT COUNT(*) as count FROM dros WHERE id >= 4', (err, result) => {
        if (err) {
          console.error('âŒ Error verifying DROs:', err);
          reject(err);
        } else {
          console.log(`âœ… New DROs added: ${result[0].count}`);
          resolve();
        }
      });
    });

    // Show total counts
    await new Promise((resolve, reject) => {
      db.all('SELECT COUNT(*) as count FROM users', (err, result) => {
        if (err) {
          console.error('âŒ Error counting total users:', err);
          reject(err);
        } else {
          console.log(`ðŸ“Š Total users in database: ${result[0].count}`);
          resolve();
        }
      });
    });

    await new Promise((resolve, reject) => {
      db.all('SELECT COUNT(*) as count FROM dros', (err, result) => {
        if (err) {
          console.error('âŒ Error counting total DROs:', err);
          reject(err);
        } else {
          console.log(`ðŸ“Š Total DROs in database: ${result[0].count}`);
          resolve();
        }
      });
    });

    // Close database connection
    db.close();

    console.log('ðŸŽ‰ Import completed successfully!');
    console.log('ðŸ“ˆ Summary:');
    console.log('   - Added 50 new MSME users');
    console.log('   - Added 200 new DROs');
    console.log('   - Covering 11+ industries');
    console.log('   - Various investment ranges and return rates');

  } catch (error) {
    console.error('âŒ Import failed:', error);
