import { duckdb } from '../src/config/duckdb-connection';
import { readFile } from 'fs/promises';
import { join } from 'path';

async function importDROs() {
  try {
    console.log('ðŸ—„ï¸ Starting DRO import process...');

    // Connect to DuckDB using the project's connection class
    await duckdb.connect();
    console.log('ðŸ“‚ Connected to database successfully');

    // Read and execute Part 1 SQL file
    console.log('ðŸ“„ Reading Part 1 SQL file...');
    const part1Path = join(__dirname, 'sirex-100-dros-part1.sql');
    const part1SQL = await readFile(part1Path, 'utf8');

    console.log('ðŸ”§ Executing Part 1 (100 DROs)...');

    // Split SQL into statements and execute each one
    const part1Statements = part1SQL
      .split(';')
      .map(stmt => stmt.trim())
      .filter(stmt => stmt.length > 0 && !stmt.startsWith('--') && stmt.length > 10);

    console.log(`Found ${part1Statements.length} statements in Part 1`);

    // Start transaction for Part 1
    await duckdb.query('BEGIN TRANSACTION');

    try {
      for (let i = 0; i < part1Statements.length; i++) {
        const statement = part1Statements[i];
        console.log(`Executing Part 1 statement ${i + 1}/${part1Statements.length}...`);
        await duckdb.query(statement);
      }

      // Commit Part 1
      await duckdb.query('COMMIT');
      console.log('âœ… Part 1 committed successfully');
    } catch (error) {
      await duckdb.query('ROLLBACK');
      throw error;
    }

    // Read and execute Part 2 SQL file
    console.log('ðŸ“„ Reading Part 2 SQL file...');
    const part2Path = join(__dirname, 'sirex-100-dros-part2.sql');
    const part2SQL = await readFile(part2Path, 'utf8');

    console.log('ðŸ”§ Executing Part 2 (100 DROs)...');

    // Split SQL into statements and execute each one
    const part2Statements = part2SQL
      .split(';')
      .map(stmt => stmt.trim())
      .filter(stmt => stmt.length > 0 && !stmt.startsWith('--') && stmt.length > 10);

    console.log(`Found ${part2Statements.length} statements in Part 2`);

    // Start transaction for Part 2
    await duckdb.query('BEGIN TRANSACTION');

    try {
      for (let i = 0; i < part2Statements.length; i++) {
        const statement = part2Statements[i];
        console.log(`Executing Part 2 statement ${i + 1}/${part2Statements.length}...`);
        await duckdb.query(statement);
      }

      // Commit Part 2
      await duckdb.query('COMMIT');
      console.log('âœ… Part 2 committed successfully');
    } catch (error) {
      await duckdb.query('ROLLBACK');
      throw error;
    }

    // Verify the import
    console.log('ðŸ” Verifying import...');

    const userResult = await duckdb.query('SELECT COUNT(*) as count FROM users WHERE id >= 7');
    const newUsersCount = userResult.rows[0]?.count || 0;
    console.log(`âœ… New users added: ${newUsersCount}`);

    const droResult = await duckdb.query('SELECT COUNT(*) as count FROM dros WHERE id >= 4');
    const newDROsCount = droResult.rows[0]?.count || 0;
    console.log(`âœ… New DROs added: ${newDROsCount}`);

    // Show total counts
    const totalUsersResult = await duckdb.query('SELECT COUNT(*) as count FROM users');
    const totalUsers = totalUsersResult.rows[0]?.count || 0;
    console.log(`ðŸ“Š Total users in database: ${totalUsers}`);

    const totalDROsResult = await duckdb.query('SELECT COUNT(*) as count FROM dros');
    const totalDROs = totalDROsResult.rows[0]?.count || 0;
    console.log(`ðŸ“Š Total DROs in database: ${totalDROs}`);

    // Show DRO distribution by industry
    console.log('ðŸ“ˆ DRO distribution by industry:');
    const industryResult = await duckdb.query(`
      SELECT industry, COUNT(*) as count 
      FROM dros 
      GROUP BY industry 
      ORDER BY count DESC
    `);

    for (const row of industryResult.rows) {
      console.log(`   ${row.industry}: ${row.count} DROs`);
    }

    // Close database connection
    await duckdb.close();

    console.log('ðŸŽ‰ Import completed successfully!');
    console.log('ðŸ“ˆ Summary:');
    console.log(`   - Added ${newUsersCount} new MSME users`);
    console.log(`   - Added ${newDROsCount} new DROs`);
    console.log('   - Covering 11+ industries');
    console.log('   - Various investment ranges and return rates');

  } catch (error) {
    console.error('âŒ Import failed:', error);

    // Try to close database connection
    try {
      await duckdb.close();
    } catch (closeError) {
      console.error('âŒ Failed to close database connection:', closeError);
    }

    process.exit(1);
  }
}

// Run the import
importDROs()
  .then(() => {
    console.log('âœ… Import process completed successfully');
    process.exit(0);
  })
  .catch((error) => {
    console.error('âŒ Import process failed:', error);
    process.exit(1);
  });

