import { duckdb } from '../config/duckdb-connection';

// 10 new MSME users data (we'll create more programmatically)
const sampleMSMEUsers = [
  { id: 7, email: 'msme4@example.com', password_hash: '$2b$10$rQZt.sLgf5oRK1Ow5vO8VuKfF3tIkL8.eW2nQ5mR7pS9uT3vX4yZ6', first_name: 'David', last_name: 'Kim', user_type: 'msme', kyc_status: 'approved', is_verified: true, company_name: 'FoodTech Innovations', business_type: 'Technology', annual_revenue: 800000.00, phone: '+82-2-1234-5678', address: 'Seoul Business District', country: 'South Korea' },
  { id: 8, email: 'msme5@example.com', password_hash: '$2b$10$rQZt.sLgf5oRK1Ow5vO8VuKfF3tIkL8.eW2nQ5mR7pS9uT3vX4yZ6', first_name: 'Anna', last_name: 'Schmidt', user_type: 'msme', kyc_status: 'approved', is_verified: true, company_name: 'EcoClean Services', business_type: 'Services', annual_revenue: 450000.00, phone: '+49-30-12345678', address: 'Berlin Innovation Hub', country: 'Germany' },
  { id: 9, email: 'msme6@example.com', password_hash: '$2b$10$rQZt.sLgf5oRK1Ow5vO8VuKfF3tIkL8.eW2nQ5mR7pS9uT3vX4yZ6', first_name: 'Raj', last_name: 'Patel', user_type: 'msme', kyc_status: 'approved', is_verified: true, company_name: 'FinTech Solutions', business_type: 'Technology', annual_revenue: 1500000.00, phone: '+91-22-12345678', address: 'Mumbai Tech Park', country: 'India' },
  { id: 10, email: 'msme7@example.com', password_hash: '$2b$10$rQZt.sLgf5oRK1Ow5vO8VuKfF3tIkL8.eW2nQ5mR7pS9uT3vX4yZ6', first_name: 'Sophie', last_name: 'Dubois', user_type: 'msme', kyc_status: 'approved', is_verified: true, company_name: 'Artisan Bakery Co', business_type: 'Food & Beverage', annual_revenue: 350000.00, phone: '+33-1-12345678', address: 'Paris Montmartre', country: 'France' },
  { id: 11, email: 'msme8@example.com', password_hash: '$2b$10$rQZt.sLgf5oRK1Ow5vO8VuKfF3tIkL8.eW2nQ5mR7pS9uT3vX4yZ6', first_name: 'James', last_name: 'Thompson', user_type: 'msme', kyc_status: 'approved', is_verified: true, company_name: 'Urban Agriculture', business_type: 'Agriculture', annual_revenue: 600000.00, phone: '+1-555-0301', address: 'Portland Urban Farm', country: 'USA' },
];

function generateUser(id: number): any {
  const firstNames = ['Maria', 'Carlos', 'Elena', 'Ahmed', 'Lisa', 'Marco', 'Yuki', 'Oliver', 'Isabella', 'Chen', 'Lucas', 'Priya'];
  const lastNames = ['Gonzalez', 'Rodriguez', 'Petrov', 'Hassan', 'Wong', 'Rossi', 'Tanaka', 'Johnson', 'Andersson', 'Wei', 'Mueller', 'Sharma'];
  const companies = ['Tech Solutions', 'Green Energy', 'Digital Platform', 'Health Innovation', 'Smart Systems', 'Eco Products'];
  const countries = ['Spain', 'Mexico', 'Russia', 'UAE', 'Hong Kong', 'Italy', 'Japan', 'UK', 'Sweden', 'China', 'Germany', 'India'];

  const firstName = firstNames[id % firstNames.length];
  const lastName = lastNames[id % lastNames.length];

  return {
    id,
    email: `msme${id}@example.com`,
    password_hash: '$2b$10$rQZt.sLgf5oRK1Ow5vO8VuKfF3tIkL8.eW2nQ5mR7pS9uT3vX4yZ6',
    first_name: firstName,
    last_name: lastName,
    user_type: 'msme',
    kyc_status: 'approved',
    is_verified: true,
    company_name: `${firstName} ${companies[id % companies.length]}`,
    business_type: 'Technology',
    annual_revenue: 500000 + (id * 10000),
    phone: `+${id}-123-456-789`,
    address: `Business District ${id}`,
    country: countries[id % countries.length]
  };
}

function generateDRO(id: number, msmeId: number): any {
  const industries = ['Technology', 'Food & Beverage', 'Services', 'Healthcare', 'Education', 'Real Estate', 'Entertainment', 'Manufacturing', 'Agriculture', 'Retail', 'Renewable Energy'];
  const riskLevels = ['low', 'medium', 'high'];
  const statuses = ['active', 'pending'];

  const industry = industries[id % industries.length];
  const riskLevel = riskLevels[id % riskLevels.length];
  const status = statuses[id % statuses.length];

  const revenuePercentage = 8 + (id % 22); // 8% to 30%
  const minInvestment = 5000 + (id * 1000); // 5k to 205k
  const targetAmount = minInvestment * (5 + (id % 15)); // 5x to 20x min investment
  const raisedAmount = id % 3 === 0 ? targetAmount * (id % 50) / 100 : 0; // Some have partial funding

  return {
    id,
    msme_id: msmeId,
    title: `${industry} Revenue Opportunity ${id}`,
    description: `Revenue sharing opportunity in ${industry.toLowerCase()} sector with established business operations`,
    industry,
    revenue_percentage: revenuePercentage,
    minimum_investment: minInvestment,
    target_amount: targetAmount,
    raised_amount: raisedAmount,
    duration_months: 12 + (id % 48), // 12 to 60 months
    annual_revenue: 400000 + (id * 5000),
    roi_estimate: revenuePercentage * 1.2,
    risk_level: riskLevel,
    collateral_description: `${industry} assets and business operations`,
    use_of_funds: 'Business expansion and growth',
    repayment_terms: 'Monthly revenue distributions',
    status,
    funding_deadline: `2025-${8 + (id % 4)}-${15 + (id % 15)} 23:59:59`
  };
}

async function import200DROs() {
  try {
    console.log('ðŸ—„ï¸ Starting import of 200 DROs...');

    // Connect to database
    await duckdb.connect();
    console.log('ðŸ“‚ Connected to database');

    // Start transaction
    await duckdb.query('BEGIN TRANSACTION');

    try {
      // Insert sample MSME users
      console.log('ðŸ‘¥ Inserting MSME users...');
      for (const user of sampleMSMEUsers) {
        try {
          await duckdb.query(`
            INSERT INTO users (id, email, password_hash, first_name, last_name, user_type, kyc_status, is_verified, company_name, business_type, annual_revenue, phone, address, country)
            VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
          `, [
            user.id, user.email, user.password_hash, user.first_name, user.last_name,
            user.user_type, user.kyc_status, user.is_verified, user.company_name,
            user.business_type, user.annual_revenue, user.phone, user.address, user.country
          ]);
        } catch (error: any) {
          if (!error.message.includes('UNIQUE constraint failed')) {
            throw error;
          }
          console.log(`  User ${user.email} already exists, skipping`);
        }
      }

      // Generate and insert additional users
      for (let id = 12; id <= 56; id++) {
        const user = generateUser(id);
        try {
          await duckdb.query(`
            INSERT INTO users (id, email, password_hash, first_name, last_name, user_type, kyc_status, is_verified, company_name, business_type, annual_revenue, phone, address, country)
            VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
          `, [
            user.id, user.email, user.password_hash, user.first_name, user.last_name,
            user.user_type, user.kyc_status, user.is_verified, user.company_name,
            user.business_type, user.annual_revenue, user.phone, user.address, user.country
          ]);
        } catch (error: any) {
          if (!error.message.includes('UNIQUE constraint failed')) {
            throw error;
          }
        }
      }
      console.log('âœ… MSME users processed');

      // Generate and insert 200 DROs
      console.log('ðŸŽ¯ Generating and inserting 200 DROs...');
      let droId = 4;
      let msmeId = 7;

      for (let i = 0; i < 200; i++) {
        const dro = generateDRO(droId, msmeId);

        try {
          await duckdb.query(`
            INSERT INTO dros (id, msme_id, title, description, industry, revenue_percentage, minimum_investment, target_amount, raised_amount, duration_months, annual_revenue, roi_estimate, risk_level, collateral_description, use_of_funds, repayment_terms, status, listing_date, funding_deadline)
            VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, CURRENT_TIMESTAMP, ?)
          `, [
            dro.id, dro.msme_id, dro.title, dro.description, dro.industry,
            dro.revenue_percentage, dro.minimum_investment, dro.target_amount,
            dro.raised_amount, dro.duration_months, dro.annual_revenue,
            dro.roi_estimate, dro.risk_level, dro.collateral_description,
            dro.use_of_funds, dro.repayment_terms, dro.status,
            dro.funding_deadline
          ]);
        } catch (error: any) {
          if (!error.message.includes('UNIQUE constraint failed')) {
            throw error;
          }
        }

        droId++;
        if ((i + 1) % 4 === 0) { // 4 DROs per MSME
          msmeId++;
          if (msmeId > 56) msmeId = 7; // Cycle back if needed
        }

        if ((i + 1) % 50 === 0) {
          console.log(`   Progress: ${i + 1}/200 DROs inserted`);
        }
      }

      // Commit transaction
      await duckdb.query('COMMIT');
      console.log('âœ… All data committed successfully');

      // Verify results
      const userCount = await duckdb.query('SELECT COUNT(*) as count FROM users');
      const droCount = await duckdb.query('SELECT COUNT(*) as count FROM dros');
      const industryStats = await duckdb.query('SELECT industry, COUNT(*) as count FROM dros GROUP BY industry ORDER BY count DESC');

      console.log('ðŸ“Š Import Summary:');
      console.log(`   Total users: ${userCount.rows[0].count}`);
      console.log(`   Total DROs: ${droCount.rows[0].count}`);
      console.log('   DROs by industry:');
      for (const row of industryStats.rows) {
        console.log(`     ${row.industry}: ${row.count}`);
      }

    } catch (error) {
      await duckdb.query('ROLLBACK');
      throw error;
    }

    await duckdb.close();
    console.log('ðŸŽ‰ Import completed successfully!');

  } catch (error) {
    console.error('âŒ Import failed:', error);
    try {
      await duckdb.close();
    } catch (closeError) {
      console.error('Failed to close database:', closeError);
    }
    process.exit(1);
  }
}

// Run the import if this script is executed directly
if (require.main === module) {
  import200DROs()
    .then(() => {
      console.log('âœ… Import process completed');
      process.exit(0);
    })
    .catch((error) => {
      console.error('âŒ Import process failed:', error);
      process.exit(1);
    });
}

export { import200DROs };

