  console.log('ðŸš€ Creating demo users...');

  let successCount = 0;
  let errorCount = 0;

  for (const user of demoUsers) {
    try {
      const response = await fetch(`${API_BASE}/auth/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
