        body: JSON.stringify(user)
      });

      const result = await response.json();

      if (response.ok) {
        console.log(`âœ… Created user: ${user.email} (${user.user_type})`);
        successCount++;
      } else {
        if (result.code === 'USER_EXISTS') {
          console.log(`â„¹ï¸  User already exists: ${user.email}`);
          successCount++; // Count as success since user exists
        } else {
          console.log(`âŒ Failed to create ${user.email}: ${result.error}`);
          errorCount++;
        }
      }
    } catch (error) {
      console.log(`âŒ Error creating ${user.email}: ${error.message}`);
      errorCount++;
    }

    // Small delay to avoid overwhelming the server
    await new Promise(resolve => setTimeout(resolve, 100));
  }

  console.log(`\nðŸ“Š Summary:`);
  console.log(`   âœ… Successfully created/verified: ${successCount} users`);
  console.log(`   âŒ Errors: ${errorCount} users`);

  if (successCount > 0) {
    console.log(`\nðŸŽ‰ Demo users are ready! You can now:`);
