    console.log(`   2. Login with demo accounts:`);
    console.log(`      - investor@demo.com / demo123456`);
    console.log(`      - msme@demo.com / demo123456`);
  }
}

createDemoUsers().catch(error => {
  console.error('âŒ Script failed:', error);
  process.exit(1);
});

