import { duckdb } from '../config/duckdb-connection';
import { R2BackupService } from './R2BackupService';

interface HealthCheckResult {
  component: string;
  status: 'healthy' | 'warning' | 'error';
  message: string;
  details?: any;
}

export class StartupHealthCheck {
  private results: HealthCheckResult[] = [];

  async runAllChecks(): Promise<boolean> {
    console.log('ðŸ” Running startup health checks...');

    // Reset results
    this.results = [];

    // Run all checks
    await this.checkDuckDB();
    await this.checkR2Backup();
    await this.checkEnvironmentVariables();
    await this.checkPaymentServices();

    // Report results
    this.reportResults();

    // Return true if no critical errors
    const criticalErrors = this.results.filter(r => r.status === 'error');
    return criticalErrors.length === 0;
  }

  private async checkDuckDB(): Promise<void> {
    try {
      console.log('   ðŸ“Š Checking DuckDB connection...');

      // Test basic connection
      const result = await duckdb.query('SELECT 1 as test');
      if (!result || !result.rows || result.rows.length === 0) {
        throw new Error('DuckDB query returned no results');
      }

      // Check if tables exist
      const tablesResult = await duckdb.query(`
        SELECT table_name 
        FROM information_schema.tables 
        WHERE table_schema = 'main'
      `);

      const tableCount = tablesResult.rows?.length || 0;

      if (tableCount === 0) {
        this.results.push({
          component: 'DuckDB',
          status: 'warning',
          message: 'Database connected but no tables found - may need initialization',
          details: { tablesFound: tableCount }
        });
      } else {
        this.results.push({
          component: 'DuckDB',
          status: 'healthy',
          message: `Database connected successfully with ${tableCount} tables`,
          details: { tablesFound: tableCount }
        });
      }

    } catch (error) {
      this.results.push({
        component: 'DuckDB',
        status: 'error',
        message: `Database connection failed: ${error.message}`,
        details: { error: error.message }
      });
    }
  }

  private async checkR2Backup(): Promise<void> {
    try {
      console.log('   ðŸ“¦ Checking R2 Backup service...');

      const r2Service = new R2BackupService();

      if (!r2Service.isEnabled()) {
        this.results.push({
          component: 'R2 Backup',
          status: 'warning',
          message: 'R2 backup service disabled - check environment variables',
          details: { enabled: false }
        });
        return;
      }

      // Test R2 connection by listing backups
      const backups = await r2Service.listBackups();

      this.results.push({
        component: 'R2 Backup',
        status: 'healthy',
        message: `R2 backup service connected successfully`,
        details: { 
          enabled: true, 
          backupsFound: backups.length,
          bucket: process.env.CLOUDFLARE_R2_BUCKET_NAME 
        }
      });

    } catch (error) {
      this.results.push({
        component: 'R2 Backup',
        status: 'error',
        message: `R2 backup service failed: ${error.message}`,
        details: { error: error.message }
      });
    }
  }

  private async checkEnvironmentVariables(): Promise<void> {
    console.log('   ðŸ”§ Checking environment variables...');

    const requiredVars = [
      'NODE_ENV',
      'JWT_SECRET',
      'JWT_REFRESH_SECRET'
    ];

    const optionalVars = [
      'STRIPE_SECRET_KEY',
      'PAYPAL_CLIENT_ID',
      'CLOUDFLARE_R2_ACCESS_KEY_ID'
    ];

    const missing = requiredVars.filter(varName => !process.env[varName]);
    const optionalMissing = optionalVars.filter(varName => !process.env[varName]);

    if (missing.length > 0) {
      this.results.push({
        component: 'Environment',
        status: 'error',
        message: `Missing required environment variables: ${missing.join(', ')}`,
        details: { missing, optionalMissing }
      });
    } else if (optionalMissing.length > 0) {
      this.results.push({
        component: 'Environment',
        status: 'warning',
        message: `Optional environment variables not set: ${optionalMissing.join(', ')}`,
        details: { missing: [], optionalMissing }
      });
    } else {
      this.results.push({
        component: 'Environment',
        status: 'healthy',
        message: 'All environment variables configured',
        details: { missing: [], optionalMissing: [] }
      });
    }
  }

  private async checkPaymentServices(): Promise<void> {
    console.log('   ðŸ’³ Checking payment services...');

    const stripeConfigured = !!process.env.STRIPE_SECRET_KEY;
    const paypalConfigured = !!(process.env.PAYPAL_CLIENT_ID && process.env.PAYPAL_CLIENT_SECRET);

    if (!stripeConfigured && !paypalConfigured) {
      this.results.push({
        component: 'Payment Services',
        status: 'warning',
        message: 'No payment services configured - platform will run in demo mode',
        details: { stripe: false, paypal: false }
      });
    } else {
      this.results.push({
        component: 'Payment Services',
        status: 'healthy',
        message: `Payment services available: ${stripeConfigured ? 'Stripe ' : ''}${paypalConfigured ? 'PayPal' : ''}`,
        details: { stripe: stripeConfigured, paypal: paypalConfigured }
      });
    }
  }

  private reportResults(): void {
    console.log('\nðŸ“‹ Startup Health Check Results:');
    console.log('=====================================');

    for (const result of this.results) {
      const icon = result.status === 'healthy' ? 'âœ…' : 
