        const localTime = localStats.mtime;

        console.log('ðŸ” Comparing database timestamps:');
        console.log(`   Local DB: ${localTime.toISOString()} (${(localStats.size / 1024).toFixed(1)} KB)`);
        console.log(`   Latest backup: ${backupTime.toISOString()} (${latestBackup.size})`);

        if (localTime > backupTime) {
          console.log('âœ¨ Local database is newer than backup - keeping local version');
          return null;
        } else {
          console.log('ðŸ“¥ Backup is newer than local database - restoring...');
        }
      } else if (!existsSync(dbPath)) {
        console.log('ðŸ†• No local database found - restoring from backup...');
      } else {
        console.log('ðŸ”„ Force restore requested - overwriting local database...');
      }

      // Get the most recent backup (already sorted by date)
      const latestBackup = backups[0];

      console.log('ðŸ“¦ Restoring backup...', { 
        backupId: latestBackup.id, 
        type: latestBackup.type,
        size: latestBackup.size 
      });

      // Download and restore
      await this.downloadFromR2(latestBackup.id, dbPath);
      await this.updateBackupStatus(latestBackup.id, 'latest_restored');

      console.log('âœ… Backup restored successfully', { backupId: latestBackup.id });
      return latestBackup.id;

    } catch (error) {
      console.error('âŒ Failed to restore latest backup:', error);
      return null;
    }
  }

  /**
   * List available backups
   */
  async listBackups(limit = 20): Promise<BackupInfo[]> {
    if (!this.enabled) {
      throw new Error('Backup service is disabled');
    }

    const command = new ListObjectsV2Command({
      Bucket: this.bucketName,
      Prefix: this.backupPath.replace(/^\//, ''),
      MaxKeys: limit
    });

    const response = await this.s3Client!.send(command);

    if (!response.Contents) {
