import express from 'express';
import { UserModel, CreateUserData } from '../models/User';
import { JWTUtils } from '../utils/jwt';
import { authenticateToken } from '../middleware/auth';

const router = express.Router();

// POST /api/auth/register
router.post('/register', async (req, res) => {
  try {
    console.log('ðŸ” Registration attempt:', JSON.stringify(req.body, null, 2));
    const { email, password, first_name, last_name, user_type } = req.body;

    // Validation
    if (!email || !password || !user_type) {
      return res.status(400).json({ 
        error: 'Email, password, and user_type are required',
        code: 'VALIDATION_ERROR'
      });
    }

    if (!['investor', 'msme'].includes(user_type)) {
      return res.status(400).json({ 
        error: 'user_type must be either "investor" or "msme"',
        code: 'INVALID_USER_TYPE'
      });
    }

    if (password.length < 8) {
      return res.status(400).json({ 
        error: 'Password must be at least 8 characters long',
        code: 'WEAK_PASSWORD'
      });
    }

    console.log('âœ… Validation passed, creating UserModel...');
    const userModel = new UserModel();

    console.log('ðŸ” Checking if user exists:', email);
    // Check if user already exists
    const existingUser = await userModel.findByEmail(email);
    if (existingUser) {
      console.log('âŒ User already exists:', email);
      return res.status(409).json({ 
        error: 'User with this email already exists',
        code: 'USER_EXISTS'
      });
    }

    console.log('âœ… User does not exist, creating new user...');
    // Create user
    const userData: CreateUserData = {
      email,
      password,
      first_name,
      last_name,
      user_type
    };

    console.log('ðŸ” Creating user with data:', { ...userData, password: '[HIDDEN]' });
    const user = await userModel.create(userData);
    console.log('âœ… User created successfully:', user.id);

    // Generate tokens
    const tokenPayload = {
      userId: user.id!,
      email: user.email,
      userType: user.user_type
    };

    const accessToken = JWTUtils.generateAccessToken(tokenPayload);
    const refreshToken = JWTUtils.generateRefreshToken(tokenPayload);

    res.status(201).json({
      message: 'User registered successfully',
      user,
      tokens: {
        access_token: accessToken,
        refresh_token: refreshToken,
        token_type: 'Bearer'
      }
    });

  } catch (error) {
    console.error('âŒ Registration error details:', {
      message: error.message,
      stack: error.stack,
      name: error.name,
      code: error.code
    });
    res.status(500).json({ 
      error: 'Registration failed',
      code: 'REGISTRATION_ERROR'
    });
  }
});

// POST /api/auth/login
router.post('/login', async (req, res) => {
  try {
    const { email, password } = req.body;

    if (!email || !password) {
      return res.status(400).json({ 
        error: 'Email and password are required',
        code: 'VALIDATION_ERROR'
      });
    }

    const userModel = new UserModel();
    const user = await userModel.findByEmail(email);

    if (!user || !user.password_hash) {
      return res.status(401).json({ 
        error: 'Invalid email or password',
        code: 'INVALID_CREDENTIALS'
      });
    }

    // Verify password
    const isValidPassword = await userModel.verifyPassword(password, user.password_hash);

    if (!isValidPassword) {
      return res.status(401).json({ 
        error: 'Invalid email or password',
        code: 'INVALID_CREDENTIALS'
      });
    }

    // Generate tokens
    const tokenPayload = {
      userId: user.id!,
      email: user.email,
      userType: user.user_type
    };

    const accessToken = JWTUtils.generateAccessToken(tokenPayload);
    const refreshToken = JWTUtils.generateRefreshToken(tokenPayload);

    // Remove password hash from response
    const { password_hash, ...userWithoutPassword } = user;

    res.json({
      message: 'Login successful',
      user: userWithoutPassword,
      tokens: {
        access_token: accessToken,
        refresh_token: refreshToken,
        token_type: 'Bearer'
      }
    });

  } catch (error) {
    console.error('Login error:', error);
    res.status(500).json({ 
      error: 'Login failed',
      code: 'LOGIN_ERROR'
    });
  }
});

// POST /api/auth/refresh
router.post('/refresh', async (req, res) => {
  try {
    const { refresh_token } = req.body;

    if (!refresh_token) {
      return res.status(400).json({ 
        error: 'Refresh token is required',
        code: 'VALIDATION_ERROR'
      });
    }

    const payload = JWTUtils.verifyRefreshToken(refresh_token);

    if (!payload) {
      return res.status(401).json({ 
        error: 'Invalid or expired refresh token',
        code: 'INVALID_REFRESH_TOKEN'
      });
    }

    // Verify user still exists
    const userModel = new UserModel();
    const user = await userModel.findById(payload.userId);

    if (!user) {
      return res.status(401).json({ 
        error: 'User not found',
        code: 'USER_NOT_FOUND'
      });
    }

    // Generate new tokens
    const tokenPayload = {
      userId: user.id!,
      email: user.email,
      userType: user.user_type
    };

    const accessToken = JWTUtils.generateAccessToken(tokenPayload);
    const newRefreshToken = JWTUtils.generateRefreshToken(tokenPayload);

    res.json({
      message: 'Token refreshed successfully',
      tokens: {
        access_token: accessToken,
        refresh_token: newRefreshToken,
        token_type: 'Bearer'
      }
    });

  } catch (error) {
    console.error('Token refresh error:', error);
    res.status(500).json({ 
      error: 'Token refresh failed',
      code: 'REFRESH_ERROR'
    });
  }
});

// POST /api/auth/logout
router.post('/logout', authenticateToken, (req, res) => {
  // In a production app, you might want to blacklist the token
  // For now, we'll just send a success response
  res.json({ message: 'Logged out successfully' });
});

// GET /api/auth/me - Get current user info
router.get('/me', authenticateToken, async (req, res) => {
  try {
    const userModel = new UserModel();
    const user = await userModel.findById(req.user!.userId);

    if (!user) {
      return res.status(404).json({ 
        error: 'User not found',
        code: 'USER_NOT_FOUND'
      });
    }

    res.json({ user });
  } catch (error) {
    console.error('Get user error:', error);
    res.status(500).json({ 
      error: 'Failed to get user information',
      code: 'GET_USER_ERROR'
    });
  }
});

export default router;

