import { Request, Response, NextFunction } from 'express';
import { JWTUtils, JWTPayload } from '../utils/jwt';
import { UserModel } from '../models/User';

// Extend Express Request type to include user
declare global {
  namespace Express {
    interface Request {
      user?: JWTPayload;
    }
  }
}

export const authenticateToken = async (req: Request, res: Response, next: NextFunction) => {
  try {
    const token = JWTUtils.extractTokenFromHeader(req.headers.authorization);

    if (!token) {
      return res.status(401).json({ 
        error: 'Access token required',
        code: 'TOKEN_MISSING'
      });
    }

    const payload = JWTUtils.verifyAccessToken(token);

    if (!payload) {
      return res.status(401).json({ 
        error: 'Invalid or expired token',
        code: 'TOKEN_INVALID'
      });
    }

    // Verify user still exists
    const userModel = new UserModel();
    const user = await userModel.findById(payload.userId);

    if (!user) {
      return res.status(401).json({ 
        error: 'User not found',
        code: 'USER_NOT_FOUND'
      });
    }

    req.user = payload;
    next();
  } catch (error) {
    console.error('Authentication error:', error);
    res.status(500).json({ 
      error: 'Authentication failed',
      code: 'AUTH_ERROR'
    });
  }
};

export const requireUserType = (userType: 'investor' | 'msme') => {
  return (req: Request, res: Response, next: NextFunction) => {
    if (!req.user) {
      return res.status(401).json({ 
        error: 'Authentication required',
        code: 'AUTH_REQUIRED'
      });
    }

    if (req.user.userType !== userType) {
      return res.status(403).json({ 
        error: `Access denied. ${userType} role required.`,
        code: 'INSUFFICIENT_PERMISSIONS'
      });
    }

    next();
  };
};

export const requireKycApproval = async (req: Request, res: Response, next: NextFunction) => {
  try {
    if (!req.user) {
      return res.status(401).json({ 
        error: 'Authentication required',
        code: 'AUTH_REQUIRED'
      });
    }

    const userModel = new UserModel();
    const user = await userModel.findById(req.user.userId);

    if (!user) {
      return res.status(404).json({ 
        error: 'User not found',
        code: 'USER_NOT_FOUND'
      });
    }

    if (user.kyc_status !== 'approved') {
      return res.status(403).json({ 
        error: 'KYC approval required for this action',
        code: 'KYC_REQUIRED',
        kyc_status: user.kyc_status
      });
    }

    next();
  } catch (error) {
    console.error('KYC check error:', error);
    res.status(500).json({ 
      error: 'KYC verification failed',
      code: 'KYC_CHECK_ERROR'
    });
  }
};

