const { connectDatabases, duckdb } = require('./dist/config/duckdb-connection');
const { UserModel } = require('./dist/models/User');

async function testRegistration() {
  try {
    console.log('ðŸ” Testing registration functionality...');

    // Connect to database
    await connectDatabases();
    console.log('âœ… Database connected');

    // Test basic query
    const result = await duckdb.query('SELECT COUNT(*) as count FROM users');
    console.log('âœ… Users table accessible, current count:', result.rows[0].count);

    // Test user creation
    console.log('ðŸ” Testing user creation...');
    const userModel = new UserModel();

    const userData = {
      email: 'test-direct@example.com',
      password: 'password123',
      first_name: 'Test',
      last_name: 'User',
