import express from 'express';
import { DROModel, CreateDROData } from '../models/DRO';
import { UserModel, CreateUserData } from '../models/User';
import { authenticateApiToken } from '../middleware/auth';

const router = express.Router();

// POST /api/batch/dros - Create multiple DROs in batch
router.post('/dros', authenticateApiToken, async (req, res) => {
  try {
    const { dros } = req.body;

    if (!Array.isArray(dros) || dros.length === 0) {
      return res.status(400).json({ 
        error: 'Request must contain an array of DROs',
        code: 'INVALID_BATCH_DATA'
      });
    }

    if (dros.length > 100) {
      return res.status(400).json({ 
        error: 'Maximum 100 DROs per batch operation',
        code: 'BATCH_TOO_LARGE'
      });
    }

    const droModel = new DROModel();
    const results = [];
    const errors = [];

    for (let i = 0; i < dros.length; i++) {
      try {
        const droData = dros[i];

        // Validate required fields
        const requiredFields = ['msme_id', 'title', 'description', 'industry', 'revenue_percentage', 'minimum_investment', 'target_amount', 'duration_months'];
        const missingFields = requiredFields.filter(field => !droData[field]);

        if (missingFields.length > 0) {
          errors.push({
            index: i,
            error: `Missing required fields: ${missingFields.join(', ')}`,
            code: 'MISSING_FIELDS'
          });
          continue;
        }

        // Validate data types and ranges
        if (isNaN(parseFloat(droData.revenue_percentage)) || parseFloat(droData.revenue_percentage) <= 0 || parseFloat(droData.revenue_percentage) > 50) {
          errors.push({
            index: i,
            error: 'Revenue percentage must be between 0.1 and 50',
            code: 'INVALID_REVENUE_PERCENTAGE'
          });
          continue;
        }

        if (isNaN(parseInt(droData.duration_months)) || parseInt(droData.duration_months) < 1 || parseInt(droData.duration_months) > 120) {
          errors.push({
            index: i,
            error: 'Duration must be between 1 and 120 months',
            code: 'INVALID_DURATION'
          });
          continue;
        }

        const createData: CreateDROData = {
          msme_id: droData.msme_id,
          title: droData.title,
          description: droData.description,
          industry: droData.industry,
          revenue_percentage: parseFloat(droData.revenue_percentage),
          minimum_investment: parseFloat(droData.minimum_investment),
          target_amount: parseFloat(droData.target_amount),
          duration_months: parseInt(droData.duration_months)
        };

        const dro = await droModel.create(createData);
        results.push({
          index: i,
          success: true,
          dro: dro
        });

      } catch (error) {
        errors.push({
          index: i,
          error: error.message,
          code: 'CREATE_FAILED'
        });
      }
    }

    res.status(201).json({
      message: `Batch operation completed: ${results.length} created, ${errors.length} failed`,
      results: results,
      errors: errors,
      summary: {
        total: dros.length,
        successful: results.length,
        failed: errors.length
      }
    });

  } catch (error) {
    console.error('Batch create DRO error:', error);
    res.status(500).json({ 
      error: 'Batch DRO creation failed',
      code: 'BATCH_CREATE_ERROR'
    });
  }
});

// POST /api/batch/users - Create multiple users in batch
router.post('/users', authenticateApiToken, async (req, res) => {
  try {
    const { users } = req.body;

    if (!Array.isArray(users) || users.length === 0) {
      return res.status(400).json({ 
        error: 'Request must contain an array of users',
        code: 'INVALID_BATCH_DATA'
      });
    }

    if (users.length > 50) {
      return res.status(400).json({ 
        error: 'Maximum 50 users per batch operation',
        code: 'BATCH_TOO_LARGE'
      });
    }

    const userModel = new UserModel();
    const results = [];
    const errors = [];

    for (let i = 0; i < users.length; i++) {
      try {
        const userData = users[i];

        // Validate required fields
        const requiredFields = ['email', 'password', 'user_type'];
        const missingFields = requiredFields.filter(field => !userData[field]);

        if (missingFields.length > 0) {
          errors.push({
            index: i,
            error: `Missing required fields: ${missingFields.join(', ')}`,
            code: 'MISSING_FIELDS'
          });
          continue;
        }

        // Validate user type
        if (!['investor', 'msme'].includes(userData.user_type)) {
          errors.push({
            index: i,
            error: 'user_type must be either "investor" or "msme"',
            code: 'INVALID_USER_TYPE'
          });
          continue;
        }

        // Check if user already exists
        const existingUser = await userModel.findByEmail(userData.email);
        if (existingUser) {
          errors.push({
            index: i,
            error: 'User with this email already exists',
            code: 'USER_EXISTS'
          });
          continue;
        }

        const createData: CreateUserData = {
          email: userData.email,
          password: userData.password,
          first_name: userData.first_name,
          last_name: userData.last_name,
          user_type: userData.user_type
        };

        const user = await userModel.create(createData);
        results.push({
          index: i,
          success: true,
          user: user
        });

      } catch (error) {
        errors.push({
          index: i,
          error: error.message,
          code: 'CREATE_FAILED'
        });
      }
    }

    res.status(201).json({
      message: `Batch operation completed: ${results.length} created, ${errors.length} failed`,
      results: results,
      errors: errors,
      summary: {
        total: users.length,
        successful: results.length,
        failed: errors.length
      }
    });

  } catch (error) {
    console.error('Batch create users error:', error);
    res.status(500).json({ 
      error: 'Batch user creation failed',
      code: 'BATCH_CREATE_ERROR'
    });
  }
});

// GET /api/batch/status - Get batch operations status
router.get('/status', authenticateApiToken, (req, res) => {
  res.json({
    message: 'SIREX Batch API is operational',
    timestamp: new Date().toISOString(),
    endpoints: {
      'POST /api/batch/dros': 'Create multiple DROs (max 100)',
      'POST /api/batch/users': 'Create multiple users (max 50)',
      'GET /api/batch/status': 'Check batch API status'
    },
    authentication: 'API Token required (X-API-Key header)',
    limits: {
      dros_per_batch: 100,
      users_per_batch: 50
    }
  });
});

export default router;

