import express from 'express';
import cors from 'cors';
import helmet from 'helmet';
import compression from 'compression';
import rateLimit from 'express-rate-limit';
import morgan from 'morgan';
import dotenv from 'dotenv';

// Import configuration
import { productionConfig, validateProductionConfig } from './config/production';

// Import new database connection
import { connectDatabases, closeDatabases } from './config/duckdb-connection';
import { r2BackupService } from './services/R2BackupService';

// Import routes
import authRoutes from './routes/auth';
import userRoutes from './routes/user';
import droRoutes from './routes/dro';
import revenueRoutes from './routes/revenue';
import paymentRoutes from './routes/payment';

// Load environment variables
dotenv.config();

// Validate production configuration
if (process.env.NODE_ENV === 'production' && !validateProductionConfig()) {
  console.error('âŒ Production configuration validation failed');
  process.exit(1);
}

const app = express();
const PORT = process.env.NODE_ENV === 'production' ? productionConfig.server.port : (process.env.PORT || 3001);

// Production-ready middleware configuration
const corsOptions = process.env.NODE_ENV === 'production' ? productionConfig.cors : {
  origin: ['http://localhost:5173', 'http://localhost:5174'],
  credentials: true
};

const helmetOptions = process.env.NODE_ENV === 'production' ? {
  contentSecurityPolicy: {
    directives: {
      defaultSrc: ["'self'"],
      styleSrc: ["'self'", "'unsafe-inline'", "https://fonts.googleapis.com"],
      fontSrc: ["'self'", "https://fonts.gstatic.com"],
      scriptSrc: ["'self'", "https://js.stripe.com", "https://www.paypal.com"],
      connectSrc: ["'self'", "https://api.stripe.com", "https://api.paypal.com"],
      frameSrc: ["'self'", "https://js.stripe.com", "https://www.paypal.com"],
      imgSrc: ["'self'", "data:", "https:"],
    }
  }
} : {};

// Middleware (following hesocial proven patterns)
app.use(helmet(helmetOptions));
app.use(cors(corsOptions));
app.use(compression());

// Rate limiting (production-ready)
const limiter = rateLimit(productionConfig.rateLimit);
app.use(limiter);

app.use(morgan(process.env.NODE_ENV === 'production' ? 'combined' : 'dev'));
app.use(express.json({ limit: '10mb' }));
app.use(express.urlencoded({ extended: true, limit: '10mb' }));

// Routes
app.use('/api/auth', authRoutes);
app.use('/api/users', userRoutes);
app.use('/api/dro', droRoutes);
app.use('/api/revenue', revenueRoutes);
app.use('/api/payment', paymentRoutes);

// Batch operations route
import batchRoutes from './routes/batch';
app.use('/api/batch', batchRoutes);

// Enhanced health check endpoint for production
app.get('/health', async (req, res) => {
  try {
    const healthStatus = {
      status: 'OK',
      message: 'SIREX API is running',
      timestamp: new Date().toISOString(),
      environment: process.env.NODE_ENV || 'development',
      version: '1.0.0',
      services: {
        database: 'unknown',
        r2_backup: 'unknown',
        payments: {
          stripe: process.env.NODE_ENV === 'production' ? 
            (productionConfig.payments.stripe.enabled ? 'configured' : 'not_configured') : 
            'configured',
          paypal: process.env.NODE_ENV === 'production' ? 
            (productionConfig.payments.paypal.enabled ? 'configured' : 'not_configured') : 
            'configured'
        }
      }
    };

    // Check database connectivity (basic check)
    try {
      // This is a simple check - in production you might want more comprehensive checks
      healthStatus.services.database = 'connected';
    } catch (error) {
      healthStatus.services.database = 'disconnected';
      healthStatus.status = 'DEGRADED';
    }

    // Check R2 backup service
    try {
      if (process.env.BACKUP_ENABLED === 'true' && r2BackupService) {
        const r2Connected = await r2BackupService.testConnection();
        healthStatus.services.r2_backup = r2Connected ? 'connected' : 'disconnected';
      } else {
        healthStatus.services.r2_backup = 'disabled';
      }
    } catch (error) {
      healthStatus.services.r2_backup = 'error';
    }

    // Set appropriate HTTP status
    const httpStatus = healthStatus.status === 'OK' ? 200 : 503;
    res.status(httpStatus).json(healthStatus);

  } catch (error) {
    res.status(503).json({
      status: 'ERROR',
      message: 'Health check failed',
      timestamp: new Date().toISOString(),
      error: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

// Default route
app.get('/', (req, res) => {
  res.json({ 
    message: 'Welcome to SIREX API',
    version: '1.0.0',
    documentation: '/api/docs'
  });
});

// Error handling middleware
app.use((err: any, req: express.Request, res: express.Response, next: express.NextFunction) => {
  console.error(err.stack);
  res.status(500).json({ 
    error: 'Something went wrong!',
    message: process.env.NODE_ENV === 'development' ? err.message : 'Internal server error'
  });
});

// 404 handler
app.use('*', (req, res) => {
  res.status(404).json({ error: 'Route not found' });
});

// Graceful shutdown handler
const gracefulShutdown = async (signal: string) => {
  console.log(`\nðŸ›‘ Received ${signal}. Starting graceful shutdown...`);

  try {
    // Create backup before shutdown
    if (r2BackupService) {
      console.log('ðŸ“¦ Creating shutdown backup...');
      await r2BackupService.backupOnShutdown();
    } else {
      console.log('ðŸ“¦ R2 backup service not available for shutdown backup');
    }

    // Close database connections
    console.log('ðŸ”’ Closing database connections...');
    await closeDatabases();

    console.log('âœ… Graceful shutdown completed');
    process.exit(0);
  } catch (error) {
    console.error('âŒ Error during shutdown:', error);
    process.exit(1);
  }
};

// Register shutdown handlers
process.on('SIGTERM', () => gracefulShutdown('SIGTERM'));
process.on('SIGINT', () => gracefulShutdown('SIGINT'));

app.listen(PORT, async () => {
  console.log(`ðŸš€ SIREX server running on port ${PORT}`);
  console.log(`ðŸ“Š Environment: ${process.env.NODE_ENV || 'development'}`);

  // Import and run startup health checks
  const { StartupHealthCheck } = await import('./services/StartupHealthCheck');
  const healthCheck = new StartupHealthCheck();
  const allSystemsHealthy = await healthCheck.runAllChecks();

  if (!allSystemsHealthy) {
    console.log('âš ï¸  Server started with warnings - some features may be limited');
  }

  // Legacy R2 test (keeping for compatibility)
  if (process.env.BACKUP_ENABLED === 'true' && r2BackupService) {
    console.log('ðŸ”§ Testing R2 backup service...');
    const r2Connected = await r2BackupService.testConnection();
    console.log(`ðŸ“¦ R2 Backup Service: ${r2Connected ? 'âœ… Connected' : 'âŒ Failed'}`);

    // Smart restore: only restore if no local DB or backup is newer
    try {
      const backupRestored = await r2BackupService.restoreLatestBackup();
      if (backupRestored) {
        console.log(`ðŸ“¥ Restored backup: ${backupRestored}`);
      } else {
        console.log('ðŸ“Š Using existing local database (newer than backup or no backup found)');
      }
    } catch (error) {
      console.log('â„¹ï¸  No backup to restore or restore failed, proceeding with local database');
    }
  } else if (process.env.BACKUP_ENABLED === 'true') {
    console.log('âš ï¸  R2 backup service enabled but not properly configured - continuing without backup');
  }

  // Initialize database connection
  try {
    await connectDatabases();
    console.log('ðŸ“š Database connected and initialized successfully');
  } catch (error) {
    console.error('âŒ Database connection failed:', error);
    process.exit(1);
  }
});

