import jwt from 'jsonwebtoken';

export interface JWTPayload {
  userId: number;
  email: string;
  userType: 'investor' | 'msme';
}

export class JWTUtils {
  private static readonly SECRET = process.env.JWT_SECRET || 'fallback-secret-key';
  private static readonly REFRESH_SECRET = process.env.JWT_REFRESH_SECRET || 'fallback-refresh-secret';
  private static readonly EXPIRES_IN = process.env.JWT_EXPIRES_IN || '24h';
  private static readonly REFRESH_EXPIRES_IN = process.env.JWT_REFRESH_EXPIRES_IN || '7d';

  static generateAccessToken(payload: JWTPayload): string {
    return jwt.sign(payload, this.SECRET, { 
      expiresIn: this.EXPIRES_IN,
      issuer: 'sirex-api',
      audience: 'sirex-client'
    } as jwt.SignOptions);
  }

  static generateRefreshToken(payload: JWTPayload): string {
    return jwt.sign(payload, this.REFRESH_SECRET, { 
      expiresIn: this.REFRESH_EXPIRES_IN,
      issuer: 'sirex-api',
      audience: 'sirex-client'
    } as jwt.SignOptions);
  }

  static verifyAccessToken(token: string): JWTPayload | null {
    try {
      const decoded = jwt.verify(token, this.SECRET, {
        issuer: 'sirex-api',
        audience: 'sirex-client'
      }) as JWTPayload;
      return decoded;
    } catch (error) {
      return null;
    }
  }

  static verifyRefreshToken(token: string): JWTPayload | null {
    try {
      const decoded = jwt.verify(token, this.REFRESH_SECRET, {
        issuer: 'sirex-api',
        audience: 'sirex-client'
      }) as JWTPayload;
      return decoded;
    } catch (error) {
      return null;
    }
  }

  static extractTokenFromHeader(authHeader: string | undefined): string | null {
    if (!authHeader || !authHeader.startsWith('Bearer ')) {
      return null;
    }
    return authHeader.substring(7);
  }
}

