import Database from 'duckdb'
import { readFile } from 'fs/promises'
import { join, dirname } from 'path'
import { fileURLToPath } from 'url'

// Since we're using CommonJS, use __dirname directly
const __dirname = process.cwd()

interface ServerStats {
  id: number
  start_count: number
  first_start_time: string
  last_start_time: string
  last_stop_time?: string
  last_session_duration: number
  total_lifetime: number
}

class DuckDBConnection {
  private db: Database.Database | null = null
  private connection: Database.Connection | null = null

  async connect(): Promise<void> {
    try {
      // Create DuckDB instance with file storage using absolute path
      const dbPath = join(__dirname, 'sirex.duckdb')
      console.log(`Connecting to DuckDB at: ${dbPath}`)

      // Force persistent mode by explicitly setting file path
      this.db = new Database.Database(dbPath)
      this.connection = this.db.connect()

      console.log('Connected to DuckDB database')
    } catch (error) {
      console.error('Failed to connect to DuckDB:', error)
      throw error
    }
  }

  async query(sql: string, params: any[] = []): Promise<any> {
    if (!this.connection) {
      throw new Error('Database not connected')
    }

    return new Promise((resolve, reject) => {
      this.connection!.all(sql, ...params, (err: Error | null, result: any) => {
        if (err) {
          console.error('DuckDB query error:', { sql, params, error: err.message })
          reject(err)
        } else {
          resolve({ rows: result || [] })
        }
      })
    })
  }

  async close(): Promise<void> {
    try {
      if (this.connection) {
        this.connection = null
      }
      if (this.db) {
        this.db.close()
        this.db = null
      }
      console.log('DuckDB connection closed')
    } catch (error) {
      console.error('Error closing DuckDB connection:', error)
    }
  }

  async checkIfInitialized(): Promise<boolean> {
    try {
      // Check server_state table instead of users table
      console.log('Checking if database is initialized...')
      const result = await this.query('SELECT COUNT(*) as count FROM server_state')
      const count = result.rows[0]?.count || 0
      console.log(`Found ${count} server state records`)
      return count > 0
    } catch (error) {
      // If query fails (table doesn't exist), assume not initialized
      console.log('Server state table does not exist, database not initialized')
      return false
    }
  }

  async runInit(): Promise<void> {
    try {
      console.log('Running DuckDB initialization...')

      // Start explicit transaction
      await this.query('BEGIN TRANSACTION')

      const schemaPath = join(__dirname, 'database/sirex-schema.sql')
      console.log(`Reading schema from: ${schemaPath}`)
      const schemaSQL = await readFile(schemaPath, 'utf-8')
      console.log(`Schema file length: ${schemaSQL.length} characters`)

      // Split by semicolon and execute each statement
      const rawStatements = schemaSQL.split(';')
      console.log(`Raw statements after split: ${rawStatements.length}`)

      const trimmedStatements = rawStatements.map(stmt => stmt.trim())
      console.log(`After trim: ${trimmedStatements.filter(s => s.length > 0).length} non-empty`)

      const statements = trimmedStatements.filter(stmt => {
        if (stmt.length === 0) return false

        // Remove comments and check if there's actual SQL content
        const sqlContent = stmt
          .split('\n')
          .map(line => line.trim())
          .filter(line => line.length > 0 && !line.startsWith('--'))
          .join('\n')
          .trim()

        return sqlContent.length > 0
      })

      console.log(`After filtering comments: ${statements.length} statements`)

      // Debug: log first few statements
      statements.slice(0, 3).forEach((stmt, i) => {
        console.log(`Statement ${i + 1}: ${stmt.substring(0, 100)}...`)
      })

      for (const statement of statements) {
        if (statement.trim()) {
          console.log(`Executing: ${statement.substring(0, 50)}...`)
          await this.query(statement)
        }
      }

      // Commit transaction
      await this.query('COMMIT')

      // Verify tables were created
      const tables = await this.query("SELECT table_name FROM information_schema.tables WHERE table_schema = 'main'")
      console.log(`Tables created: ${tables.rows.map((r: any) => r.table_name).join(', ')}`)

      console.log('âœ… DuckDB schema initialization completed successfully')
    } catch (error) {
      console.error('âŒ DuckDB initialization failed:', error)
      throw error
    }
  }

  async seedData(): Promise<void> {
    try {
      console.log('Seeding DuckDB with sample data...')

      // Start explicit transaction
      await this.query('BEGIN TRANSACTION')

      const seedPath = join(__dirname, 'database/sirex-seed.sql')
      const seedSQL = await readFile(seedPath, 'utf-8')

      // Split by semicolon and execute each statement
      const statements = seedSQL
        .split(';')
        .map(stmt => stmt.trim())
        .filter(stmt => stmt.length > 0 && !stmt.startsWith('--'))

      for (const statement of statements) {
        if (statement.trim() && statement.trim().length > 10) {
          try {
            console.log(`Seeding: ${statement.substring(0, 50)}...`)
            await this.query(statement)
          } catch (error) {
            console.error(`Failed to execute statement: ${statement.substring(0, 100)}...`, error)
            await this.query('ROLLBACK')
            throw error
          }
        }
      }

      // Commit transaction
      await this.query('COMMIT')

      console.log('âœ… DuckDB seeding completed successfully')
    } catch (error) {
      console.error('âŒ DuckDB seeding failed:', error)
      throw error
    }
  }

  async recordServerStart(): Promise<void> {
    try {
      const now = new Date().toISOString()

      // Get current state
      const result = await this.query('SELECT * FROM server_state WHERE id = 1')

      if (result.rows.length === 0) {
        // First time startup - create initial record
        await this.query(`
          INSERT INTO server_state (
            id, start_count, first_start_time, last_start_time, 
            last_stop_time, last_session_duration, total_lifetime
          ) VALUES (1, 1, ?, ?, NULL, 0, 0)
        `, [now, now])
        console.log('ğŸ¯ Server state: First startup recorded')
      } else {
        // Update existing record
        const state = result.rows[0]
        const newStartCount = (state.start_count || 0) + 1

        await this.query(`
          UPDATE server_state SET 
            start_count = ?,
            last_start_time = ?
          WHERE id = 1
        `, [newStartCount, now])

        console.log(`ğŸ¯ Server state: Startup #${newStartCount} recorded`)
      }
    } catch (error) {
      console.error('Failed to record server start:', error)
    }
  }

  async recordServerStop(): Promise<void> {
    try {
      const now = new Date().toISOString()

      // Get current state to calculate session duration
      const result = await this.query('SELECT * FROM server_state WHERE id = 1')

      if (result.rows.length > 0) {
        const state = result.rows[0]
        const lastStartTime = new Date(state.last_start_time)
        const stopTime = new Date(now)
        const sessionDuration = Math.floor((stopTime.getTime() - lastStartTime.getTime()) / 1000) // seconds
        const newTotalLifetime = (state.total_lifetime || 0) + sessionDuration

        await this.query(`
          UPDATE server_state SET 
            last_stop_time = ?,
            last_session_duration = ?,
            total_lifetime = ?
          WHERE id = 1
        `, [now, sessionDuration, newTotalLifetime])

        console.log(`ğŸ¯ Server state: Shutdown recorded (session: ${sessionDuration}s, total: ${newTotalLifetime}s)`)
      }
    } catch (error) {
      console.error('Failed to record server stop:', error)
    }
  }

  async getServerStats(): Promise<ServerStats | null> {
    try {
      const result = await this.query('SELECT * FROM server_state WHERE id = 1')
      return result.rows[0] || null
    } catch (error) {
      console.error('Failed to get server stats:', error)
      return null
    }
  }
}

// Export singleton instance
export const duckdb = new DuckDBConnection()

export const connectDatabases = async (): Promise<void> => {
  try {
    await duckdb.connect()

    const isInitialized = await duckdb.checkIfInitialized()

    if (!isInitialized) {
      console.log('Database not initialized, running setup...')
      await duckdb.runInit()
      await duckdb.seedData()
      console.log('âœ… DuckDB setup completed - Full functionality available')
    } else {
      console.log('âœ… DuckDB already initialized - Full functionality available')
    }

    // Record server startup
    await duckdb.recordServerStart()

    // Display server stats
    const stats = await duckdb.getServerStats()
    if (stats) {
      console.log(`ğŸ“Š Server Stats: Startup #${stats.start_count}, Total uptime: ${Math.floor(stats.total_lifetime / 60)}min`)
    }

  } catch (error) {
    console.error('Failed to connect to databases:', error)
    throw error
  }
}

export const closeDatabases = async (): Promise<void> => {
  try {
    // Record server stop before closing
    await duckdb.recordServerStop()
    await duckdb.close()
    console.log('Database connections closed')
  } catch (error) {
    console.error('Error closing database connections:', error)
    throw error
  }
}

