import { Database } from 'duckdb';
import { S3Client, PutObjectCommand, GetObjectCommand } from '@aws-sdk/client-s3';
import { Upload } from '@aws-sdk/lib-storage';
import path from 'path';
import fs from 'fs';

// Cloudflare R2 configuration
const r2Client = new S3Client({
  region: 'auto',
  endpoint: process.env.CLOUDFLARE_R2_ENDPOINT,
  credentials: {
    accessKeyId: process.env.CLOUDFLARE_R2_ACCESS_KEY_ID || '',
    secretAccessKey: process.env.CLOUDFLARE_R2_SECRET_ACCESS_KEY || '',
  },
});

class DatabaseManager {
  private db: Database | null = null;
  private readonly localDbPath = './data/sirex.db';
  private readonly bucketName = process.env.CLOUDFLARE_R2_BUCKET_NAME || 'sirex-data';

  constructor() {
    this.ensureDataDirectory();
  }

  private ensureDataDirectory(): void {
    const dataDir = path.dirname(this.localDbPath);
    if (!fs.existsSync(dataDir)) {
      fs.mkdirSync(dataDir, { recursive: true });
    }
  }

  async connect(): Promise<Database> {
    if (this.db) {
      return this.db;
    }

    try {
      // Try to download database from R2 if it exists
      await this.downloadFromR2();
    } catch (error) {
      console.log('No existing database in R2, creating new one');
    }

    this.db = new Database(this.localDbPath);
    await this.initializeTables();
    return this.db;
  }

  private async downloadFromR2(): Promise<void> {
    if (!process.env.CLOUDFLARE_R2_ACCESS_KEY_ID) {
      console.log('R2 credentials not configured, using local database only');
      return;
    }

    try {
      const command = new GetObjectCommand({
        Bucket: this.bucketName,
        Key: 'sirex.db',
      });

      const response = await r2Client.send(command);
      if (response.Body) {
        const stream = response.Body as NodeJS.ReadableStream;
        const writeStream = fs.createWriteStream(this.localDbPath);

        return new Promise((resolve, reject) => {
          stream.pipe(writeStream);
          writeStream.on('finish', resolve);
          writeStream.on('error', reject);
        });
      }
    } catch (error) {
      console.error('Error downloading database from R2:', error);
      throw error;
    }
  }

  async backupToR2(): Promise<void> {
    if (!process.env.CLOUDFLARE_R2_ACCESS_KEY_ID || !fs.existsSync(this.localDbPath)) {
      console.log('R2 not configured or database file not found, skipping backup');
      return;
    }

    try {
      const fileStream = fs.createReadStream(this.localDbPath);

      const upload = new Upload({
        client: r2Client,
        params: {
          Bucket: this.bucketName,
          Key: 'sirex.db',
          Body: fileStream,
        },
      });

      await upload.done();
      console.log('Database backed up to R2 successfully');
    } catch (error) {
      console.error('Error backing up database to R2:', error);
    }
  }

  private async initializeTables(): Promise<void> {
    if (!this.db) throw new Error('Database not connected');

    const tables = [
      // Users table
      `CREATE TABLE IF NOT EXISTS users (
        id INTEGER PRIMARY KEY,
        email VARCHAR UNIQUE NOT NULL,
        password_hash VARCHAR NOT NULL,
        first_name VARCHAR,
        last_name VARCHAR,
        user_type VARCHAR CHECK(user_type IN ('investor', 'msme')) NOT NULL,
        kyc_status VARCHAR DEFAULT 'pending' CHECK(kyc_status IN ('pending', 'approved', 'rejected')),
        is_verified BOOLEAN DEFAULT false,
        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
        updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
      )`,

      // DROs table
      `CREATE TABLE IF NOT EXISTS dros (
        id INTEGER PRIMARY KEY,
        msme_id INTEGER NOT NULL,
        title VARCHAR NOT NULL,
        description TEXT,
        industry VARCHAR NOT NULL,
        revenue_percentage DECIMAL(5,2) NOT NULL,
        minimum_investment DECIMAL(15,2) NOT NULL,
        target_amount DECIMAL(15,2) NOT NULL,
        raised_amount DECIMAL(15,2) DEFAULT 0,
        duration_months INTEGER NOT NULL,
        status VARCHAR DEFAULT 'pending' CHECK(status IN ('pending', 'active', 'completed', 'cancelled')),
        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
