    console.log('ğŸ“¦ Bucket contents:', response.Contents?.length || 0, 'objects');

    if (response.Contents && response.Contents.length > 0) {
      console.log('ğŸ“„ Recent objects:');
      response.Contents.slice(0, 3).forEach(obj => {
        console.log(`  - ${obj.Key} (${(obj.Size / 1024).toFixed(1)} KB)`);
      });
    } else {
      console.log('ğŸ“­ Bucket is empty (ready for first backup)');
    }

