        .map(stmt => stmt.trim())
        .filter(stmt => stmt.length > 0 && !stmt.startsWith('--'))

      for (const statement of statements) {
        if (statement.trim() && statement.trim().length > 10) {
          try {
            console.log(`Seeding: ${statement.substring(0, 50)}...`)
            await this.query(statement)
          } catch (error) {
            console.error(`Failed to execute statement: ${statement.substring(0, 100)}...`, error)
            await this.query('ROLLBACK')
            throw error
          }
        }
      }

      // Commit transaction
      await this.query('COMMIT')

      console.log('âœ… DuckDB seeding completed successfully')
    } catch (error) {
      console.error('âŒ DuckDB seeding failed:', error)
      throw error
    }
  }

  async recordServerStart(): Promise<void> {
    try {
      const now = new Date().toISOString()

      // Get current state
      const result = await this.query('SELECT * FROM server_state WHERE id = 1')

      if (result.rows.length === 0) {
        // First time startup - create initial record
        await this.query(`
          INSERT INTO server_state (
            id, start_count, first_start_time, last_start_time, 
            last_stop_time, last_session_duration, total_lifetime
          ) VALUES (1, 1, ?, ?, NULL, 0, 0)
        `, [now, now])
        console.log('ğŸ¯ Server state: First startup recorded')
      } else {
        // Update existing record
        const state = result.rows[0]
        const newStartCount = (state.start_count || 0) + 1

        await this.query(`
          UPDATE server_state SET 
            start_count = ?,
            last_start_time = ?
          WHERE id = 1
        `, [newStartCount, now])

        console.log(`ğŸ¯ Server state: Startup #${newStartCount} recorded`)
      }
    } catch (error) {
      console.error('Failed to record server start:', error)
    }
  }

  async recordServerStop(): Promise<void> {
    try {
      const now = new Date().toISOString()

      // Get current state to calculate session duration
      const result = await this.query('SELECT * FROM server_state WHERE id = 1')

      if (result.rows.length > 0) {
        const state = result.rows[0]
        const lastStartTime = new Date(state.last_start_time)
        const stopTime = new Date(now)
        const sessionDuration = Math.floor((stopTime.getTime() - lastStartTime.getTime()) / 1000) // seconds
        const newTotalLifetime = (state.total_lifetime || 0) + sessionDuration

        await this.query(`
          UPDATE server_state SET 
            last_stop_time = ?,
            last_session_duration = ?,
            total_lifetime = ?
          WHERE id = 1
        `, [now, sessionDuration, newTotalLifetime])

        console.log(`ğŸ¯ Server state: Shutdown recorded (session: ${sessionDuration}s, total: ${newTotalLifetime}s)`)
      }
    } catch (error) {
      console.error('Failed to record server stop:', error)
    }
  }

  async getServerStats(): Promise<ServerStats | null> {
    try {
      const result = await this.query('SELECT * FROM server_state WHERE id = 1')
      return result.rows[0] || null
    } catch (error) {
      console.error('Failed to get server stats:', error)
      return null
    }
  }
}

