#!/usr/bin/env node

// SIREX Backup Management CLI
require('dotenv').config();
const { r2BackupService } = require('./dist/services/R2BackupService');

const args = process.argv.slice(2);
const command = args[0];

async function main() {
  console.log('ğŸ—„ï¸  SIREX Backup Management CLI\n');

  try {
    switch (command) {
      case 'list':
        await listBackups();
        break;
      case 'backup':
        await createBackup();
        break;
      case 'restore':
        const backupId = args[1];
        await restoreBackup(backupId);
        break;
      case 'force-restore':
        const forceBackupId = args[1];
        await forceRestore(forceBackupId);
        break;
      case 'status':
        await showStatus();
        break;
      case 'cleanup':
        await cleanupOldBackups();
        break;
      default:
        showHelp();
    }
  } catch (error) {
    console.error('âŒ Error:', error.message);
    process.exit(1);
  }
}

async function listBackups() {
  console.log('ğŸ“‹ Available backups:');
  const backups = await r2BackupService.listBackups(20);

  if (backups.length === 0) {
    console.log('   No backups found');
    return;
  }

  backups.forEach((backup, index) => {
    const age = Math.floor((Date.now() - new Date(backup.timestamp).getTime()) / (1000 * 60 * 60));
    console.log(`   ${index + 1}. ${backup.id}`);
    console.log(`      Type: ${backup.type} | Size: ${backup.size} | ${age}h ago`);
  });
}

async function createBackup() {
  console.log('ğŸ“¦ Creating manual backup...');
  const backupId = await r2BackupService.createManualBackup();
  console.log(`âœ… Backup created: ${backupId}`);
}

async function restoreBackup(backupId) {
  if (backupId) {
    console.log(`ğŸ”„ Restoring specific backup: ${backupId}`);
    const restored = await r2BackupService.forceRestoreBackup(backupId);
    if (restored) {
      console.log(`âœ… Restored: ${restored}`);
    }
  } else {
    console.log('ğŸ“¥ Smart restore (only if backup is newer)...');
    const restored = await r2BackupService.restoreLatestBackup();
    if (restored) {
      console.log(`âœ… Restored: ${restored}`);
    } else {
      console.log('â„¹ï¸  Local database is newer, no restore needed');
    }
  }
}

async function forceRestore(backupId) {
  console.log('âš ï¸  Force restore will overwrite your local database!');
  console.log('   This action cannot be undone.');

  // Simple confirmation (in a real CLI you might use a proper prompt library)
  if (process.env.NODE_ENV !== 'production') {
    if (backupId) {
      console.log(`ğŸ”„ Force restoring: ${backupId}`);
      const restored = await r2BackupService.forceRestoreBackup(backupId);
      if (restored) {
        console.log(`âœ… Force restore completed: ${restored}`);
      }
    } else {
      console.log('ğŸ”„ Force restoring latest backup...');
      const restored = await r2BackupService.forceRestoreBackup();
      if (restored) {
        console.log(`âœ… Force restore completed: ${restored}`);
      }
    }
  } else {
    console.log('âŒ Force restore disabled in production mode');
  }
}

async function showStatus() {
  console.log('ğŸ“Š Backup System Status:');

  const connected = await r2BackupService.testConnection();
  console.log(`   R2 Connection: ${connected ? 'âœ… Connected' : 'âŒ Failed'}`);

  const backups = await r2BackupService.listBackups(5);
  console.log(`   Total Backups: ${backups.length}`);

  if (backups.length > 0) {
    const latest = backups[0];
    const age = Math.floor((Date.now() - new Date(latest.timestamp).getTime()) / (1000 * 60 * 60));
    console.log(`   Latest Backup: ${latest.id} (${age}h ago)`);
  }

  // Check local database
  const fs = require('fs');
  const path = require('path');
  const dbPath = path.join(process.cwd(), 'sirex.duckdb');

  if (fs.existsSync(dbPath)) {
    const stats = fs.statSync(dbPath);
    const localAge = Math.floor((Date.now() - stats.mtime.getTime()) / (1000 * 60 * 60));
    console.log(`   Local Database: ${(stats.size / 1024).toFixed(1)} KB (modified ${localAge}h ago)`);
  } else {
    console.log('   Local Database: Not found');
  }
}

async function cleanupOldBackups() {
  console.log('ğŸ§¹ Cleaning up old backups...');
  await r2BackupService.cleanupOldBackups();
  console.log('âœ… Cleanup completed');
}

function showHelp() {
  console.log(`Usage: node backup-cli.js <command> [options]

Commands:
  list                    List available backups
  backup                  Create a manual backup
  restore [backup-id]     Smart restore (only if backup newer than local)
  force-restore [backup-id]  Force restore (overwrites local database)
  status                  Show backup system status
  cleanup                 Clean up old backups based on retention policy

Examples:
  node backup-cli.js list
  node backup-cli.js backup
  node backup-cli.js restore
  node backup-cli.js restore sirex-manual-2025-07-02T10-30-00.duckdb
  node backup-cli.js force-restore
  node backup-cli.js status
  node backup-cli.js cleanup
`);
}

main().catch(console.error);

