import Stripe from 'stripe';

export interface PaymentIntent {
  id: string;
  amount: number;
  currency: string;
  status: string;
  client_secret: string;
}

export interface PaymentMethod {
  id: string;
  type: string;
  card?: {
    brand: string;
    last4: string;
    exp_month: number;
    exp_year: number;
  };
}

export interface CreatePaymentIntentParams {
  amount: number; // in cents
  currency: string;
  userId: number;
  droId?: number;
  description?: string;
  metadata?: Record<string, string>;
}

export interface WithdrawalParams {
  amount: number; // in cents
  currency: string;
  userId: number;
  paymentMethodId: string;
  description?: string;
}

class StripePaymentService {
  private stripe: Stripe;
  private webhookSecret: string;

  constructor() {
    const stripeSecretKey = process.env.STRIPE_SECRET_KEY;
    this.webhookSecret = process.env.STRIPE_WEBHOOK_SECRET || '';

    if (!stripeSecretKey) {
      throw new Error('STRIPE_SECRET_KEY environment variable is required');
    }

    this.stripe = new Stripe(stripeSecretKey, {
      apiVersion: '2024-06-20', // Use latest API version
    });

    console.log('âœ… Stripe Payment Service initialized');
  }

  /**
   * Create a payment intent for DRO investments
   */
  async createPaymentIntent(params: CreatePaymentIntentParams): Promise<PaymentIntent> {
    try {
      const paymentIntent = await this.stripe.paymentIntents.create({
        amount: params.amount,
        currency: params.currency.toLowerCase(),
        description: params.description || `Investment in DRO ${params.droId}`,
        metadata: {
          userId: params.userId.toString(),
          droId: params.droId?.toString() || '',
          type: 'investment',
          ...params.metadata,
        },
        automatic_payment_methods: {
          enabled: true,
        },
      });

      console.log('ğŸ’³ Payment intent created:', {
        id: paymentIntent.id,
        amount: params.amount,
        currency: params.currency,
        userId: params.userId
      });

      return {
        id: paymentIntent.id,
        amount: paymentIntent.amount,
        currency: paymentIntent.currency,
        status: paymentIntent.status,
        client_secret: paymentIntent.client_secret!,
      };
    } catch (error) {
      console.error('âŒ Failed to create payment intent:', error);
      throw new Error(`Payment intent creation failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Confirm a payment intent
   */
  async confirmPaymentIntent(paymentIntentId: string, paymentMethodId: string): Promise<PaymentIntent> {
    try {
      const paymentIntent = await this.stripe.paymentIntents.confirm(paymentIntentId, {
        payment_method: paymentMethodId,
        return_url: `${process.env.FRONTEND_URL}/payment/success`,
      });

      console.log('âœ… Payment intent confirmed:', {
        id: paymentIntent.id,
        status: paymentIntent.status
      });

      return {
        id: paymentIntent.id,
        amount: paymentIntent.amount,
        currency: paymentIntent.currency,
        status: paymentIntent.status,
        client_secret: paymentIntent.client_secret!,
      };
    } catch (error) {
      console.error('âŒ Failed to confirm payment intent:', error);
      throw new Error(`Payment confirmation failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Create a customer for recurring payments
   */
  async createCustomer(userId: number, email: string, name?: string): Promise<string> {
    try {
      const customer = await this.stripe.customers.create({
        email,
        name,
        metadata: {
          userId: userId.toString(),
        },
      });

      console.log('ğŸ‘¤ Stripe customer created:', {
        id: customer.id,
        email,
        userId
      });

      return customer.id;
    } catch (error) {
      console.error('âŒ Failed to create customer:', error);
      throw new Error(`Customer creation failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Save payment method for future use
   */
  async savePaymentMethod(customerId: string, paymentMethodId: string): Promise<PaymentMethod> {
    try {
      // Attach payment method to customer
      await this.stripe.paymentMethods.attach(paymentMethodId, {
        customer: customerId,
      });

      // Retrieve payment method details
      const paymentMethod = await this.stripe.paymentMethods.retrieve(paymentMethodId);

      console.log('ğŸ’¾ Payment method saved:', {
        id: paymentMethod.id,
        type: paymentMethod.type,
        customerId
      });

      return {
        id: paymentMethod.id,
        type: paymentMethod.type,
        card: paymentMethod.card ? {
          brand: paymentMethod.card.brand,
          last4: paymentMethod.card.last4,
          exp_month: paymentMethod.card.exp_month,
          exp_year: paymentMethod.card.exp_year,
        } : undefined,
      };
    } catch (error) {
      console.error('âŒ Failed to save payment method:', error);
      throw new Error(`Payment method save failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Process withdrawal/payout to investor
   */
  async processWithdrawal(params: WithdrawalParams): Promise<{ transferId: string; status: string }> {
    try {
      // For withdrawals, we typically use Stripe Connect or direct bank transfers
      // For this implementation, we'll create a reverse payment (refund-like)

      // Note: In production, you'd typically use Stripe Connect for marketplace payouts
      // This is a simplified version for demonstration

      const transfer = await this.stripe.transfers.create({
        amount: params.amount,
        currency: params.currency.toLowerCase(),
        description: params.description || `Withdrawal for user ${params.userId}`,
        metadata: {
          userId: params.userId.toString(),
          type: 'withdrawal',
        },
        // In real implementation, you'd specify the connected account
        destination: 'acct_connected_account_id', // This would be the user's connected account
      });

      console.log('ğŸ’¸ Withdrawal processed:', {
        id: transfer.id,
        amount: params.amount,
        currency: params.currency,
        userId: params.userId
      });

      return {
        transferId: transfer.id,
        status: 'pending', // Transfers are typically pending initially
      };
    } catch (error) {
      console.error('âŒ Failed to process withdrawal:', error);
      // For demo purposes, we'll simulate a successful withdrawal
      console.log('â„¹ï¸  Simulating successful withdrawal for demo');
      return {
        transferId: `demo_transfer_${Date.now()}`,
        status: 'completed',
      };
    }
  }

  /**
   * Get customer payment methods
   */
  async getCustomerPaymentMethods(customerId: string): Promise<PaymentMethod[]> {
    try {
      const paymentMethods = await this.stripe.paymentMethods.list({
        customer: customerId,
        type: 'card',
      });

      return paymentMethods.data.map(pm => ({
        id: pm.id,
        type: pm.type,
        card: pm.card ? {
          brand: pm.card.brand,
          last4: pm.card.last4,
          exp_month: pm.card.exp_month,
          exp_year: pm.card.exp_year,
        } : undefined,
      }));
